css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

[class="popup2 popup2-click-friends"]{
-moz-border-radius-bottomright: 5px !important;
-moz-border-radius-bottomleft: 5px !important;
-moz-box-shadow: 0 2px 3px  #444444 !important;
height: 447px !important;
width: 290px !important;
}

[class="controls"]>[class="edit"]{
color: #AAAAAA !important;
position: absolute !important;
top: 423px !important;
background: -moz-linear-gradient(top, #3A4240, #313737) !important;
background-position: top left !important;
background-color: transparent !important;
background-repeat: no-repeat !important;
-moz-border-radius: 8px !important;
}

[class="controls"]>[class="edit"]:hover{
color: #FFFFFF !important;
background-color: transparent !important;
background: -moz-linear-gradient(top, #508EA0, #39798D) !important;
background-position: top left !important;
}

[class="controls"]>[class="edit"]:active{
color: #9B9E9D !important;
background-color: transparent !important;
background: -moz-linear-gradient(top, #456870, #355862) !important;
background-position: top left !important;
}

[class="readout"]
DD[class="f"]{
background: #303E3C !important;
color: #AAAAAA !important;
}

[class="readout"]
[class="f a"]{
color: #AAAAAA !important;
background: #303E3C !important;
}

[class="online deviants"]{
padding: 26px 0 0 0 !important;
color: #AAAAAA !important;
background-image: url(http://i223.photobucket.com/albums/dd220/artiantil/online2.png) !important;
background-position: top left !important;
}

[class="offline deviants"]{
background-color: #485856 !important;
padding: 26px 0 0 0 !important;
background-image: url(http://i223.photobucket.com/albums/dd220/artiantil/offline2.png) !important;
background-position: top left !important;
}

[class="readout"]
[class="a"]{
position: relative !important;
color: #AAAAAA !important;
padding: 5px 700px 5px 40px !important;
margin-left: -40px !important;
text-decoration: none !important;
z-index: 4000 !important;
}

[class="readout"]
[class="f a"]{
background-color: #2C3937!important;
}

[class="readout"]
[class="a"]:hover{
color: #FFFFFF !important;
}

[class="readout"]
[class="f a"]:hover {
background-color: #498091 !important;
color: #FFFFFF !important;
}

[class="readout"]
DD[class="f"]:hover {
background-color: #498091 !important;
color: #FFFFFF !important;
}

[class="readout"]
#dd class f{
position: relative !important;
color: #AAAAAA !important;
padding: 5px 700px 5px 40px !important;
margin-left: -40px !important;
text-decoration: none !important;
z-index: 4000 !important;
white-space: nowrap !important;
overflow: hidden !important;
}

[class="readout"]{
background: #303E3C !important;
}

[class="readout"]
dd
a{
position: relative !important;
color: #AAAAAA !important;
padding: 5px 700px 5px 40px !important;
margin-left: -40px !important;
text-decoration: none !important;
z-index: 4000 !important;
white-space: nowrap !important;
overflow: hidden !important;
}

[class="readout"]
dd
a:hover{
color: #FFFFFF !important;
}

[class="readout"]
dd
span{
position: absolute !important;
z-index: 1 !important;
color: inherit !important;
}

/*Duplicate Fix*/

.readout > DL:first-child, .readout > DL:first-child + DL{
display: none !important;
}

}
