css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ficsavers.com") {

body,td,th,tr {
 	font-family: Corbel, Verdana, "Verdana Ref", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans",  "DejaVu Sans", "Bitstream Vera Sans", "Liberation Sans", Tahoma, sans-serif !important;
 }

* a  {
 	font-size: larger !important;
 }

.style14 {
    color: #000066 !important;
    background:#ffe !important;
    border:1px solid #ff0;
    margin:0.4em;
    padding:0.2em;
    font-size: medium !important;
    font-family: Consolas, "DejaVu Sans Mono", "Bitstream Vera Sans Mono", "Lucida Console", "Courier New", Courier, monospace !important;}

.style29 {font-size: inherit !important;}

.style28 {
    font-family: Corbel, Verdana, "Verdana Ref", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans",  "DejaVu Sans", "Bitstream Vera Sans", "Liberation Sans", Tahoma, sans-serif !important; 
    font-size: inherit !important; }

.style16 a:hover {text-decoration: none !important;}

.style15 {text-decoration: none !important;}

.style1 {font-weight:bold !important;}

u {text-decoration: none !important;}

u a {text-decoration: underline !important;}

u a:hover {text-decoration: none !important;}

a u {text-decoration: underline !important;}

a u:hover {text-decoration: none !important;}

strong a {text-decoration: underline !important;}

strong a:hover {text-decoration: none !important;}

p a {text-decoration: underline !important;}

p a:hover {text-decoration: none !important;}


#notes { 
        margin:0.5em;
	padding:0.5em;
	background-repeat: no-repeat;
	background-position: center center;
	background-image: url(data:image/png;base64,) !important;
	}

#bottom-nav blockquote a {
 	font-weight: bolder !important;
 }

#bottom-nav a {text-decoration: underline;}

#bottom-nav a:visited { color: #CC9933 !important; text-decoration: none !important; border-bottom: dotted thin !important;}

#bottom-nav a:hover, #bottom-nav a:focus {text-decoration: none !important; border-bottom: dotted thin !important;}

#bottom-nav a:visited:hover {border-bottom: none !important;}



