css:
@charset "utf-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.google.com/calendar")
{
	.ff
	{
		background: #a3a9b5 url('data:image/jpeg;base64,') repeat-x 0 0 !important;
		color: #fff !important;
	}
	a, a:hover, a:visited, .sidelink, .lk, .goog-offlinestatus
	{
		border: none !important;
		color: #fff !important;
	}
	a, a:hover, a:visited
	{
		text-decoration: underline !important;
	}
	input, .today-button, .cal-dialog, .bubble, .bubble .lk, .bubble a, #cover, #cover a, #cover lk, #cover span, .calHeader, .sng-title, #gbi a
	{
		color: #000 !important;
	}
	#calcontent, #mothertable, #maincell, #mainbody, #mainnav, #tc_top, #chrome_main1
	{
		background: none !important;
	}
	.logoparent img, #rhstogglecell div
	{
		display: none !important;
	}
	.logoparent
	{
		background: url('data:image/png;base64,') no-repeat bottom left !important;
		height: 40px !important;
		width: 149px !important;
	}
	#printlink img
	{
		background: url('data:image/png;base64,') no-repeat bottom left !important;
		margin-right: 5px !important;
	}
	#rhstogglecell
	{
		background: none !important;
		cursor: default !important;
	}
	.sng-title
	{
		padding-left: 4px;
	}
	.goog-control-highlight, .goog-control-hover, .drag-lasso, .onepick-dialog, .comment-dialog, .addgadget-dialog, .warning-dialog, .cal-dialog, .ep-headerfooter, .ac-active, .scheduler, .scheduler-content, .sng-title-bar, .static-form-dialog, .ep-scg-guestcell, .ep-tab-tbs, .sng-rounded, .ep-scg-guestheader
	{
		background-color: #d2d6dc !important;
	}
	.erd-event
	{
		border-top: 1px solid #d2d6dc !important;
	}
	.goog-offlinestatuscard-content, .sng-rounded, .sng-wrapper, .sng-remove, .ep-scg-guestheader, .ep-scg-grid
	{
		border-color: #d2d6dc !important;
	}
	.ep-scg-weektop
	{
		border-right: 1px solid #d2d6dc !important;
	}
}
