css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("index.hu"), domain("sportgeza.hu") {
/* Arial helyett Trebuchet MS mindenhol */
body, div#content {
  font-family: "Trebuchet MS","Trebuchet MS",serif !important;
}
/* reklamok off */
div.microsite, div.hirdetes_container, div.hirdetes_bottom, div.hirdetes, div#ads, div#superbanner_container, div.szeles_szponzoracio, a#interware, div#allasbox, ul.fontos, div#fontosvolt, div.rovidhirek, div.ajanlo_accordion {
  display: none !important;
}
div[style="border-bottom: 1px solid rgb(204, 204, 204); padding-bottom: 10px; overflow: visible;"], div[style="border-bottom: 1px solid rgb(204, 204, 204); padding-bottom: 10px; overflow: hidden;"] {
  display: none !important;
}
/* indabox off */
div#indabox {
  display: none !important;
}
h3#bloghu {
  margin-top: 0 !important;
}
/* idojaras box off */
h2#idojaras_top, div#idojaras, div#idojaras_bottom, div.doboz_bottom.szurke {
  display: none !important;
}
/* interware animlogo off */
div#iw {
  display: none !important;
}
/* programajanlo box off */
div#box_program {
  display: none !important;
}
/* blogdoboz egy kicsit feljebb */
div.box_light.blogbox {
  margin-top: 0 !important;
}
/* legfrissebb es kepek doboz off */
div#ajanlobox {
  display: none !important;
}
div.hasab {
  clear: left !important;
}
div.hasab.masodik {
  clear: right !important;
}
/* szelesebb cikkek */
body#cikk div#page_wrapper div#page div#pagecontent div#content, body.cikk div#page_wrapper div#page div#pagecontent div#content {
  width: 640px !important;
  background-color: #FFFFFF !important;
  background-position: 194px 0px !important;
  text-align: justify !important;
}
div#features {
  background-color: #FFFFFF !important;
}
body#cikk div#page div#pagecontent div#content {
  width: 688px !important;
}
body.cikk div#page div#pagecontent div#content {
  width: 648px !important;
}
#kopf, #szoveg p, #szoveg h3, #szoveg ul {
  font-size: 14px !important;
  padding-left: 0 !important;
}
/* kepek kozepre igazitasa */
div.kep {
  margin-left: auto !important;
  margin-right: auto !important;
}
/* a cimoldali subtopicok kiemelese */
div#content h3 {
  margin-bottom: 30px !important;
  border: none !important;
}
div#content h3 span {
  width: 90% !important;
  font-size: 17px !important;
  color: #FFB380 !important;
  padding: 5px 0 5px 0px !important;
  border-bottom: 1px inset #FF751A !important;
}
/* sportgeza.hu specifikus beallitasok */
body.cimlap div#page {
  left: -550px !important;
}
}
