/*****************************************/
/******* Techsnap Dark mod part 1 ********/
/********* Created by: tjwoosta **********/
/*****************************************/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("techsnap.co.uk") {


/**************** MAIN *******************/


body,
div#body-top,
div#header,
div#headerleft,
div#headerright,
div#footer-left,
div#footer-right,
div#footer,
div#sidebar,
div#contentarea,
div#body2,
div#page {
background: #343434 !important;
}

#topmenu ul li a.current,
#topmenu ul li a.current span,
#topmenu ul li a:hover,
#topmenu ul li a:hover span {
background: none !important;
}

blockquote,
code.bbc_code,
textarea,
input,
select,
button {
background-color: #343434 !important;
border-color: #636363 !important;
color: #afafaf !important;
}

.user-avatar img {
height: 100% !important;
width: 100% !important;
}

div.posttop div.dateicon {
border-color: #343434 !important;
}

#sidebar ul li ul li a {
border-color: #636363 !important;
}

.button_submit,
.button_reset {
background: #af5f00 !important;
color: #262626 !important;
}


/**************** TEXT *******************/


* {
color: #8a8a8a !important;
}

a:link {
color: #a7a15e !important;
}

a:active {
color: #a67b07 !important;
}

a:visited {
color: #a67b07 !important;
}

a:hover,
#topmenu ul li a:hover,
#topmenu ul li a:hover span {
color: #556b2f !important;
}


/*************** IMAGES ******************/


span#logo,
img[src*="http://www.techsnap.co.uk/wp-content/themes/darkzen/images/logo.png"] {
width: 0px !important;
height: 100px !important;
background: url(data:image/png;base64,) no-repeat !important;
padding-left: 300px !important;
}

img[src*="http://techsnap.co.uk/forum/Themes/overview_20rc3/images/off.png"] {
width: 0px !important;
height: 45px !important;
background: url(data:image/png;base64,) no-repeat !important;
padding-left: 42px !important;
}

img[src*="http://techsnap.co.uk/forum/Themes/overview_20rc3/images/on.png"] {
width: 0px !important;
height: 45px !important;
background: url(data:image/png;base64,) no-repeat !important;
padding-left: 42px !important;
}

img[src*="http://techsnap.co.uk/forum/Themes/overview_20rc3/images/new_some.png"] {
width: 0px !important;
height: 22px !important;
background: url(data:image/png;base64,) no-repeat !important;
padding-left: 21px !important;
}

img[src*="http://techsnap.co.uk/forum/Themes/overview_20rc3/images/new_none.png"] {
width: 0px !important;
height: 22px !important;
background: url(data:image/png;base64,) no-repeat !important;
padding-left: 21px !important;
}

.buttonlist ul li a.active span {
background: url(data:image/png;base64,) no-repeat scroll 100% -30px transparent !important;
color: #262626 !important;
}

.buttonlist ul li a.active {
background: url(data:image/png;base64,) no-repeat scroll 0 -30px transparent !important;
}

.buttonlist ul li a span {
background: url(data:image/png;base64,) no-repeat scroll 100% -60px transparent !important;
}

.buttonlist ul li a:hover span {
background: url(data:image/png;base64,) no-repeat scroll 100% 0 transparent !important;
}

.buttonlist ul li a {
background: url(data:image/png;base64,) no-repeat scroll 0 -60px transparent !important;
}

.buttonlist ul li a:hover {
background: url(data:image/png;base64,) no-repeat scroll 0 0 transparent !important;
}


}
