/********* Created by: tjwoosta **********/
/*****************************************/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("techsnap.co.uk") {


/*************** IMAGES ******************/


.dropmenu li ul {
background: url(data:image/png;base64,) no-repeat scroll 0 -100px transparent !important;
}

.dropmenu li a.active {
background: url(data:image/png;base64,) no-repeat scroll 100% 0 transparent !important;
}

.dropmenu li a.active span.firstlevel {
background: url(data:image/png;base64,) no-repeat scroll 0 0 transparent !important;
}

.dropmenu li a.firstlevel:hover,
.dropmenu li:hover a.firstlevel {
background: url(data:image/png;base64,) no-repeat scroll 100% -30px transparent !important;
}

.dropmenu li a.firstlevel:hover span.firstlevel,
.dropmenu li:hover a.firstlevel span.firstlevel {
background: url(data:image/png;base64,) no-repeat scroll 0 -30px transparent !important;
color: #262626 !important;
}

blockquote.bbc_standard_quote,
blockquote.bbc_alternate_quote {
background: url(data:image/png;base64,) no-repeat scroll 0.1em 0.1em transparent !important;
}

.postbottom a.stumbleupon {
background: url(data:image/png;base64,) no-repeat !important;
}

.postbottom a.digg {
background: url(data:image/png;base64,) no-repeat !important;
}

.postbottom a.delicious {
background: url(data:image/png;base64,) no-repeat !important;
}

.postbottom a.google {
background: url(data:image/png;base64,) no-repeat !important;
}

#searchsubmit {
background: url(data:image/png;base64,) no-repeat !important;
}

ul#navlist li a:hover,
#menulinks {
background: none !important;
}


}
