/*
 * Google/Images - Google Zune(NEW GOOGLE)
 *
 * Author      : Sarge101
 * Modified    : MedriFogmatio
 * Updated     : May 13th, 2010
 * Description : Modifies the colors, logos, and a few other images on Google / Google Images
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  url(http://www.google.com/),
  url-prefix(http://www.google.com/webhp),
  url-prefix(http://www.google.com/search?),
  url-prefix(http://www.google.com/imghp),
  url-prefix(http://www.google.com/images),
  url-prefix(http://www.google.com/#hl=),
  url-prefix(http://www.google.com/#q=),
  url-prefix(http://images.google.com/webhp),
  url(http://www.google.com/intl/en/),
  url-prefix(http://www.google.com/movies),

  url(http://www.google.com.au/),
  url-prefix(http://www.google.com.au/webhp),
  url-prefix(http://www.google.com.au/search?),
  url-prefix(http://www.google.com.au/imghp),
  url-prefix(http://www.google.com.au/images),
  url-prefix(http://www.google.com.au/#hl=),
  url-prefix(http://www.google.com.au/#q=),
  url-prefix(http://images.google.com.au/webhp),
  url(http://www.google.com.au/intl/en/),
  url-prefix(http://www.google.com.au/movies),

  url(http://www.google.co.uk/),
  url-prefix(http://www.google.co.uk/webhp),
  url-prefix(http://www.google.co.uk/search?),
  url-prefix(http://www.google.co.uk/imghp),
  url-prefix(http://www.google.co.uk/images),
  url-prefix(http://www.google.co.uk/#hl=),
  url-prefix(http://www.google.co.uk/#q=),
  url-prefix(http://images.google.co.uk/webhp),
  url-prefix(http://www.google.co.uk/movies),

  url(http://www.google.ca/),
  url-prefix(http://www.google.ca/webhp),
  url-prefix(http://www.google.ca/search?),
  url-prefix(http://www.google.ca/imghp),
  url-prefix(http://www.google.ca/images),
  url-prefix(http://www.google.ca/#hl=),
  url-prefix(http://www.google.ca/#q=),
  url-prefix(http://images.google.ca/webhp),
  url-prefix(http://www.google.ca/movies),


  url-prefix(http://video.google.com.au/),
  url-prefix(http://video.google.com/),
  url-prefix(http://video.google.co.uk/),
  url-prefix(http://video.google.ca/)
{

body {
  color: #fff !important;
  background: #0D0D0D url('http://img46.imageshack.us/img46/1706/backgroundforgoogle.png') !important;
  background-repeat: repeat-y !important;
  background-attachment: fixed !important;
  overflow-x: hidden !important;
}

/* Transparency Fixes
----------------------------------------------*/
#fctr, #ghead, #pmocntr, #sbl, #tba, #tbe, .fade, #gog, #tbd, #atd, #hidden_modes, #hmp, #sc-block .sc{
  background: none !important;
}

.gbh, .gbd{
  display:none;
}

/* link fixes
----------------------------------------------*/
a, .tbpc, .tbpo, #tbd a, #atd a, a:link, a:visited, a:hover, a:active, .link, a font[color="#FF7300"], .spell{
  color: #FF7300 !important;
}

/* Top Nav Bar Fixes
----------------------------------------------*/
#guser{
  background:none repeat scroll 0 0 #282828 !important;
  border-bottom:1px solid #444 !important;
  width:100% !important;
  margin-top: -1px !important;
}

#guser nobr{
 margin-right: 2px !important;
 color: #FF7300 !important;
}

#gbi, #gbg, .gac_m {
  background: #282828 !important;
  border-color: #444 !important;
  -moz-box-shadow: 4px 4px 10px rgba(0,0,0, 0.5) !important;
}

a.gb2:hover,
.gac_b .gac_c,
.gac_b .gac_d {
  background: #444 !important;
}

/* Search field / recommendations
----------------------------------------------*/
.gac_m, .gac_id{
  background: #333 !important;
  color: fff !important;
}

.gac_id, .gac_od{
  border: #222 !important;
}

#foot{
  margin:auto !important;
}

/* Search results fixes
----------------------------------------------*/
/* big link */
#res .g .r a {
  color: #FF7300 !important;
}

/* big link visited */
#res .g a:visited {
  opacity: 0.5 !important;
}

/* link description */
#res .g .s {
  color: #c9c9c9 !important;
}

/* URL text */
.a, cite, .cite, .cite:link {
  color: #6e6e6e !important;
}

/* cached and similar links */
#res .g .s .gl a {
  color: #999 !important;
}

/* dashes between Cached and Similar links */
.gl { color: #999 !important; }

/* visited cached and similar links */
#res .g .s .gl a:visited {
  opacity: 0.6 !important;
}

/* sub page links (on first link result) */
#res .s .slk a {
  color: #ddd !important;
  opacity: 0.8 !important;
}

/* visited sub page links  */
#res .s .slk a:visited {
  opacity: 0.5 !important;
}

/* larger link title */
#res .l { font-size: 1.1em !important; }

/* more line spacing on descriptions */
#res .s { line-height: 1.4 !important; }

/* image thumbnails */
#res .g img {
  border: none !important;
  -moz-box-shadow: 4px 4px 10px rgba(0,0,0,0.4) !important;
}

/* File type (ex: [PDF]) next to link */
.g .w { color: #ff6 !important; }

/* "File Format" */
.f { color: #ffc !important; }


/* "video results" time info */
#res .g .ts font[color="#666666"] {
  color: #ffc !important;
}

/* "more options" */
.tbo #tbd {
  background: transparent !important;
  border-color: #666 !important;
}

/* source code listing */
#res tt { color: #fff !important; }

/* Side Navbar fix
----------------------------------------------*/
#showmodes{
  border-bottom: 1px solid #777 !important;
  margin-right: 5px !important;
}

#leftnav{
  background: #333 !important;
}

#center_col{
  border-left: 1px solid #444 !important;
  margin-right: 0px !important;
} 

#sc-block .sc a {
  border-color: #444 !important;
} 

#rhs, #tads{
  Display: none !important;
}

/* Image Fixes
----------------------------------------------*/
.csb, .ss, #logo span, .play_icon, .mini_play_icon, .micon, .close_btn, #tbp, .mbi {
  background-image: url("http://img441.imageshack.us/img441/2230/imagesheetorange.png")!important;
}

#logo{
   background:transparent url(http://img541.imageshack.us/img541/5850/googleorange.png) top left no-repeat !important;
}

img[src*="/intl/en_ALL/images/logo2.gif"], img[src*="/intl/en_ALL/images/logos/images_logo_lg.gif"], img[src*="intl/en_ALL/images/logos/video_logo_lg.gif"], img[src*="/intl/en_ALL/images/srpr/logo1w.png"],
img[src*="/logos/"]{
	width:0px !important;
	height:103px !important;
	padding-right:281px !important;
	background:transparent url(http://img541.imageshack.us/img541/5850/googleorange.png) top left no-repeat !important;
}

a#logo{
  background-image: none !important;
}

/* small logo */
a#logo > img {
  width: 0 !important;
  height: 106px !important;
  padding-left: 164px !important;
  background-image: url("http://img441.imageshack.us/img441/2230/imagesheetorange.png")!important;
}

/* Hide small logo text link, and intl text name */
a#logo, #lgpd + div > div[style^="color: rgb(102, 102, 102); font-size: 16px"] { font-size: 0 !important; }

/* Widescreen Fix
-----------------------------*/
#cnt, .s, a.l{
  max-width: 100% !important;
}
}
