css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("portal.nifty.com") {

#pageKey,
#subContents,
body > div > p,
#mainContents > br,
#mainContents > table[background],
body > div td[bgcolor="#000000"],
body > div img[src="../../../../page_img/foot.gif"],
body > div img[src="../../../../page_img/ten_line_l.gif"],
body > div td[class="tx12px"][align="right"],
script + table,
script + table + table,
script + table + table + br,
script + table + table + br + br,
script + table + table + br + br + table,
script + table + table + br + br + table + table,
script + br + table,
script + br + table + table,
script + br + table + table + table,
script + br + table + table + table  + br,
script + br + table + table + table  + br + table,
script + br + table + table + table  + br + table + table,
script + br + table + table + table  + br + table + table + table,
script + br + table + table + table  + br + table + table + table + br,
script + br + table + table + table  + br + table + table + table + br + table,
script + br + table + table + table  + br + table + table + table + br + table + table
{ display: none !important; }

#container,
#mainContents
{
    background: #ffffff !important;
    width: 100% !important;
    overflow: visible !important;
    float: none !important;
}


table,
td[width="640"]
{
    width: 100% !important;
}

}
