css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("macbook.blog83.fc2.com") {

/* New format */

#footer,
#menu,
#search,
#rmenu,
.pagetop,
.pagetop2,
.kizi_body > iframe,
.fc2_footer,
.combottom,
#comformtitle,
form[name="comment_form"],
#tbheader,
.shitanabi,
.content1,
.content2
{ display: none !important; }

body,
#container,
#header,
#main,
#footer,
#topimage,
#left,
.content3,
.combo,
.comtitle
{
    background: #ffffff !important;
    width: 100% !important;
    overflow: visible !important;
    float: none !important;
    margin: 0 0 0 !important;
    padding: 0 !important;
}

.comtitle
{
     background: #dddddd !important;
}


/* Old format */

#NnVD,
.menu,
.blank,
table#layout > tbody > tr > td.center > div[style = "height: 10px;"],
a[name = "comment"] + table + div + table,
a[name = "trackback"] + table,
.page,
#copyright,
#twitterFollowBadge
{ display: none !important; }

body,
body > center > table
{
    background: #ffffff !important;
    width: 100% !important;
    overflow: visible !important;
    float: none !important;
}

}
