@-moz-document url-prefix(https://mail.google.), url-prefix(http://mail.google.) {

/* Body background */
.bn{
    background: #333 url("data:image/png;base64,") top left repeat-x !important;
    color: #DDDDDD !important;
    font-family: Arial, Helvetica, sans-serif !important;
}

.mb div {
background-image: none !important;
background-color: transparent !important;
border-width: 0px !important;
}

.mb table, .mb tr, .mb td {
background-color: transparent !important;
background-image: none !important;
border-width: 0px;
}

/*fix tooltip background*/
#tip  {background-color: #555 !important; border-style: solid !important; border-width: 1px !important; border-color: #FFF !important;}
.ppoc {background-image: none !important; background-color: #555 !important;}
/*Wide Search Box*/
[name="q"] {width:625px; font-size:14pt!important; color:black!important; font-weight:bold!important }

/*Hide Spam Bold Count */
#ds_spam b { visibility: hidden; }
#ds_spam b::before { content: "Spam"; visibility: visible; font-weight: normal; }

[id^=serd_] div {background-color: #444 !important;}

.pu {
background:#AAA none repeat scroll 0% !important;
border-color:#DDDDDD !important;
border-style:solid !important;
}

.pu a:link{ color: #DDDDDD !important; }
.pu a:visited { color: #DDDDDD !important; }
.pu a:hover { color: #DDDDDD !important; background-color: #5570cc !important;}


.ma { border-width: 0px !important;}
.tbb {border-color: #777 !important; background-color: #777 !important;}

.ef {
border-color: #444 !important;
border-width: 1px !important;
border-style: solid !important;}
body { background-color: #222 !important;
    color: #DDDDDD !important;
    font-family: Arial, Helvetica, sans-serif !important;
}

[class~=nl] {color: #b8b8b8 !important;}
/* defaults for all links */
a:link{ color: #b8b8b8 !important; }
a:visited, .q, .gmail_quote{ color: #a0a0a0 !important; }
a:hover { color: #ccc !important; }

[type="submit"] { display: none !important;}

font, div, td { color: #DDDDDD !important; }

input *, textarea *, .aAutoComplete { background-color: -moz-Field !important; color: -moz-fieldtext !important; }

span#more, #gbar .gbard, #gbar [class^="gb"] { background: #000 !important; border: none !important; }
#gbar .gbard a:hover, #gbar [class^="gb"] a:hover { background: #444 !important; }
#gbi, #gbh { border: none !important; background: transparent !important; }

[id^="gbar"] { border: none !important; }

/* HEADER IMAGE */
body div#ds_inbox img {    display: none !important;}
body div#ds_inbox {background-image: url("data:image/png;base64,") !important;
        height: 50px !important;
    width: 135px !important;
    background-repeat: no-repeat !important;
}

/* Hide GMail invites */
span[class~=ilc][class~=sxs] {
  display: none !important;
}
[id^=prf_g]{color: #b8b8b8 !important; }
.lk, .l, .lkw {color: #b8b8b8 !important; }

[class~=ur] {
background-color: transparent !important;
border-color:#DDDDDD !important;
}

[class~=rr] {
background-color: transparent !important;
border-color:#DDDDDD !important;
}
#pop table {border-color: #DDDDDD !important;}
#pop td, .s {background-color: #666 !important;}
.rr td { color: #666 !important;}

[id^="roster_filter"],[id^="tcb"] { display: none !important; }
.bz_rlsp { display: none !important; }
.tlc td { border-bottom: none !important;}
[class~=fs] {
border-left-color: transparent !important;
background-color: transparent !important;
}
.bz_cnst, #bc_csc {border-width: 0px !important;}
.bz_mecs, .bz_mecs_on, .bz_mecs_over { border-color: #777777 !important;}
.bz_mestri, .bz_mestri_on, .bz_mestri_over { background-color: #777777 !important; border-color: #777777!important;}
.bz_mestri_on, .bz_mestri_over {background-color: #666666 !important;}
#fb {background-color: transparent !important; border-width: 0px !important;}

.rnd,  .rnd1, .rnd2
{display:none !important}

.bz_rbbb{
color: #b8b8b8 !important;
}


.fbd { background-color: #777 !important; border-color: #777 !important}
.fbe { background-color: #777 !important; border-color: #777 !important}
.fbh { background-color: #555 !important; border-color: #555 !important}

.thm { border-width: 0px !important;
color: #DDDDDD !important;
background-color: transparent !important;
border-color:#DDDDDD !important;
}
.clfd, #clc { border: 0px !important;}

/* Varoius elements with a transparent background */
.y, .thc, .chc, #tct, .nb, .nc, .s, #aca, .bz_rr, .bz_mecd, .bz_meff, .thc, .rtabi, .cg, .tbo, .ci, .cf, #sm_2, #sm_4,.nm, .rno, .smb,.thm, .pr{
color: #DDDDDD !important;
background-color: transparent !important;
border-color:#DDDDDD !important;
}
#nb_1 {padding-top: 12px !important;}

.tbo { background-color: #444 !important;}
.mb, .cbln, .cbrn, [id^=rc_]{background-image: none !important;}

[id^=mf_] { background-color: transparent !important; }


/* New images for message borders */
.cbotn { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.ctopn { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.ctln { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.cbtln { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.stln { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.cbln2, .cbln { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.ctrn { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.strn { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.cbtrn { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.cbrn2, .cbrn { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.cbusd{ background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.cbud { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.cbuhsd, .cbupsd{ background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}
.cbuhd, .cbupd { background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;}



table.tlc tr.rr:hover,
table.tlc tr.y:hover,
table.tlc tr.ur:hover { -moz-opacity: 1.0 !important;
background-color: #111111 !important;}

.s{ border-bottom: 0px !important;}

.pf { border-color: #999999 !important;}
#ds_starred, .cbuhl, .cbuhm, .cbuhs, .cbul, .cbum, .cbus {
      display: none !important; }
[id^=cp_] {padding-right: 15px !important; margin-right:5px !important;}
#cp_r {padding-right: 5px !important; margin-right: 0px! important}
.pcl, .pfs, .pch, .pfs div{ border-width: 0px !important;}

table .pr, #pp, .pct, .pcl { background-color: transparent !important; border-color: #777777 !important; }

#hm ,td .pr{ background-color: #777777 !important; border-color: #777777 !important;}
.ac span.asel{
background:#777 none repeat scroll 0% !important; border: 0px !important; color: #DDDDDD !important;}
.pmc {background-color: transparent !important; border-width: 0px !important;}
.pch{ border-color: #777777 !important; border-width: 1px !important;}
.ctfd {border-left: 0px !important;}
.sd .an, .an, .cv, .crtab, .ctsm,.ctmcchov, #g, #a, #l, #f, #d, #c, #w, .psm, #pcr, .pch{
background:#444444 none repeat scroll 0% !important;}


/* Contact popup */
.ctmec { background-color: #666666 !important;}
.ctmes { background-color: #444444 !important;}
.wackbody, .wackinfomsg, .wackinputopen, #bc_history, #wkbtm_ td, .bz_cw_mom, .bz_cw_po {border-width: 0px !important;
background-color: #555555 !important;}

/* Button styles */
button {border-color: #666666 !important; border-style: solid !important; border-width: 2px !important;}
button div { color: black !important;}
.ab {  color: black !important; background-color: #DDDDDD !important; border-color: #666666 !important; border-style: solid !important; border-width: 2px !important;}

/*Remove ads and footer */
#rh, #rh .c{display:none!important;}
#fic{margin-right:inherit!important;}
#ft { display:none!important;}

/*Clean up Search Options and Filter Options */
.mxs, .mxf, #as_f table, #mt2 div {background-color: transparent !important;}
#mt.nor, .mxh, .mxt {color: #B8B8B8 !important}

/*Brighten up colors a bit*/
[style="color: rgb(121, 6, 25);"] {color: rgb(200,20,20) !important;}
[style="color: rgb(0, 104, 28);"] {color: rgb(40,170,40) !important;}
[style="color: rgb(91, 16, 148);"] {color: rgb(170, 40, 140) !important;}

/*Fix dropdown menus*/

.dvm {
background:#AAA none repeat scroll 0% !important;
border-color:#DDDDDD !important;
border-style:solid !important;
border-width: 1px !important;
}

[id^=to_fontsize-] {background-color: #AAA !important;}
[id^=to_fontsize-]:hover {background-color: #8B8B8B !important;}
[id^=to_fontname-] {background-color: #AAA !important;}
[id^=to_fontname-]:hover {background-color: #8B8B8B !important;}
