css:
@namespace "http://www.w3.org/1999/xhtml";

@-moz-document url-prefix("http://tawerna.rpg.pl/forum"),
url-prefix("http://www.tawerna.rpg.pl/forum"),
url-prefix("http://trpg.pl/forum"),
url-prefix("http://www.trpg.pl/forum")  {

body{
  font-size: 11px !important;
  font-family: "Verdana" !important;
  background-image: url(http://img407.imageshack.us/img407/9361/drewno4duzedh3.jpg) !important;}

#wrapcentre {
  margin: 5px 25px 0 25px !important;}

#logodesc {
  background: none !important;
  border-bottom: none !important;
  font-family: "Monotype Corsiva", Batang !important;
  color: #382513 !important;}

#logodesc td {
  line-height: 25px !important;}

#logodesc strong {
   font-size: 20px !important;
  font-weight: normal !important;}

#logodesc a {
  color: inherit !important;}

#logodesc img {
  max-height: 110px !important;
  max-width: 320px !important;}

h1 {
  color: #382513 !important;
  font: italic normal normal 76px "Monotype Corsiva", Batang !important;
  min-width: 300px !important;
  line-height: 60px !important;}

h2 a {
  color: #0D004C !important;}

h4 a {
  color: #002F2F !important;}

th {
  color: #DADADA !important;
  background-color: #002F2F !important;
  background-image: none !important;}

th a {
  color: #FFA34F !important;}

.cat, .catdiv {
  font-size: inherit !important;
  height: 20px !important;
  background: transparent !important;}

.tablebg {
  background-color: #A7A37E !important;}

.row1 {
  background: #E6E2AF !important;}

.row2,
.row2 td {
  background: #EFECCA !important;}

.row3 {
  background: #B4AF91 !important;}

#menubar .genmed a,
p.searchbar a {
  color: #363942 !important;}

.nav {
  color: darkred !important;}

p.linkmcp a {
  color: darkgreen !important;}

p.breadcrumbs a {
  color: #0D004C !important;}

.quotetitle, .attachtitle {
  background: #002F2F !important;
  border-color: white !important;
  border-style: dotted !important;
  color: #F6F5E5 !important;}

.quotecontent, .attachcontent {
  background: #F6F5E5 !important;
  border-color: #002F2F !important;
  border-style: dotted !important;
  color: #002F2F !important;}

.codetitle {
  background: #0D004C !important;
  border-color: white !important;
  border-style: dotted !important;
  color: #F6F5E5 !important;}

.codecontent {
  background: #F6F5E5 !important;
  border-color: darkblue !important;
  border-style: dotted !important;
  color: #0D004C !important;}

a.forumlink {
  color: #0D004C !important;
  font-size: 13px !important;}

a.topictitle {
  color: #002F2F !important;
  font-weight: bolder !important;}

a.postlink {
  color: #467865 !important;
  font-style: italic !important;} 

.post,
.spacer,
textarea,
select,
option,
.btnbbcode,
.btnmain,
.btnlite,
.helpline {
  background: #F6F5E5 !important;}

/* -------------------------- Dopasowanie obrazkÃ³w ------------ */

.postbody {
  overflow-x: auto !important;
  display: block !important;}
}
