@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain(jom.agens-ev.de) {

    body        { font-family: Verdana !important }
    fieldset br { display:none !important }
    a           { color:white !important }
    a:hover     { color:red !important }
    a:active    { color:red !important }
    a:focus     { color:red !important }

    td>p { color:white !important }

    /* Workaround fÃ¼r fehlerhafte Syntax, die jetzt fehlenden Beschriftungen werden nachgetragen */
    fieldset>p  { display:none }
    fieldset>legend:first-child { font-size:14px !important; font-weight:bold !important; color:white !important }

    /* Farbe und Beschriftungen fÃ¼r <fieldset> in der Navigationsleiste */
    tr[height="440"]>td[width="148"]:first-child>fieldset>legend:first-child:before { content:"Spenden" }
    tr[height="440"]>td[width="148"]:first-child>fieldset+br+fieldset>legend:first-child:before { content:"AuftrÃ¤ge" }
    tr[height="440"]>td[width="148"]:first-child>fieldset+br+fieldset+br+fieldset>legend:first-child:before { content:"Verwaltung" }
    tr[height="440"]>td[width="148"]:first-child>fieldset>legend:first-child { color:#eeee00 !important }

}



@-moz-document url("http://jom.agens-ev.de/index.php"),
               url("http://jom.agens-ev.de/") {
    table table:first-child p /*, table script+table p */
               { color:darkblue !important; font-size:14px !important}
}




@-moz-document url("http://jom.agens-ev.de/index.php?m=s1") {
    table table[width="608"] { font-size:10pt !important; 
                               color:darkblue !important; 
                               font-weight:normal !important }
}
