    table[width="670"] tr    { font-size:10pt !important; 
                               color:darkblue !important; 
                               font-weight:normal !important }
}
@-moz-document url("http://jom.agens-ev.de/index.php?m=s3") {
    table[width="670"] tr td                             { color:darkblue !important }
    table[width="670"] tr+tr td:first-child[colspan="4"] { color:red !important }
}




@-moz-document url("http://jom.agens-ev.de/index.php?m=a1") {
