css:
@namespace url(http://www.w3.org/1999/xhtml); 
	
@-moz-document domain(xiaonei.com), domain(renren.com) {

.blank-holder{
display:none;
}
	
.publisher .status-publisher textarea{
	-moz-border-radius:5px;
	padding:6px !important;
min-height:32px !important;
}
.home .newrequests li{
width:auto !important;
float:none !important;
}
input[type=text],input[type=submit]{
-moz-border-radius:5px;
border:0 !important;
}
#navSearchInput{
-moz-border-radius:0 ;
}
.publisher .status-publisher input.submit{
height:32px !important;
}
body, select, label, textarea, input{
font-family:verdana !important;
}
.user-data,.panel.bookmarks{
-moz-border-radius:6px;
}
#search-submit{
margin-top:-1px !important;
}
.reply-adding textarea{
-moz-border-radius:4px;
min-height:18px;
padding:3px 4px !important;
line-height:1.5em;
}
ul.richlist.feeds li .figure a i{
width:50px !important;
}
#sponsorsWidget{
display:none;
}
ul.richlist.feeds li .figure a.avatar,
.home .footprint .people-list li span.headpichold {
border:1px solid #CEE1EE !important;
padding:2px !important;
}
ul.richlist.feeds li .figure a.avatar:hover,.home .footprint .people-list li span.headpichold:hover{
border-color:#BDC7D8 !important;
}
.home .footprint .people-list li span.headpichold a,.home .footprint .people-list li span.headpichold,
.home .footprint .people-list li span.headpichold i{
width:50px !important;
height:50px !important;
}
.show-all {
-moz-border-radius:6px;
margin:10px 0 !important;
}
.feed-content .headline .title h4,.feeds h3{
font-size:12px !important;
}
.feed-header li{
padding:6px 15px 4px !important;
}
.feed-header li.current{
-moz-border-radius:5px 5px 0 0;
padding:5px 15px 6px !important;
}
.footprint .time-tip{
display:block !important;
position:static !important;
background:none !important;
color:#999 !important;
}
.home .with-arrow .side-item-body ul.people-list li{
height:95px !important;
width:56px !important;
margin-right:18px !important;
}
#footPrint{
height:322px !important;
height:auto !important;
width:225px !important;
}
#footPrintList{
width:225px !important;
left:0 !important;
}
.info-edit,k,.pymk{
display:none !important;
}
#footForward,#footBack{
display:none !important;
}
.side-item-body > div{
width:225px !important;
}
}
