css:
@-moz-document domain("ebuyer.com") {
	/* LS 2012-06-08 13:05:38
	 * Set product height to 105px and tighten up the padding.
	 */
	.product-listing {
		padding:10px 10px 10px 0!important;
		height:105px!important;
	}

	/*
	 * LS 2012-06-08 13:05:34
	 * Max height for images.
	 */
	.image-holder {
		height:105px!important;
	}
		.image-holder img {
			max-height:105px!important;
		}

	/*
	 * LS 2012-06-08 13:05:26
	 * Reduce the height of the description block.
	 */
	.title-holder {
		height:105px!important;
		overflow-y:visible!important;
	}
		.review {
			margin-bottom:0!important;	/* LS 2012-06-08 12:53:00 | reduce the space beneath the review count. */
		}

	/*
	 * LS 2012-06-08 13:05:29
	 * Reduce the height of the price block.
	 */
	.price-holder {
		height:105px!important;
		min-width:135px!important;
	}
		.stock {
			margin-bottom:10px!important;	/* LS 2012-06-08 12:49:02 | reduce the space beneath the stock count. */
		}
}
