css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {
.damncrc-topic {
background:#CCD9C8!important;
border-top:4px solid #C0D0C1!important;
border-right:3px solid #C0D0C1!important;
border-bottom:1px solid #E2E8E1!important;
border-left:2px solid #A4B8A8!important;
}
.damncrc-topic a {
color:#196BAB!important;
}
.damncr-title {
background:-moz-linear-gradient(bottom, #D4E0D2, #E2E8E1)!important;
border-bottom:1px solid #9EB1A2!important;
}
.damncr-title a:hover {
color:#196BAB!important;
}
.damncrc-chat-window {
border:2px solid #BCCBBA!important;
}
.alt0 {
background:-moz-linear-gradient(bottom, #CCD9C8 99%, #ADC2B1)!important;
}
.damnc-tabbar a { 
background:-moz-linear-gradient(top, #BCCBBA 60%, #8EA491)!important;
-moz-border-radius:7px 0px 0px 0px!important;
}
.damnc-tabbar a i { 
background:-moz-linear-gradient(top, #BCCBBA 60%, #8EA491)!important;
-moz-border-radius:0px 7px 0px 0px!important;
}
.damnc-tabbar strong.withX { 
background:-moz-linear-gradient(bottom, #E2E8E1, #fff)!important;
-moz-border-radius:7px 7px 0px 0px!important;
border-right:1px solid #9EB1A2!important;
padding-right:23px!important;
margin-right:2px!important;
}
.damnc-tabbar strong.withX i {
display:none!important;
}
.damncr .self-hl .from span {
background:#EFEFEF!important;
padding:0px 5px 0px 5px!important;
-moz-border-radius:5px!important;
}
.damncr .other-hl .from span {
background:#fff!important;
padding:0px 5px 0px 5px!important;
-moz-border-radius:5px!important;
}
.damncr .other-hl .text span {
background:#fff!important;
padding:0px 5px 0px 5px!important;
-moz-border-radius:5px!important;
}
.damncrc-icon-roomname {
display:none!important;
}
.damncrc-iconbar a {
display:none!important;
}
.damncrc-iconbar {
color:transparent!important;
}
.superdamn-buttonbar {
position:relative!important;
top:0px!important;
}
input {
-moz-border-radius:5px!important;
background:-moz-linear-gradient(bottom, #fff, #dedede 80%)!important;
width:99.5%!important;
}
textarea {
-moz-border-radius:5px!important;
background:-moz-linear-gradient(bottom, #fff, #dedede 80%)!important;
width:99.5%!important;
}
input.button {
position:relative!important;
top:-4px!important;
left:0px!important;
width:50px!important;
float:right!important;
}
.damncrc-input {
border-right:3px solid #C0D0C1!important;
}
.damncr-members dt {
background:-moz-linear-gradient(top, #fff, #E2E8E1 5%, #D4E0D2 95%, #fff)!important;
border:1px solid #9EB1A2!important;
margin-left:-14px!important;
margin-right:-23px!important;
text-align:center!important;
white-space:nowrap!important;
}
.damn-lo-col-right {
background:#D4E0D3!important;
border-left:1px solid #9EB1A2!important;
border-top:2px solid #C0D0C1!important;
overflow-x:hidden!important;
}
.damncrc-iconbar {
border-top:1px solid #EDF0EC!important;
border-right:3px solid #C0D0C1!important;
height:20px!important;
}
