css:
@namespace url(http://www.w3.org/1999/xhtml);
 @-moz-document domain("imo.im") {
 body {
 background:none transparent !important;
 opacity:.2 !important;
}
body:hover {
 opacity:1 !important;
}
img {
 opacity:.4;
}
div.im_actions, div#imofooter, .toolbar {
 display:none !important;
}
div, table, td, tr, a, span, li, textarea, fieldset, input {
 background:none transparent !important;
 color:#aaa !important;
}
 .yui-panel, div#buddyInfo{
background:#fff !important;
}
 .actions {
 text-align:left !important;
}
 div.convPanelMsgHistory {
background:transparent !important;
}
}
