css:
@namespace url(http://www.w3.org/1999/xhtml); 
	
@-moz-document domain(xiaonei.com), domain(renren.com) {
body{
background:#D3DFD1 !important;
}
.navigation-wrapper,.navigation{
width:auto !important;
}
.navigation{
min-width:980px !important;
border-bottom:1px solid #313F3A !important;
background:#495B4F url(data:image/png;base64,) repeat-x !important;

}
.navigation .nav-body{
background-color:transparent !important;
float:none !important;
padding-left:160px !important;
border-top:0 !important;
width:auto !important;
}
.full-page-holder,.full-page{
width:auto !important;
min-width:960px !important;
}
.menu-bar {
padding:0 !important;
min-width:960px !important;
}
.navigation .nav-other {
background:none !important;
}
.navigation .nav-other .menu a{
color:#B3C432!important;
}
.navigation .nav-main{
margin-left:0 !important;
}
.navigation .nav-body a{
color:#EAF2EE !important;
font-size:12px !important;
}
.navigation .nav-body .menu a:hover{
background-color:#3C4441 !important;
}
#logo2 a{
background:url(data:image/png;base64,) no-repeat;
display:inline-block;
width:153px;
height:30px !important;
}
#logo2 a img{
display:none;
}
#logo2{
border:0 !important;
}
#logo2 h1{
padding-top:4px !important;
padding-left:4px !important;
}
a{
color:#337287 !important;
}
.blank-holder{
display:none;
}
	
.publisher .status-publisher textarea{
-moz-border-radius:5px;
padding:4px 6px !important;
min-height:32px !important;
font-size:12px !important;
}
.publisher .status-publisher textarea.focus{
height:70px !important;
border-color:#555 !important;
}
#publisher_emotion,.newsfeed-reply-emotions{
top:72px !important;
padding:5px !important;
-moz-border-radius:5px;
border-color:#A6B2A6 !important;
}
.newsfeed-reply-emotions{
position:relative;
right:0px;
top:3px !important;
}
.home .newrequests li{
width:auto !important;
float:none !important;
}

input[type=button],input[type=submit]{
-moz-border-radius:5px !important;
border:0 !important;

-moz-appearance: none !important;
font-weight:normal !important;
font-size:12px !important;
background:-moz-linear-gradient( center top,#00B8D3 1px,#0086B2 1px,#0086B2  10%, #005F7E) !important;

border:1px solid #1D4253 !important;
}
input[type=button]:hover,input[type=submit]:hover{
background:-moz-linear-gradient( center top,#00B8D3 1px,#00A2C4 1px,#00A2C4 10%, #00739A) !important;
}
input[type=text],textarea{
border-color:#9FB1A3 !important;
-moz-border-radius:5px !important;
background:#F2F2F2;

}
#navSearchInput{
-moz-border-radius:0 ;
}
.publisher .status-publisher input.submit{
height:32px !important;
}
body, select, label, textarea, input{
font-family:verdana !important;
}
.home #sidebar {
background:#D3DFD1 !important;
border-right:1px solid #9FB1A3  !important;
}
.user-data,.panel.bookmarks{
background:none !important;
border:0 !important;
}
.user-data a:hover,.panel.bookmarks a:hover{
background-color:#C2CCC2 !important;
}
.panel.bookmarks .footer{
margin:10px 10px 0 0 !important;
background:#B9C9BB;
border:0 !important;
padding:8px 10px !important;
-moz-border-radius:0 5px 5px 0;

}
.panel.bookmarks .footer a{
height:auto  !important;
}
.panel.bookmarks .footer a:last-of-type{
display:none;
}
.panel.bookmarks .footer a:hover{
background-color:transparent !important;
}
#search-submit{
border:0  !important;
margin-top:1px !important;
}
#search-submit a{
-moz-border-radius:0 5px 5px 0 !important;
}

ul.richlist.feeds li .figure a i{
width:50px !important;
}
ul.richlist.feeds li:last-child{
border-bottom:0 !important;
padding-bottom:0 !important;
}
#sponsorsWidget{
display:none;
}
ul.richlist.feeds li .figure a.avatar,
.home .footprint .people-list li span.headpichold {
border:1px solid #BDC7D8 !important;
padding:2px !important;
background:#DFE6DE !important;
-moz-border-radius:3px;
}
ul.richlist.feeds li .figure a.avatar:hover,.home .footprint .people-list li span.headpichold:hover{
border-color:#A6B2A6 !important;
background:#fff !important;
}
.home .footprint .people-list li span.headpichold a,.home .footprint .people-list li span.headpichold,
.home .footprint .people-list li span.headpichold i{
width:50px !important;
height:50px !important;
}
.show-all,.newsfeed-empty-ajax {
border-color:#1D4253  !important;
color:#fff !important;
-moz-border-radius:6px;
margin:10px 0 !important;
background:#00759C !important;
background:-moz-linear-gradient( center top,#00B8D3 1px,#0086B2 1px,#0086B2  10%, #005F7E) !important;
}
.show-all:hover{
background:-moz-linear-gradient( center top,#00B8D3 1px,#00A2C4 1px,#00A2C4 10%, #00739A) !important;
}
.newsfeed-empty-ajax{
font-size:14px !important;
font-weight:bold !important;
border-color:#93A98F !important;
color:#8C9B8C !important;
background:-moz-linear-gradient( center top,#EBF1E6 1px,#D0DEC3 1px,#D0DEC3 10%, #BDD1B4) !important;
}
.newsfeed-empty-ajax a{
color:#337287 !important;
margin-left:3px!important;
}
.show-all a,#feedLoading{
padding:5px 0 !important;
color:#fff !important;
background:none !important;
font-size:14px !important;
line-height:40px;
}
#feedLoading span{
color:#fff !important;
font-weight:bold;
background-position:0 50% !important;
}
.feed-content .headline .title h4,.feeds h3{
font-size:12px !important;
}
.feed-header li{
padding:6px 20px 4px !important;
}
.feed-header li.current{
border-color:#9DB0A2  !important;
-moz-border-radius:5px 5px 0 0;
padding:5px 20px 6px !important;
background:#F1F4F1 !important;
background:-moz-linear-gradient( center top,#fff 1px,#F1F4F1 1px,#E5EAE3) !important;
}
.feed-header li.current a{
color:#2C3635 !important;
}

.feed-header li a:hover{
text-decoration:none !important;
color:#2C3635 !important;
}
.feed-header{
border-bottom:1px solid #EFF3EF  !important;
}
.news-feed .fresh-news{
padding:4px 10px !important;
}
.footprint .time-tip{
display:block !important;
position:static !important;
background:none !important;
color:#999 !important;
}
.home .with-arrow .side-item-body ul.people-list li{
height:85px !important;
width:56px !important;
margin-right:10px !important;
}
a[href="http://www.renren.com/myfoot.do"]{
display:none;
}
#footPrint{
height:322px !important;
height:auto !important;
width:220px !important;
}
#footPrintList{
width:200px !important;
left:0 !important;
}
.info-edit,k,.pymk{
display:none !important;
}
#footForward,#footBack{
display:none !important;
}
.side-item-body > div{
width:auto !important;
}
.home .main-page{
width:auto !important;
float:none  !important;
margin-left:160px !important;
border-left:1px solid #9FB1A3  !important;
background:#CCD9C8!important;
padding-top:20px !important;
}
.home .home-body {
margin-right:250px !important;
width:auto !important;
float:none !important;
}
.news-feed{
clear:none !important;
width:100% !important;
float:left;
}
.home-content-holder{
position:relative;
}
.home .home-sidebar {
position:absolute;
top:0;
right:15px;
}
.statuscmtitem{
background:none !important;
border:none !important;
padding:0 !important;
}
.statuscmtitem {
background:#DAE4D9 !important;
border:1px solid #A6B2A6 !important;
-moz-border-radius:6px;
padding:10px !important;
margin:0 0 10px !important;
background:-moz-linear-gradient( center top,rgb(255,255,255) 1px,rgb(228,233,227) 1px, rgb(228,233,227) 10%, rgb(218,228,217) )!important;
}
.reply-adding{
border:0 !important;
background:none!important;
padding:0px !important;
}
.reply-adding .minfriendpic{
display:none !important;
}
.reply-adding .reply-nav{
margin:5px 0 0 !important;
}
.reply-adding .reply-nav span span:last-of-type{
display:inline-block !important;

}
.min-cmtbox {
background:none !important;
}
.mincmtcount{
color:#97A2A0 !important;
padding-right:4px !important;
}
.reply-adding.actived textarea{
height:70px !important;
}
.reply-adding textarea{
width:100% !important;
-moz-box-sizing: border-box;
min-height:30px;
background:#fff!important;
-moz-border-radius:4px;
padding:5px 6px !important;
line-height:1.5em;
border-color:#A6B2A6 !important
}
.reply-adding.actived textarea{
border-color:#555 !important;
}
ul.richlist.feeds li{
border-bottom:1px solid #A6B2A6 !important;
border-top:1px solid #F2FCF1!important;
margin-top:0 !important;
padding-top:10px !important;
}
ul.richlist.feeds li:first-of-type{
border-top:0 !important;
}
.feed-header ul {
border-bottom:1px solid #A6B2A6 !important;
}
ul.richlist.feeds li .content i.photo, ul.richlist.feeds li .content img.photo{
 padding:0 !important;
-moz-box-shadow:1px 3px 3px rgba(0, 0, 0, 0.3);
border:0  !important;
}
.menu-dropdown-border {
border:0 !important;
}

.bookmarks.panel .more a.legend:hover,.bookmarks.panel .more.hover a.legend,.bookmarks.panel .more:hover a.legend{
border:0 !important;
color:#fff !important;
background:#3B4340 !important;
text-decoration:none !important;
-moz-border-radius:5px 0 0 5px !important;
margin:0 0 0 3px !important;
padding-left:7px  !important;

}
.menu-dropdown,.bookmarks.panel .more div.apps{
background:#3B4340 !important;
-moz-border-radius:0 0 5px 5px;
border:0 !important;
-moz-box-shadow:1px 3px 6px rgba(0, 0, 0, 0.3);
}
.bookmarks.panel .more div.apps{
padding:5px 0 !important;
-moz-border-radius:5px 5px  5px 0;
}
.menu-dropdown a,.bookmarks.panel .more div.apps a{
color:#C0D0B6 !important;
}
.menu-dropdown a:hover,.bookmarks.panel .more div.apps a:hover{
background-color:#458497 !important;
color:#fff!important;
}
.menu-dropdown h4{
color:#C0D0CA !important;
font-weight:bold !important;
}
#navMyApps{
border-left:1px solid #26332F!important;
}
#navMyApps ul{
border-color:#52605D!important;
}
.separator{
background-color:#52605D!important;
margin:0!important;
border-top:1px solid #26332F;
}
.navigation .menu-title a{
padding:0 10px!important;
}
#showAppMenu,#optionMenuActive a{
padding-right:25px!important;
background:url(data:image/png;base64,) 80% 50% no-repeat;
}
#optionMenuActive a{
background-image:url(data:image/png;base64,)!important;
}
#navSearch #search-input #navSearchInput {
border-color:#46504A !important;
-moz-border-radius:5px 0 0 5px !important;
}
#publisher_submit{

padding:6px 30px 7px !important;
height:auto !important;
width:auto !important;
}
.publisher .status-publisher form{
padding-right:100px !important;
}
.mincmt-body {
width:auto !important;
}
ul.richlist.feeds li .details span.duration{
color:#A5AEA3 !important;
}
ul.richlist.feeds li h3 a[stats=NF_People]{
font-weight:bold;
color:#304030;
}
ul.richlist.feeds li a.delete,.x-to-hide {
background:url(data:image/gif;base64,) no-repeat !important;
width:15px !important;
height:15px !important;
top:8px !important;
right:3px !important;
}
ul.richlist.feeds li a.delete:hover,.x-to-hide:hover{
background-position:-15px 0 !important;
}
.home .side-item{
border:0 !important;
}
.side-item-body,.feed-story{
border:1px solid #A6B2A6 !important;
padding:10px !important;
-moz-border-radius:5px;
background:#DAE4D9 !important;
background:-moz-linear-gradient( center top,rgb(255,255,255) 1px,rgb(228,233,227) 1px, rgb(228,233,227) 10%, rgb(218,228,217) 
) !important;
}
.home .side-item .web-function li{
height:auto !important;
}
.web-function img{
display:none !important;

}
#footer{
width:auto !important;
padding:5px 25px 30px !important;
margin:0 !important;
background:#4D625D;

}
#footer .copyright{
border:0 !important;
color:#98A8A3 !important;
}
#footer .copyright a{

color:#8DA898!important;
}
#footer .copyright a:hover{
color:#AFD0BA !important;
}
.news-feed .feed-content, .news-feed .feed-content .headline .title {
width:auto !important;
float:none !important;
margin-left:25px !important;
border:0 !important;
}
.feed-story{
margin:0 0 10px !important;
}
.section-header{
border:0 !important;
padding:0 10px !important;
}
.publisher{
margin-bottom:5px !important;
}
#webpager{
background:#E3E8E2!important;
border:1px solid  #9FB1A3  !important;
border-bottom:0!important;
-moz-border-radius:5px 5px 0 0;
-moz-box-shadow:0 2px 3px rgba(0, 0, 0, 0.3);
}
#webpager .panelbarbutton{
-moz-border-radius:5px 0 0 0;
}
.webpager #back-widget-group .panelbarbutton{
-moz-border-radius:0 5px 0 0 !important;
border:0  !important;
}
.webpager a:hover{
text-decoration:none !important;
}
#webpager-ad-panel{
display:none;
}

}
