@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.de") {
  body, html, li.g {
    font-family: monospace !important;
    font-size: 14px !important;
  }
  body, #body, #main, #ghead, #gog, #leftnav, #fctr, .sblc, .fade, #tbd, #hidden_modes, #hmp, .lst, .gac_od, .gac_id, .gac_m {
    background: #000000 !important;
    color: #FFFFFF !important;
  }
  a {
    color: #7788EE !important;
  }
  a:visited {
    color: #7755DD !important;
  }
  #logo {
    display: none;
  }
  #logo img {
    display: none;
  }
  #center_col {
    right: 10px;
  }
  .s {
    width: 100% !important;
  }
}
