center+center table[cellspacing="5"] input.button{color:#808080!important;position:relative;z-index:-1}
center+center table[cellspacing="5"] td:nth-child(2){font-size:0;letter-spacing:-1px;visibility:hidden}
center+center table[cellspacing="5"] td:nth-child(2):after{content:"999,999,999 Meat";display:block;font-size:12pt;letter-spacing:0;margin-left:-2px;visibility:visible}
center+center table[cellspacing="5"] tr:first-child td{font-size:12pt;letter-spacing:0;visibility:visible}
center+center table[cellspacing="5"] tr:first-child td:after{content:""}
