css:
@charset "utf-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("feedly.com") {

body {
background: #000000 !important;
color: #000000 !important;
}

#mediaArea, #sectionsArea, #bottomArea, #feedlyPart0 {
background: #000000 !important;
border: 0px !important;
}

#photoarea {
display: none !important;
}

#feedlyPart, .sideAreaModule {
 background: #3c3c3c !important;
 border: 5px solid #000000 !important;
color: #fff !important;
margin-left: -35px !important;
}

.cell, .headlines1 {
background: #3C3C3C !important;
color: black !important;
}

 a, a.link, .action {
color: #999966 !important ;
}

.entryHolder {
background: #CCCCCC !important;
}

 a:hover, .action:hover {
color: #666 !important;
}

 a:visited, .action:visited {
color: #666666 !important ;
}

 .feedIndexUnreadCount {
color: #CCCCCC !important ;
}

 .feedIndexSeparator{
border-left:25px solid #ea4100 !important;
height:3px !important;
left:-5px !important;
position:relative !important;
top:-2px !important;
width:200px !important;
}

 #indexActions, .moduleTitleInfo, .moduleTitleText, h1, h2, h3 {
color: #CCCCCC !important;
margin-top: -25px !important;
}

 .u6Entry {
border:1px solid #CCCCCC !important;
background-position: center !important;
}

 .u100Entry, #entryHolder{
border: 0px solid #CCCCCC !important;
}

 .visual {
border:1px solid #CCC !important;
}

 .feedIndex {
width: 280px !important;
height: 16px !important;
cursor: pointer !important;
padding-top: 4px !important;
padding-bottom: 4px !important;
border-bottom-width: 1px !important;
border-bottom-style: solid !important;
border-bottom-color: #808080 !important;
}

 .u100Entry .entryHeader {
margin-bottom: 10px !important;
position: relative !important;
left: -34px !important;
padding-left: 24px !important;
padding-top: 2px !important;
padding-bottom: 2px !important;
background-color: #3C3C3C !important;
color: #000000 !important;
}

 .topRecommendedEntry .visual, .u4Entry .visual, .u10Entry .visual, .u6Entry  {
background-color: #3C3C3C !important;
 }

 .surroundingEntries .selected, .visibleFeedIndex {
background-color:#5b5b5b !important;
}

 .column-subscriptions li {
background-color:#ddeec2 !important;
border-bottom:1px solid #000 !important;
}


 #feedlyNavBarBack, .hhint, div.marker-bubble-popup, .activity .clip, .activity, .clip, .unreadCountHolder, .preferencesItemDescription, .feedlyPart0 {
color:#b7cce0 !important;
}

 .nbrRecommendations, .highlight, .sourceInfo, .sourceInfo a, #diggCount, .listLabel {
color:#CCCCCC !important;
background: #3C3C3C !important;
}

div[style="display: block; top: 0px;"], div[style="width: 130px;"], div[style="display: block;"] {
background: #3C3C3C !important;
border: 0px !important;
}

.topRecommendedEntry {
background: #3C3C3C !important;
border: 0px !important;
}


#sideArea, #feedlyTitleBar, #feedlyPageActionBar {
background: #000000 !important;
}

 .lastModified, .lessThanAnHour, .unreadCountHover,.recommenderLink, .preferencesItemLabel, .subscriptionDashboardUnreadCount {
color:#CCCCCC !important;
}

 .popup, div.marker-bubble-popup, .listColumnArea .visual, #columnPage #tickerArea {
background-color:#555 !important;
color:#CCCCCC !important;
}

 .unreadCount {
color:#555 !important;
}

 .bubble {
border:3px solid #b2d281 !important;
}

 .subscriptionSpringCleaning.overdose  {
background-color:#777 !important;
}

 .subscriptionSpringCleaning.dead {
background-color:#eb470d !important;
}

 .subscriptionSpringCleaning, #quicklookHeader {
background-color:#333 !important;
}

 .subscriptionSpringCleaning.healthy, .showcased {
background-color:#4d778b !important;
}

 #quicklookFramePart {
border:1px solid #86b0e1 !important;
}

 h1, h2, h3, .ModuleTitleText { 
font-family:"Lucida Grande",sans-serif !important; 
  }
}
