css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fromthepavilion.org") {


#content{
font-size:11px!important;
line-height:1.4!important;
}

#content, #content a {
color: #333!important;
}

#content h1, #content h1 a{
color: #000 !important;
}

#alerts a{
color: yellow !important;
}

.skillup {
color: #00F !important;
}


h1 {
background-image:url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1228005709566/config/app/images/customBackground/bg-light.jpg) !important;
background-repeat: repeat !important;
border: 1px solid #009900 !important;
}

.box, .smallbox, table.stats, table.commentary, table.cup, .verywidesmallbox, h1.header, .verywidebox {
background: #EEFFBB !important;
background-image:url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1274518956096/slin-pics/bg-light_yellow.jpg) !important;
}

#dateonline {
font-size: 12px !important;
}

#leftmenu {
background: #EEFFBB !important;
width: 110px !important;
background-image:url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1274518956096/slin-pics/bg-light_yellow.jpg) !important;
}

#leftmenu li {
border-bottom: 0px solid #009900 !important;
}

div#leftmenu {
border: 1px solid #009900 !important;
width: 110px !important;
margin-left: 3px !important;
}

table.display td {
border-bottom: 1px dashed #009900 !important;
}

#content, #wrapper, .ui-tabs-panel, .ui-tabs-nav, .whiteback {
background: #E0FFB0 !important;
background-image:url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1228005485700/config/app/images/customBackground/bg-mid.jpg) !important;
background-repeat: repeat !important;
}

.box, .smallbox, .newsdate, table.stats, table.commentary, table.cup, .verywidesmallbox, table.stats th, table.stats td, h1.header {
border: 1px solid #009900 !important;
}

.newsdate {
background: #DDFFAA !important;
background-image:url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1228005485700/config/app/images/customBackground/bg-light.jpg) !important;
}

h2 {
color: black !important;
}

.oversummary {
background: #66AA44 !important;
background-image:url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1228005485700/config/app/images/customBackground/bg-dark.jpg) !important;
color: #FFFFFF
}

table.stats th,td.header,th.header {
background-color: #99FF66 !important;
background-image:url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1228005709566/config/app/images/customBackground/bg-light.jpg) !important;
background-repeat: repeat !important;
}


select {
color: #000000 !important; 
}

.matchintro, .drinksbreak {
background-color: #008800 !important;
background-image:url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1228005485700/config/app/images/customBackground/bg-dark.jpg) !important;
color: #FFFFFF
}

.out, .six, .milestone {
font-weight: Bold !important;
font-size:12px!important;
}

.four, .six {
color: #3333FF !important;
font-size:12px!important;
}

.sf-menu * {
font-size:12px!important;
font-weight: Bold !important;
}

.sf-menu,  .sf-menu ul li, .sf-menu a:visited, .sf-menu li, .sf-menu li.sfhover, .sf-menu li:hover, .sf-menu a, .sf-menu li li, .sf-menu li li li {
background-color: #006600 !important;
border-color: #003300 !important;
background-image:url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1228005485700/config/app/images/customBackground/bg-dark.jpg) !important;
}

.sf-menu ul {
background-color: #003300 !important;
}

.sf-menu a:hover {
background-color: #009900 !important;
background-image:url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1228005485700/config/app/images/customBackground/bg-mid.jpg) !important;
}

#banner {
background-image: url("http://sites.google.com/site/fromthepavilionuae/_/rsrc/1274537049350/slin-pics/banner_grass.jpg") !important;
}
.ui-tabs-nav a, .ui-tabs-nav a span {
background-image: url("http://sites.google.com/site/fromthepavilionuae/_/rsrc/1273416485190/slin-pics/tabs_green.png") !important;
}


#shortcut img[src='resourcenew/icon_match.gif']{
height: 0 !important;
width: 0 !important;
padding-left: 30px !important;
padding-top: 30px !important;
background: url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1273423047254/slin-pics/ballicon.gif) no-repeat  !important;
}

#shortcut img[src='resourcenew/icon_mail.gif']{
height: 0 !important;
width: 0 !important;
padding-left: 30px !important;
padding-top: 30px !important;
background: url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1273423047254/slin-pics/mailicon.gif) no-repeat  !important;
}

#shortcut img[src='resourcenew/icon_search.gif']{
height: 0 !important;
width: 0 !important;
padding-left: 30px !important;
padding-top: 30px !important;
background: url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1273423047254/slin-pics/searchicon.gif) no-repeat  !important;
}

#shortcut img[src='resourcenew/icon_transfer.gif']{
height: 0 !important;
width: 0 !important;
padding-left: 30px !important;
padding-top: 30px !important;
background: url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1273423047254/slin-pics/finicon.gif) no-repeat  !important;
}

#shortcut img[src='resourcenew/icon_bookmark.gif']{
height: 0 !important;
width: 0 !important;
padding-left: 30px !important;
padding-top: 30px !important;
background: url(http://sites.google.com/site/fromthepavilionuae/_/rsrc/1273423047254/slin-pics/bookicon.gif) no-repeat  !important;
}


}
