@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nuttenspiel.de") {
  body * { background: transparent !important; }
  #container { margin: 0 auto -60px!important; }
  #augelinks{ display:none; }
  #ohrrechts { display:none; }
  #ohrlinks { display:none; }
  #kopf { height: 100px !important; float:left; }
  #profilimgfield{ display:none; }  
  #lauftextbox{display:none;}
  #nav, #nav * { background: transparent !important; }
  #nav { margin-top:-50px !important; }
  #nav_content ul { padding-bottom: 5px !important; border-bottom: 1px solid #660000; }
  #nav_content ul li { border-bottom: 1px solid #440000; }
  #vbutton{ display:none; }
  img[src^="cgi-bin/picture.cgi"] { display:none; }
  img[src^="http://www.drogendealer.de/banner"] { display:none; }
  /* News */
  .news_hl{ display:none; }
  /* ContentboxH1 */
  .contbox_hl {display: none; }

  .bar_c { background-image: url(http://www.nuttenspiel.de/images/screen/bar_c.gif) !important; }
  .bar_b { background-image: url(http://www.nuttenspiel.de/images/screen/bar_b.gif) !important; }
  img[src^='images/gamegirls/'] { display:none; }

  #armlinkscontent { background-image: url(data:image/jpeg;base64,) !important; -moz-border-radius: 15px; border: 1px solid #AA0000; }
  #contentbox { background: transparent !important; -moz-border-radius: 15px; border: 1px solid #AA0000; margin-left: 20px!important; }
.bar_b2 {
background:transparent url(http://www.nuttenspiel.de/images/screen/bar_b2.jpg) !important;
}
.content50, .content50 * {
width: auto !important;
}
#mainstatso, #mainstatsu { width: 600px !important; height: 20px !important; float: left !important; margin-top:0 !important; }
#mainstatsu { width: 300px !important; float:none; }
.einzelstats{
float:left !important; 
}
#augerechts { margin-top: 65px!important; margin-left: auto !important; margin-right:auto !important; position:fixed;  border: 1px solid red !important; z-index:99 !important; width: 900px !important; height: 30px !important; float: right !important; -moz-border-radius: 15px; background-color: #CCCCCC !important; opacity:0.7; }
}
