css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org")
{
.ad,#donate{display:none}

#screenshots {background: #222 !important; border-radius: 10px !important;}

#left-sidebar > NAV {background: #222 !important; border-radius: 10px !important;}

HEADER {background: #222 !important; border-radius: 10px !important;}

#related {background: #222 !important; border-radius: 10px !important;}

#Head {background: #222 !important; }

#Content { background: #444 !important; border: solid 10px #444 !important; border-radius: 10px !important;}

 .Tabs.DiscussionsTabs, .Tabs.ActivityTabs, .Tabs.ConversationsTabs, .Tabs.ProfileTabs, .Tabs.HeadingTabs.DiscussionTabs, .Tabs.HeadingTabs.ConversationTabs { background: #222 !important; border-radius: 10px !important; border-bottom: none !important}

A[href="/discussions"], .MyDiscussions, A[href="/activity"], A[href="/messages/all"], A[href="/profile/activity"], A[href="/profile/notifications"], #Content > DIV > DIV:first-child + DIV > UL > LI:first-child > A, A[href="/categories/stylish"],.WriteButton, .PreviewButton, .DraftButton, #Content > DIV > DIV:first-child + DIV > UL > LI:last-child > A, A[href="/categories/style-reviews"], A[class="MyParticipated"][href="/discussions/participated"]
 {background: #222 !important; border-radius: 10px !important; border: none !important}

.Category {background: #444 !important}

.Box, ul.PanelInfo li {background: #222 !important;  border-radius: 10px !important; border: none !important}

#Head h1 a span {
   font-size: 24px;
   color:#999 !important;
   text-shadow: none !important;}

div.Tabs li a span {background: #333 !important}

#Panel > DIV > H4 {color: #999 !important}

ul.PanelInfo,.more {border-top: solid 1px #444 !important}

#Head form input.Button {color: #999 !important; text-shadow: none !important; border-radius: 0px 7px 7px 0px !important;}

#Head > DIV > DIV > FORM { border-radius: 10px !important;}

.SubTab {background: #222!important; border-radius: 6px !important}

ul.DataList div.Meta strong {background: #B9D56E !important}

a.BigButton {background: #444 !important; border: none !important; text-shadow: none !important}

.PanelInfo {background: #222 !important; border-radius: 10px !important; border-bottom: none !important}

.MessageForm.CommentForm, #MessageForm {background: #222 !important; border-radius: 10px !important; border-bottom: none !important}

.Mine {background: #444 !important; border-radius: 10px !important; border-bottom: none !important}

html, body { background: none !important; color: #ccc !important; background-color: #333 !important; }

#style-list { background-color: #222 !important; border: solid 6px #222 !important; border-radius: 10px !important }

a:link,#search-submit { color: #ccc  !important }

h1  { color: #ccc !important; background-color: #222 !important; border: solid 10px #222 !important; border-radius: 10px !important }

h2 { color: #ccc !important; background-color: #222 !important; border: solid 6px #222 !important;  border-radius: 10px !important }

#install { background-color: #222 !important; border: solid 0px #111 !important; border-radius: 10px !important}

a:hover, a:active , a:visited:hover { color:  #B9D56E  !important; background: transparent !important }

a:visited { color: #ccc !important }

#search-terms, ul#navigation a, input[type="submit"] { background-color: #333 !important; border-color: dimgray !important }

input#search-terms { background-color: black !important;color:#FFF }

pre { background-color: #444 !important; border: 1px solid darkgray !important }

#header-bar { background-color: #444 !important }

#Header { background-color: #222 !important; background-image: none !important; border: none !important }

.NoNewComments, #Discussions li.Read ul {background-color: #333 !important}

#Discussions li ul {background-color: #222 !important}

.UnblockedCategory {background-color: #777 !important}

#Form form {background-color: #555 !important; border: solid 1px #111 !important}

#Panel  {background-color: #444 !important; background-image: none !important; border: solid 4px #444 !important; border-radius: 10px 10px 10px 10px !important}

#AboutVanilla {background-color: #444 !important; border-radius: 10px !important}

BLOCKQUOTE, CITE  {background-color: #666 !important; color: #111 !important; border: solid 4px #666 !important;   border-radius: 10px !important}

LABEL, #CommentFormats {color: #999 !important;}

LI {color: #ccc !important;}

#reviews  {background-color: #222 !important; background-image: none !important; border: solid 4px #222 !important; border-radius: 10px 10px 10px 10px !important}

#Header ul li a, .TabOn {background-color: #888 !important;  background-image: none !important;border: none !important;   border-radius: 6px 6px 0px 0px !important}

#Header ul li.TabOn a, #Header ul li.TabOn a:hover {background-color: #333 !important;}

#Session {color: #ccc !important;}

#obsoletion-message {background-color: #666 !important; color: #111 !important; border: solid 1px #111 !important;   border-radius: 10px !important}

.CommentBody {background-color: #777 !important; color: #111 !important; border: solid 6px #777 !important;   border-radius: 10px !important}

#discussions {background-color: #222 !important; color: #111 !important; border: solid 6px #222 !important;   border-radius: 10px !important}

#search-options {background-color: #222 !important; color: #ccc !important; border: solid 6px #222 !important;   border-radius: 10px !important}















/* full star (3) */
#discussions .good-rating {
  list-style-image:  url("data:image/png;base64,")
    !important;
} /* */

/* empty star (1) */
#discussions .bad-rating {
  list-style-image:  url("data:image/png;base64,")
    !important;
} /* */

/* half star (2) */
#discussions .ok-rating {
  list-style-image:  url("data:image/png;base64,")
    !important;
} /* */

/* grey diamond (0) */
#discussions .no-rating {
  list-style-image:  url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAOCAYAAADwikbvAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAGXRFWHRDb21tZW50AENyZWF0ZWQgd2l0aCBHSU1QV4EOFwAAACZJREFUeJxjYBgF1APl5eX3ydYIxO9JNgBJ43dKDSDf6WRpHMYAACQOHc2U8/yZAAAAAElFTkSuQmCC")
    !important;
} /* */




/*

  style average-rating

*/

#average-rating img.good-average-rating /*[src$="/good.gif"]*/ {

  width:  0  !important;

  padding:  0 0 16px 16px  !important;

  margin:  0 0 0 4px  !important;

  background-image: url("data:image/png;base64,") 

    !important;

  background-repeat:  no-repeat  !important;

  background-position:  1px  1px  !important; 

}

#average-rating img.bad-average-rating /*[src$="/bad.gif"]*/ {

  width:  0  !important;

  padding: 0 0 16px 16px  !important;

  margin:  0 0 0 4px  !important;

  background-image: url("data:image/png;base64,") 

    !important;

  background-repeat:  no-repeat  !important;

  background-position:  1px  1px  !important; 

}

#average-rating img.ok-average-rating /*[src$="/ok.png"]*/ {

  width:  0  !important;

  padding:  0 0 16px 16px  !important;

  margin:  0 0 0 4px  !important;

  background-image: url("data:image/png;base64,") 

    !important;

  background-repeat:  no-repeat  !important;

  background-position:  1px  1px  !important;}
}
