css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("barafranca.com"),
               domain("barafranca.nl"),
               domain("barafranca.com.br"),
               domain("barafranca.com.pt"),
               domain("barafranca.fr"),
               domain("barafranca.de"),
               domain("barafranca.gen.tr"),
               domain("barafranca.no")

{

body {

   background:#BBB !important;

}

#marquee {
   background:#111 !important;
   border-top:1px #000 solid !important;
   border-bottom:1px #000 solid !important;
   color: #e13b16 !important;
}
#marquee a {
  color: #e13b16 !important;
}

  /* menu */
  th[onclick="Menu.toggle(this);"] {
    color: #e13b16 !important;
    border: none !important;
    background:#111 !important;
    height:25px !important;
  }
  
  #menu, #menu .container, #menubg {
    background: #333 !important;
  }

 .menuLink {

   background:#333 !important;
  
}

.menuLink:hover {
    color: #e13b16 !important;
   background:#444 !important;
}
  
  /* remove ugly question marks */
  img[src="/static/images/game/generic/tooltip.png"] {
    display: none;
  }
  
  /* tables */
  .tableheader, .oheader {
    background: url("http://start.lgdesigns.nl/u/upload/roodtopic.png") !important;
  }
  
  .tableheader, .tableheader *, .oheader {
    color: white !important;
    text-shadow: 0px 1px 0px #000 !important;
  }
  
  .thinline, .otable {
    background: #a8a8a8 !important;
  }
  
  /* news */

  #menubg {
    background: #333 !important;
    font-size: 20px !important;
  }
  
  #news {
    border: none !important;
    border-bottom:1px solid #333 !important;
    background:#444 !important;
    padding-left:40px !important;
  }
#newsmore {
   width:100% !important;
}
  
  #news:hover {
    background:#555 !important;
  }
  
  .story a {
    font-size: 11px !important;
    font-family: Verdana, Arial, Helvetica, sans-serif !important;
    letter-spacing: 0.25px !important;
  }
  
  .newsheader {
    color: #e13b16 !important;
    border: none !important;
    text-align:left !important;
    background:#111 !important;
    padding-left:40px !important;
  }
  
  /* top bar */
  #paneltopbar, #paneltopbar *, #topbar {
    background: #333 !important;
    border: none !important;
  }
  
  /* logo */
  #topbar img {
    margin-top: 25px !important;
  }
  
  /* ajax nav buttons */
  .smsmenu a {
    background: url("http://start.lgdesigns.nl/u/upload/roodtopic.png") !important;
    color: white !important;
    border: 1px solid black !important;
    text-transform:lowercase !important;
    padding:4px !important;
  }
  
  .smsmenu a:hover {
     color:#EEE !important;
  }
  .smsmenu a.selected {
    background:#f00 !important;
  }

  .smsmenu {
    
     margin-bottom:20px !important;
     margin-top:20px !important;

  }
  
  /* remove 'Can't read this?' text */
  a[onclick="reloadImageCode();"] {
    display: none;
  }
  
  /* profit/loss */
  
  .profit {
	  color: #006633 !important;
  }
  
  .loss {
	  color: #cc3333 !important;
  }
  
  /* remove ad */
  div img[src="./static/images/branding/remove.jpg"], #ab423f55 {
	  display: none;
  }

}
