css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-domain("torrentz.com"),url-domain("torrentz.eu"),url-domain("torrentz.me"),url-domain("209.44.113.165"),url-domain("109.235.49.39")
{

pre{color:white!important}

a:link    { color: #8fe6ff !important}
a:visited { color: #999 !important}
a:active  { color: #999 !important}
a:hover   { color: #8fe6ff !important}
span  {color: #8fe6ff !important}
.entrybody p {color: #999 !important}
.entrybody ul {color: #666 !important}
.entrymeta {color: #777 !important}
.commenttext {color: #999 !important}
.n{padding-left:15px!important}

.footer,[style="width: 1000px; margin: 0 auto;"],p.generic,[style="width: 300px; height: 270px; margin: 0 auto; float: right; text-align: right;"],div.files > p,.info,[href^="/z/1/"],[style="width: 728px; height: 90px;margin: 0px auto;"],[style="width: 728px; margin: 10px auto;"]{display:none!important}

div.comments > div.comment{background:#333!important}

div.download > dl{border-bottom:none!important;border-top: 1px solid #CCC!important}

.head{background:#666!important}

form.profile > fieldset > legend{color:#CCC!important;background:#666!important}

div.trackers > p,div.download > p,div.results > p{border-top:none!important}

fieldset > div{border-bottom:none!important}

div.trackers,div.feedback{margin: 20px 0px 20px 0px;!important}

BODY {background: #111 !important}

.top {background: #222 !important; -moz-border-radius: 10px !important;}

UL {background: #222 !important; -moz-border-radius: 10px !important; }

div.top > ul > li > a:hover {border-bottom: none !important;}

.checked, A[title="Verified Torrent Search"][href="/verified"], A[title="My Profile"][href="/profile"], A[title="Get Help"][href="/help"], div.top > ul > li > a{background: #222 !important; -moz-border-radius: 10px !important;}

.search, FIELDSET {background: #222 !important; -moz-border-radius: 10px !important;  }

HTML > BODY > FORM > FIELDSET > DIV:first-child {border-left: solid 10px #222 !important}

HTML > BODY > FORM > FIELDSET > DIV:last-child  {border-left: solid 10px #222 !important}

#thesearchbutton, #thesearchbox ,ul.autocomplete > li.selected{background: #444 !important; color: #999 !important}

#thesearchbox{height:24px!important}

H2 {background: #222 !important; -moz-border-radius: 10px 10px 0px 0px !important ;  color: #ccc !important;  border: solid 4px #222 !important}

H3 {background: #333 !important; border: solid 4px #222 !important}

.results {background: #111 !important; -moz-border-radius: 14px 14px 0px 0px !important; border: solid 2px #222 !important} 

.download, #vote-box, .trackers, .files  {background: #111 !important; -moz-border-radius: 14px 14px 0px 0px !important; border: solid 2px #222 !important} 

DL, DT, DD  {background: #333 !important;}

div.results > p > span, div.results > p > span > span {background: #222 !important;border: none !important }

P {background: #222 !important; color: #999 !important}

div.results > p > span > a {background: #333 !important; border: solid 2px #222 !important }

strong {color: #efe6ff !important}

#suggestbox, #suggestbox > ul > li {background: #444 !important; border: solid 1px #111 !important}
 
#suggestbox > ul > li > a:hover {background: #222 !important;}

div.results > div, B, LI {color: #ccc !important}

div {color: #999 !important}

div.download > dl > dt > a:hover  {background: #444 !important;}

.info {background: #444 !important; border: solid 2px #222 !important }

div.results > dl {border-bottom: solid 2px #222 !important}

HTML > BODY > DIV:first-child + FORM + SCRIPT + DIV > DL {border-bottom: solid 2px #222 !important}

.index-stats {background: #111 !important}

div.index-stats > p {  border-top: solid 2px #222 !important }

div.index-stats > dl {border-bottom: solid 2px #222 !important}

}
