css:
@-moz-document domain("dabr.co.uk"), domain("citcuit.in") {
#advert + img{display:none !important;}
.timeline + .bottom ~ p{direction:ltr; text-align:left; margin:0 !important;}
a {
    color: #2698D1 !important;
}
.menu-text a{color: white !important;}
strong {
    color: #686868
}
.menu a {
    color: white !important;
}
body {
    position: relative;
}
body > p {
    padding-right: 15px;
    padding-left: 15px;
}
input[value="Save"] {
    width: 100px;
    height: 40px;
}
span.status {
    font-family: vazir, tahoma !important;
}
.date {
    clear: both;
}
table + p {
    text-align: right !important;
    padding-right: 20px;
}
/* removed */

div.menu-bottom + img,
.advert {
    display: none
}
div.menu-bottom + img + p {
    direction: ltr;
    font-size: 10px;
    color: green;
    position: absolute;
    left: 10px;
    bottom: 30px;
    display: none;
}
span label#lblGeo {
    font-size: .9em;
    border: 2px dashed green;
    padding: 3px;
}
label#lblGeo {
    display: none
}
.about a,
.features a {
    color: #0069C7!important;
}
.profile .avatar a img,
.profile .avatar {
    width: 60px !important;
    height: 60px !important;
}
.profile .avatar {
    margin: 10px !important;
}
.timeline a {
    color: #0069C7!important;
    text-decoration: none;
}
.timeline,
p {
    direction: rtl;
    text-align: right;
    font-family: vazir, tahoma;
}
.tweet {
    padding-right: 25px !important;
}
.avatar,
.avatar img {
    width: 38px !important;
    height: 38px !important;
}
.avatar {
    margin-left: 10px !important;
}
.status b a {
    color: #2698D1 !important;
}
.status [target="_blank"]:hover,
.tweet-url {
    color: #648013 !important;
    background-color: #DBF0FF !important;
}
.status small {
    color: #1FA184!important
}
.status small a {
    color: #19D128!important
}
a[accesskey="9"] {
    color: #0069C7 !important;
    font-weight: bold;
    border: 1px solid #88DBE3;
    padding: 2px;
    background-color: #dfd;
}
.timeline + p {
    text-align: right;
}
table {
    width: 100%
}
a {
    text-decoration: none !important
}
.even {
    background: #f6ffd7!important;
}
.odd,
.even {
    border-bottom: 1px dashed #CFCFCF;
}
.odd{
background-color: #F5FFE7 !important;
}
body {
    background: #F9FFF0 !important;
}
.menu {
    background: #0095aa!important;
    border-bottom: 2px solid black;
    border-top: 7px solid #0a4306;
    color: white !important;
}
#status,
form[action="twitpic"] textarea,
form[action="update"] {
    direction: rtl;
    text-align: right;
}
form {
    display: block
}
textarea#status,
form[action="twitpic"] textarea,
form[action="update"] textarea {
    float: right;
    width: 100% !important;
    height: 65px;
    font: 12px tahoma, arial;
    box-sizing: border-box;
}
form[action="twitpic"],
form[action="update"] {
    text-align: right;
    position: relative;
}
.avatar {
    float: right!important;
    position: relative !important
}
form div input {
    float: right;
    width: 100px;
    height: 65px;
}
span#remaining {
    float: right;
    color: #00454f;
    -moz-box-shadow: 0 0 5px #aaa;
    box-shadow: 0 0 5px #aaa;
    width: 67px;
    height: 52px;
    line-height: 52px;
    text-align: center;
    font-size: 1.7em;
    margin: 3px 30px 0;
}
#geo {
    float: right;
    position: relative;
    right: 110px;
    top: 0px;
}
form[action="twitpic"] #remaining {
    position: absolute;
    top: 35px;
    right: 500px;
}
form[action="update"] #remaining {
    position: absolute;
    top: 25px;
    right: 500px;
}
form[action="twitpic"] input[type="submit"] {
    width: 100px;
    height: 70px;
    float: right;
    position: relative;
    top: -17px;
}
*::-moz-selection {
    background-color: #090;
    color: #FFFFFF;
}
/*other pages*/

.followers .avatar {
    float: left !important;
    margin: 0 20px 0 0 !important;
}
.followers .status a {
    color: #2698D1 !important;
    font-weight: bold;
}
form[action="search"] {
    width: 380px;
    height: 80px;
    margin: 0 auto !important;
    line-height: 5em;
}
form[action="search"] * {
    line-height: 2em;
    vertical-align: middle;
    height: 30px;
    font-family: tahoma, arial;
    direction: rtl;
}
.menu,
.menu-top + p,
.menu-top + p + div,
body div + div div {
    padding-left: 10px !important;
    padding-right: 10px !important;
}
.menu-top + p {
    margin-left: auto;
    margin-right: auto;
    width: 400px;
    direction: ltr;
}
.menu-top + p + div {
    padding-bottom: 20px;
}
.menu-top + p + div > span {
    margin-right: 5px;
}
form[action="settings/save"] + hr + p,
form[action="settings/save"] {
    padding-left: 20px;
}
form[action="settings/save"] *,
form[action="settings/save"] + hr + p {
    direction: ltr !important;
    text-align: left;
}
#about {
    background-color: #F6FFD7 !important;
    border: 3px dashed gray;
}
#geo input {
    width: 14px;
}
}
