@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {

/** Common stuff **/
.dK, .ez, .eJ, .Ar  {
  -moz-border-radius:5px;
  -webkit-border-radius:5px;
  border-radius:5px;
  -moz-box-shadow:0 1px 2px rgba(0, 0, 0, 0.3);
  -webkit-box-shadow:0 1px 2px rgba(0, 0, 0, 0.3);
  box-shadow:0 1px 2px rgba(0, 0, 0, 0.3);
  border:1px solid white !important;
}

/** Send, Save & Discard buttons **/
div.dW { padding: 8px!important; }


/** To & Subject boxes **/
.eC .dK.nr, .eC .ez.nr { margin: 1px !important; }
.eC .dK.nr:focus, .eC .ez.nr:focus { border:1px solid white !important; }

/** Attach & Add Invitation **/
table.eA > tbody > tr:nth-child(7) .eF { padding-top: 8px !important }
/* attach file */
img.en { display:none }
table.eA > tbody > tr:nth-child(7) .eF .el,
.eB .el {
  background: url(data:image/png;base64,) no-repeat center left !important;
  padding: 3px 0 6px 22px;
}
.dL { padding-bottom: 5px !important }
/* event invitation */
table.eA > tbody > tr:nth-child(7) .eF .el.ou {
  background: url(data:image/png;base64,) no-repeat center left !important;
  padding: 3px 0 6px 26px;
}


/** Richtext Toolbar **/
.eJ {
  display:block;
  margin:2px 6px !important;
  width:auto;
  padding: 1px 0 !important;
  background-color: white !important;
}
.eL {
  border: none !important;
  width: 24px !important;
  height: 23px !important;
}
.e9:not(.eQ):not(.fd), /* hover */
.e7 { /* active */
  margin-bottom: -2px !important;
}

.fw .eN { /* bold */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .e3 { /* italic */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .fu { /* underline */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .eX { /* font */
  background: url(data:image/png;base64,) no-repeat 0 -1px !important;
}
.fw .eY { /* font-size */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .eS { /* text-color */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .e0 { /* highlight */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .U9 { /* web search */
  background: url(data:image/png;base64,) no-repeat 2px 1px !important;
}
.fw .QT { /* smiley */
  background: url(data:image/png;base64,) no-repeat 0 -1px !important;
}
.fw .Vg { /* image */
  background: url(data:image/png;base64,) no-repeat 0 -1px !important;
}
.fw .e5 { /* hyperlink */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .e6 { /* numbered-list */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .eO { /* bullet-list */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .e8 { /* indent */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .e2 { /* unindent */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .fa { /* quote */
  background: url(data:image/png;base64,) no-repeat 0 -1px !important;
}
.fw .e4 { /* align-left */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .eP { /* align-center */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .fc { /* align-right */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fw .fb { /* remove-formatting */
  background: url(data:image/png;base64,) no-repeat center !important;
}
.fd, .eQ { /* transliteration button */
  width: 18px !important;
  height: 18px !important;
  padding-bottom: 2px !important;
}
.eo { top: -7px !important; }

/** Spellcheck **/
.fA { padding-top: 8px !important; }
/* Button effect */
.fA > span, .fA > .eu:last-child {
  border: 1px solid #BBBBBB !important;
  border-left-color: #FFFFFF !important;
  background: transparent url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAoCAAAAAAa3c+rAAAAAXNSR0IArs4c6QAAABJJREFUCNdj+MnEQBT8CsUEIACgKQMyxVIz6AAAAABJRU5ErkJggg==) repeat-x left center !important;
  color: #000000 !important;
  text-decoration: none !important;
}
.fA > span:first-child, .fA > .eu:last-child {
  -moz-border-radius-topleft: 4px !important;
  -moz-border-radius-bottomleft: 4px !important;
  -webkit-border-top-left-radius: 3px !important;
  -webkit-border-bottom-left-radius: 3px !important;
  border-top-left-radius: 3px !important;
  border-bottom-left-radius: 3px !important;
  border-left-color: #BBBBBB !important;
  padding: 5px!important;
}
.fA > .wX, .fA > .eu:last-child {
  -moz-border-radius-topright: 3px !important;
  -moz-border-radius-bottomright: 3px !important;
  -webkit-border-top-right-radius: 3px !important;
  -webkit-border-bottom-right-radius: 3px !important;
  border-top-right-radius: 3px !important;
  border-bottom-right-radius: 3px !important;
  padding: 5px 3px!important;
}
/* Hover */
.fA > span:hover, .fA > .eu:last-child:hover {
  border-top-color: #939393 !important;
  border-bottom-color: #939393 !important;
}
.fA > span:first-child:hover, .fA > .eu:last-child:hover {
  border-left-color: #939393 !important;
}
.fA > .wX:hover, .fA > .eu:last-child:hover {
  border-right-color: #939393 !important;
}
.fA > .eu:last-child {
 margin-left: 2px !important;
}
.fA > span:first-child {
  background: url(data:image/png;base64,) no-repeat 4px -1px !important;
  padding-left: 30px !important;
}

/** Compose mail body **/
.Ar {
  display:block;
  margin:4px 2px !important;
  padding: 0 !important;
  -moz-box-shadow:0 2px 2px rgba(0, 0, 0, 0.3);
  -webkit-box-shadow:0 2px 2px rgba(0, 0, 0, 0.3);
  box-shadow:0 2px 2px rgba(0, 0, 0, 0.3);
}

/* remove the ugly border raduis in the inner box */
.dW {
  -moz-border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
  border-radius: 0 !important;
}

}
