css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.ppmsite.com/forum/"), url-prefix("http://ppmsite.com/forum/") {

html, body, div, span,
applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, font, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
dd, dl, dt, li, ol, ul,
select, option, fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td {
 margin: 0;
 padding: 0;
 border: 0;
 font-weight: inherit;
 font-style: inherit;
 font-size: 100%;
 line-height: 125%;
 font-family: inherit;
}
input, textarea, select, option {
 margin: 0;
 padding: 0;
 font-weight: inherit;
 font-style: inherit;
 font-size: 100%;
 line-height: 125%;
 font-family: inherit;
}

a img, :link img, :visited img {
 border: 0;
}

body {
 font: medium sans-serif;
 background: #245;
 color: #fff;
 overflow-y: scroll;
}

input[type="text"],
input[type="password"],
textarea,
select {
 border: 2px inset #bcd;
 border-color: #345 #bcd #bcd #345;
 background: #245;
 color: #fff;
}


a, a:link, a:visited, a:hover {
 color: #bcd !important;
}

table {
 border-spacing: 0 !important;
 background: #bcd;
}

tr {
}

th {
 visibility: hidden;
 line-height: 0 !important;
 height: 0 !important;
 margin: 0 !important;
 padding: 0 !important;
}

td {
 background: #245;
 margin: 0;
 padding: 0;
 vertical-align: middle;
}

.bodyline {
 width: 100% !important;
 padding: 0 !important;
 margin: 0 !important;
}

.forumline {
 border-spacing: 0 1px !important;
}

.catSides {
 display: none;
}

.row1 > img[alt$="New posts"],
.row1 > img[alt$="No new posts"],
.row1 > img[alt$="Link"] {
 width: 20px;
}
.row1 > img[alt$="New posts"],
.row1 > img[alt$="No new posts"] {
 width: 15pt ;
}

.gensmall > img[alt="New posts"],
.gensmall > img[alt="No new posts"] {
 display: none ;
}

.gensmall > img[alt="New posts"] + a {
 font-weight: bold ;
 padding: 0 5pt ;
}

a[title="View newest post"] ~ a {
 font-weight: bold;
}

.row1 > img[title*="Announcement"] {
 background: #ccf;
}

a.forumlink {
 font-weight: bold;
}

/*
td.row1 + td.row1 + .row2,
td.row1 + td.row1 + .row2 + .row2 {
 background: #246 ;
 color: #fff ;
}
*/

.genmed {
}

.gensmall {
 font-size: small;
}

.forumlink + .genmed {
 display: none;
}

.maintitle {
 font-size: larger ;
}

.maintitle + .gen {
 display: none ;
}

.mainmenu img {
 display: none ;
}

.maintitle + .gen + table {
 width: 600px;
 text-align: center;
}

.mainmenu a {
 font-size: small;
 padding: 0;
}

a[href^="search.php?"] + br + a[href^="search.php?"]:before {
 content: " | " ;
}

a[href^="search.php?"] + br {
 display: none ;
}

.bodyline > table[align="center"] > tbody > tr > td:first-child[valign="bottom"] > .gensmall {
 display: none ;
}

div[align="center"],
div[align="center"] + br ~ a {
 display: none ;
}

.nav {
 font-weight: bold;
}

a.gen > span[style^="color: rgb(255"],
a[style^="color: rgb(255"],
a[style^="color: rgb(255"] > b {
 color: #bcd !important;
 font-weight: bold;
}

a.gen > span[style^="color: rgb(0"],
a[style^="color: rgb(0"],
a[style^="color: rgb(0"] > b {
 color: #bcd !important;
 font-weight: normal !important;
 font-style: italic;
}

a[href="memberlist.php"] > b {
 font-weight: normal;
}

br.nav + table.forumline + br.nav + table:not([class="forumline"]) {
 display: none;
}

table[width="90%"] {
 margin: 5pt auto;
}
table[width="90%"] td {
 border: 0 !important;
}

table[width="90%"] td.quote {
 border: 1px dotted #ccf !important;
}

table[width="90%"] td.code {
 font-family: monospace;
 border: 1px dotted #ccf !important;
}

.topictitle + .gensmall {
 display: block;
}

.topictitle + .gensmall > br {
 display: none;
}

.topictitle + .gensmall > img {
 margin-left: 100px;
}

img[title="Back to top"] {
 display: none;
}

}

@-moz-document url-prefix("http://www.ppmsite.com/forum/viewtopic.php"), url-prefix("http://ppmsite.com/forum/viewtopic.php") {
input {
 font-size: smaller !important;
}
.catBottom {
 display: none;
}

.spaceRow {
 display: none;
}

.row1 + .row1 > table > tbody > tr > td[colspan="2"],
.row2 + .row2 > table > tbody > tr > td[colspan="2"] {
 border-top: 0 !important;
}

textarea {
 width: 600px;
 height: 200px;
}

td:first-child + td[valign="top"][nowrap="nowrap"][align="right"] > span.gensmall {
 display: none;
}

a[href^="posting.php?mode=newtopic"] {
 display: none;
}

a[href$="view=previous"],
a[href$="view=next"],
a[href*="watch=topic"] {
 display: none;
}

.postdetails + .postbody + br + span.gensmall {
 display: none;
}

.postdetails {
 font-size: x-small;
}

img {
 max-width: 900px;
 overflow: auto;
}

.name + br + .postdetails {
 display: none;
}

div.postdetails + div.postbody {
 display: none;
}

.catlight {
 display: none;
}

}
