css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mono-logue.air-nifty.com") {

/* Non-display part */
.footer,
#main-top,
#main-bottom,
#right,
iframe,
.entry-trackback,
.entry-comment
{ display: none !important; }

/* Display part */
body,
#container,
#pagebody,
#pagebody-inner,
#main,
#center,
.content-top,
.content-bottom,
.content,
.entry-top,
.entry-bottom,
.entry,
.entry-body-top,
.entry-body-bottmo,
.entry-body,
.entry-body-text,
.entry-more,
.entry-more-text
{
    width: 100% !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
    line-height: 140% !important;
}

p
{
    font-size: x-large !important;
}    

#banner
{
    width: 100% !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}

}
