@-moz-document domain("www.google.com") {

div#resultStats {color:#07B7BA;}

/*[[tahomafont]]*/
li.g .vsc {border-right:7px dashed rgb(232,237,255); 
background-image: -moz-linear-gradient(
    left center,
    rgb(247,247,255) 49%,
    rgb(241,240,255) 57%,
    rgb(232,237,255) 97%
);
