css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("fromthepavilion.org") {

#banner {
	height: 63px !important;
	width: 960px !important;
        	background: url(http://i970.photobucket.com/albums/ae187/nipsadinio/BANNER2.jpg) no-repeat !important;

}


#content{

font-size:11px!important;

line-height:1.4!important;

color: #111!important;

}

#leftmenu {

background: #84A6B3 !important;

width: 110px !important;

}


div#leftmenu {

border: 1px solid #0000A0 !important;

width: 110px !important;

margin-left: 3px !important;
 
}

table.display td {

border-bottom: 1px dashed #659EC7 !important;

}

#content a{

color: #15317E!important;

}



#content h1, #content h1 a{

color: #000066 !important;

}



.box, .smallbox {

background: #A0CAD9 !important;

border: 1px solid #15317E !important;

}



#content, #wrapper, .ui-tabs-panel, .ui-tabs-nav, .whiteback {


background: #BDEDFF !important;

}



.oversummary {

background: #82CAFF !important;

}


.newsdate {

background: #DDFFAA !important;

border: 1px solid #008800 !important;

}
select {

color: #000000 !important; 

}


h2 {

color: black !important;

}



table.stats, table.commentary, table.cup, .verywidesmallbox {

background: #A0CAD9 !important;

border: 2px solid #84A6B3 !important;

}



table.stats th {

border-bottom: 1px solid #153E7E !important;

background-color: #3BB9FF !important;

}



table.stats td {

border-bottom: 1px solid #CFECEC !important;

}



td.header,th.header {

background-color: 	#CFECEC !important;

}

.matchintro, .drinksbreak {

background-color: #CFECEC !important;

color: 	#151B54

}

h1.header {

background-color: #CFECEC !important;

border-color: #153E7E !important;

}


.newsdate {
background: none repeat scroll 0 0 #659EC7 !important;
border: 1px solid #153E7E !important;
}

table.stats th{
background: none repeat scroll 0 0 #84A6B3 !important;
border-bottom: 1px solid #84A6B3 !important;
}
.sf-menu a:hover {

background-color: #84A6B3 !important;

}

.sf-menu,  .sf-menu ul li, .sf-menu a:visited, .sf-menu li, .sf-menu li.sfhover, .sf-menu li:hover, .sf-menu a, .sf-menu li li, .sf-menu li li li {

background-color: #A0CAD9 !important;
border-color: #84A6B3 !important;
font-size:11px!important;

}
.out, .six, .milestone {

font-weight: Bold !important;

font-size:11px!important;

}


.four, .six {

font-weight: Bold !important;

color: #0714d4 !important;

font-size:11px!important;

}

}
