css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("themixingbowl.org"), domain("tmb.dj") {
body {
background:none repeat scroll 0 0 #FED9BF !important;
color:#444444 !important;
font-family:Verdana,Arial,Helvetica,sans-serif;
font-size:0.7em;
}
#logo  {
background-image:url("http://dl.dropbox.com/u/30897/TMBlogo-red1.png") !important;
}
/*nav bar, top torrents, radio, user stats*/
	#header .navsite .shadow3 {
	background:none repeat scroll 0 0 #BA835E !important;
	}
	#header .navuser .shadow3 {
	background-color:#BA835E !important;
	}
	#header .nav li ul li { /*menu items*/
	background-color:#FED9BF !important;
	}
	#user_tt  {
	background:none repeat scroll 0 0 #FEB380 !important;
	}
	#user_tt div {
	border-color:#BA835E !important;
	}
	#header .userinfo .shadow3 {
	background-color:#FEB380 !important;
	}
/*news*/
	#news div.body {
	background:none repeat scroll 0 0 #FEB380 !important;
	}
	#news span {
	color:#666666 !important;
	}
.const .shadow3 { /*torrent search box, forum search box, bottom users/stats box*/
background:none repeat scroll 0 0 #FF6600 !important;
}
#taggrid .grid tr{
background-color:#FCDCBF !important;
}
#taggrid .grid tr.alt{
background-color:#F2CBAD !important;
}
.grid-pager span { /*current page*/
background-color:#FFFFFF;
color:#333333;
}
.grid-pager a { /*other pages*/
background-color:#E0B79C !important;
color:#666666;
}
/*torrent,forum grid*/
.torrenttable tr{
background-color:#FCDCBF !important;
}
.torrenttable tr.alt{
background-color:#F2CBAD !important;
}
.grid th {
background-color:#B24700 !important;
}
.grid td {
border-color:#F3EDE6 !important;
}
.grid td.subject { /*forum*/
background-color:#FED9BF !important;
}
.grid td.topics { /*forumlist replies*/
background-color:#DEA57F !important;
}
.grid td.author { /*forum*/
background-color:#FEB380 !important;
}
.grid td.views { /*forum*/
background-color:#DEA57F !important;
}
.grid td.group { /*forumlist*/
background-color:#BA835E !important;
}
.grid td.posts { /*forumlist*/
background-color:#FED9BF !important;
}
.grid td.lastpost {
background-color:#FEB380 !important;
}
.grid tr.snatchedandpostedalt td{
background-color:#DAF8DA !important;
}
.grid tr.snatched td{
background-color:#FFE5E5 !important;
}
.forumviewtable tr.snatched td.topics, .forumviewtable tr.snatched td.views {
background-color:#F8DADA !important;
}
.forumviewtable tr.snatched td.author, .forumviewtable tr.snatched td.lastpost, .forumviewtable tr.snatched td.delete {
background-color:#F4C3C3 !important;
}
.grid  {
background:none repeat scroll 0 0 #F5F8FB;
border:1px solid #B24700 !important;
}
.forumviewtable td{
border-bottom:1px solid #FF6600;
}
.threadviewgrid td {
background:none repeat scroll 0 0 #F8F8F8;
border-bottom:5px solid #FF6600 !important;
}
.threadviewgrid tr.bottom td {
border-bottom:0 none !important;
}
/*actual post*/
	.grid td.left {
	background:none repeat scroll 0 0 #F8F3EB !important;
	border-bottom:1px solid #FEFCDF;
	}
	.threadviewgrid .postinfo {
	background-color:#FEB380 !important;
	border-bottom:1px solid #E0E0E0;
	color:black;
	}
	.grid td.right {
	background-color:#F8F3EB !important;
	border-bottom:1px solid #E0E0E0;
	color:black;
	}
	q  {
	background-color:#F2EBDF !important;
	border:1px solid #BBAAAA !important;
	}
	
#quickreply textarea { /*some space for the new border*/
width:98% !important;
}
.const ul.post { /*dunno*/
background-color:#F2EBDF !important;
border:1px solid #AAAAAA;
}
ul.profilenav { /*profile page*/
background:none repeat scroll 0 0 #FCDCBF !important;
border:1px solid #AAAAAA;
}
ul.prefs  { /*preferences page*/
background:none repeat scroll 0 0 #F8F3EB !important;
border:0.1em solid black;
}
/*text colors*/
.const ul li label {/*form field labels, eg forum search*/
color:#7C3200  !important;
}
#header .nav li ul li a {
color:#7C3200  !important;
}
#header .nav a {
color:#FFFFFF  !important;
}
span.tag a {
color:#888888  !important;
}
.grid a.grid-ilink:hover{
color:#BB0000  !important;
}
a {
color:#7C3200  !important;
text-decoration:none;
}
a:hover {
color:#0000CC !important;
text-decoration:none;
}
span.artist a {
color:#EE5500;
}
h1 {
border-bottom:1px solid #666666;
color:#003366 !important;
font-size:1.8em;
}
/*wiki*/
#p-navigation, #p-search, #p-tb  {
background:none repeat scroll 0 0 #F2EBDF !important;
}
#p-logo a,#p-logo a:hover{
background-image: url("http://dl.dropbox.com/u/30897/TMBlogo-red1.png") !important;
}

/*not sure need to change these*/
h2 {
color:#003366;
font-size:1.6em;
}
#headeruserinfo {
color:black;
font-size:0.9em;
}
#navbar, #navbar ul {
background:none repeat scroll 0 0 #0066CC;
border:1px solid white;
}
#navbar a {
color:white;
}
#navbar a:hover {
color:black;
}

}
