css:
@-moz-document domain(kingdomofloathing.com), domain(127.0.0.1)
{
/* HP only */
select[name*="item"] option[value="1274"], select[name*="item"] option[value="595"], 
select[name*="item"] option[value="2635"], select[name*="item"] option[value="464"], 
select[name*="item"] option[value="2402"], select[name*="item"] option[value="2369"], 
select[name*="item"] option[value="1051"], select[name*="item"] option[value="1050"], 
select[name*="item"] option[value="1049"], select[name*="item"] option[value="1048"], 
select[name*="item"] option[value="234"], select[name*="item"] option[value="474"], 
select[name*="item"] option[value="233"], select[name*="item"] option[value="232"], 
select[name*="item"] option[value="869"], select[name*="item"] option[value="231"], 
select[name*="item"] option[value="912"], select[name*="item"] option[value="2438"]  
{color: #800 !important; background: #FFFAFA !important; border-bottom: 1px dotted #a00 !important;}

select[name*="item"] option[value="1274"]:after, select[name*="item"] option[value="595"]:after, 
select[name*="item"] option[value="2438"]:after 
{content: " (All HP)" !important; font-weight: bold !important;}
select[name*="item"] option[value="2635"]:after, select[name*="item"] option[value="464"]:after 
{content: " (100 HP)" !important}
select[name*="item"] option[value="2402"]:after, select[name*="item"] option[value="2369"]:after 
{content: " (80 HP)" !important}
select[name*="item"] option[value="1051"]:after {content: " (35 HP)" !important}
select[name*="item"] option[value="1050"]:after, select[name*="item"] option[value="1049"]:after, 
select[name*="item"] option[value="1048"]:after {content: " (30 HP)" !important}
select[name*="item"] option[value="234"]:after {content: " (18 HP)" !important}
select[name*="item"] option[value="474"]:after {content: " (15 HP)" !important}
select[name*="item"] option[value="233"]:after {content: " (13 HP)" !important}
select[name*="item"] option[value="232"]:after {content: " (8 HP)" !important}
select[name*="item"] option[value="869"]:after {content: " (5 HP)" !important}
select[name*="item"] option[value="231"]:after {content: " (3 HP)" !important}
select[name*="item"] option[value="912"]:after {content: " (4 HP)" !important}

/* MP only */
select[name*="item"] option[value="2437"], select[name*="item"] option[value="2370"], 
select[name*="item"] option[value="2389"], select[name*="item"] option[value="2367"], 
select[name*="item"] option[value="2600"], select[name*="item"] option[value="1788"], 
select[name*="item"] option[value="1965"], select[name*="item"] option[value="1055"], 
select[name*="item"] option[value="1559"], select[name*="item"] option[value="2035"], 
select[name*="item"] option[value="1054"], select[name*="item"] option[value="1053"], 
select[name*="item"] option[value="1052"], select[name*="item"] option[value="465"], 
select[name*="item"] option[value="345"], select[name*="item"] option[value="882"], 
select[name*="item"] option[value="1334"], select[name*="item"] option[value="347"], 
select[name*="item"] option[value="180"], select[name*="item"] option[value="344"], 
select[name*="item"] option[value="1658"], select[name*="item"] option[value="1659"], 
select[name*="item"] option[value="1660"], select[name*="item"] option[value="357"], 
select[name*="item"] option[value="1003"], select[name*="item"] option[value="909"],
select[name*="item"] option[value="2378"], select[name*="item"] option[value="518"], select[name*="item"] option[value="2639"]
{color: #008 !important; background: #FAFAFF !important; border-bottom: 1px dotted #00a !important;}


select[name*="item"] option[value="2437"]:after {content: " (140 MP)" !important}
select[name*="item"] option[value="2370"]:after {content: " (82 MP)" !important}
select[name*="item"] option[value="2389"]:after, select[name*="item"] option[value="2367"]:after {content: " (70 MP)" !important}
select[name*="item"] option[value="2600"]:after {content: " (60 MP)" !important}
select[name*="item"] option[value="1788"]:after {content: " (50 MP)" !important}
select[name*="item"] option[value="1965"]:after {content: " (45 MP)" !important}
select[name*="item"] option[value="2378"]:after {content: " (40 MP)" !important}
select[name*="item"] option[value="1055"]:after {content: " (35 MP)" !important}
select[name*="item"] option[value="1559"]:after, select[name*="item"] option[value="2035"]:after, 
select[name*="item"] option[value="1054"]:after, select[name*="item"] option[value="1053"]:after, 
select[name*="item"] option[value="1052"]:after {content: " (30 MP)" !important}
select[name*="item"] option[value="465"]:after {content: " (55 MP)" !important}
select[name*="item"] option[value="345"]:after {content: " (25 MP)" !important}
select[name*="item"] option[value="882"]:after {content: " (20 MP)" !important}
select[name*="item"] option[value="1334"]:after, select[name*="item"] option[value="347"]:after 
{content: " (10 MP)" !important}
select[name*="item"] option[value="180"]:after,
select[name*="item"] option[value="2639"]:after {content: " (9 MP)" !important}
select[name*="item"] option[value="344"]:after {content: " (8 MP)" !important}
select[name*="item"] option[value="1658"]:after, select[name*="item"] option[value="1659"]:after, 
select[name*="item"] option[value="1660"]:after {content: " (7 MP)" !important}
select[name*="item"] option[value="357"]:after {content: " (6 MP)" !important}
select[name*="item"] option[value="1003"]:after {content: " (3 MP)" !important}
select[name*="item"] option[value="909"]:after {content: " (2 MP)" !important}
select[name*="item"] option[value="518"]:after {content: " ((1.5 * level) + (4 to 6))" !important}

/* Both */
select[name*="item"] option[value="1950"], select[name*="item"] option[value="2616"], 
select[name*="item"] option[value="593"], select[name*="item"] option[value="2576"], 
select[name*="item"] option[value="592"], select[name*="item"] option[value="466"], 
select[name*="item"] option[value="883"], select[name*="item"] option[value="557"],
select[name*="item"] option[value="2606"]
 {color: #080 !important; background: #FAFFFA !important; border-bottom: 1px dotted #0a0 !important;}

select[name*="item"] option[value="1950"]:after {content: " (100 HP / 100 MP)" !important}
select[name*="item"] option[value="2616"]:after {content: " (50 HP / 50 MP)" !important}
select[name*="item"] option[value="593"]:after {content: " (46 HP / 46 MP)" !important}
select[name*="item"] option[value="2606"]:after {content: " (30 HP / 30 MP)" !important}
select[name*="item"] option[value="2576"]:after {content: " (34 HP / 34 MP)" !important}
select[name*="item"] option[value="592"]:after {content: " (20 HP / 20 MP)" !important}
select[name*="item"] option[value="466"]:after {content: " (40 HP / 31 MP)" !important}
select[name*="item"] option[value="883"]:after {content: " (20 HP / 20 MP)" !important}
select[name*="item"] option[value="557"]:after {content: " (4 HP / 4 MP)" !important}
select[name*="item"] option {padding-right: 1em !important;}
select[name*="item"] {max-width: 15em !important; overflow: hidden !important;}

/* Non-HP/MP Restorers */
select[name*="item"] option[value="588"]:after {content: " (Remove Effects)" !important}
}
