css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.archlinux.org") {

body {
  background-color: #e4eeff !important;
}

#archnavbar ul#archnavbarlist li a { color: #999; font-weight: bold !important; text-decoration: none !important; }
#archnavbar ul li a:visited { color: #999 !important; text-decoration: none !important; }
#archnavbar ul li a:hover { color: white !important; text-decoration: underline !important; }

a:visited { color: #800080 !important; }
a:hover { text-decoration: underline; color: #008000 !important; }

div.box { background: #f6f9fc !important;}

/* highlight current website in the navbar */
#archnavbar.anb-home ul li#anb-home a { color: white !important; }
#archnavbar.anb-packages ul li#anb-packages a { color: white !important; }
#archnavbar.anb-download ul li#anb-download a { color: white !important; }

}

@-moz-document domain("aur.archlinux.org") {

body {
  background-color: #e4eeff !important;
}

#archnavbar ul#archnavbarlist li a { color: #999; font-weight: bold !important; text-decoration: none !important; }
#archnavbar ul li a:visited { color: #999 !important; text-decoration: none !important; }
#archnavbar ul li a:hover { color: white !important; text-decoration: underline !important; }

a:visited { color: #800080 !important; }
a:hover { text-decoration: underline; color: #008000 !important; }

div.box { background: #f6f9fc !important;}

/* highlight current website in the navbar */
#archnavbar.anb-aur ul li#anb-aur a { color: white !important; }

}

@-moz-document domain("bbs.archlinux.org") {

body {
  background-color: #ffffff !important;
}

#archnavbar ul#archnavbarlist li a { color: #999; font-weight: bold !important; text-decoration: none !important; }
#archnavbar ul li a:visited { color: #999 !important; text-decoration: none !important; }
#archnavbar ul li a:hover { color: white !important; text-decoration: underline !important; }

.pun a:visited { color:#800080 !important; }
.pun a:hover, #brdmain a:hover { color:#008000 !important; }
.pun a:active, #brdmain a:active { color: #e90 !important; }

/* highlight current website in the navbar */
#archnavbar.anb-forum ul li.anb-selected a { color: #fff !important; }

div.box div.inbox div.postleft,
div.box div.inbox div.postright,
div.box div.inbox div.postfootleft,
div.box div.inbox div.postfootright,
div.blockpost div.box div.inbox {
background: #e4eeff !important;
}

.pun .blockpost .postfootright li {
	color: #e4eeff !important;
}

.pun .blockpost .postbody, .pun .blockpost .postfoot {
	background: #e4eeff !important;
}

h2,
div.box thead tr th {
border: none !important;
background: #cddeee !important;
} 

td.tc2,
td.tc3,
td.tcmod,
#postpreview,
#viewprofile dd,
div.forminfo,
div.blockmenu div.box,
#adstats dd {
  background-color: #ffffff !important;
}

div.box div.inbox div.postright {
border-color: #e4eeff !important;
}

blockquote {
background: #ffffff !important;
}

div.box tbody tr td {
background: #e4eeff !important;
}

div.icon {
border-color: #cddeee #cddeee #cddeee #cddeee !important;
margin: 4px !important;
}

tr.inew div.icon {
border-color: #007abb #007abb #007abb #007abb !important;
margin: 4px !important;
}

div.box div.inbox tbody tr td.tc2,
div.box div.inbox tbody tr td.tc3 {
background-color: #cddeee !important;
}

div#brdstats div.box div.inbox,
div#brdfooter div.box div.inbox,
div.blockform div.box {
background: #cddeee !important;
}

/* To correct visibility of focus lines */
.pun .blocktable .tclcon div {
	overflow: visible !important;
	padding: 2px 0 2px 0 !important;
}

}

@-moz-document domain("projects.archlinux.org") {

body {
  background-color: #e4eeff !important;
}

}
