css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filmweb.pl") {

.bodyBackground {
background-image: none !important;
background: #FFFF99 !important;
}

#header, #footer, .BlStripe, .mainMenuMaskRight {
background: #CC6600 !important;
}

.icoFooter {
height: 32px !important;
}

.icoFooter.icoF-1 {
background-position: 0 -4339px !important;
}

.icoFooter.icoF-2 {
background-position: 0 -4401px !important;
}

.icoFooter.icoF-3 {
background-position: 0 -4463px !important;
}

.icoFooter.icoF-4 {
background-position: 0 -4525px !important;
}

.mainCol {
background-color: #FFCC66 !important;
-moz-border-radius: 7px 7px 0px 0px !important;
}

.postContentAndInfo {
background-color: #FFFFFF !important;
-moz-border-radius: 0px 0px 7px 7px !important;
border-left: none !important;
padding: 0px !important;
}

.topicPrev .postContentAndInfo p {
margin: 5px !important;
}

.postInfo {
-moz-border-radius: 0px 0px 7px 7px !important;
}

.topicPostItem,.topicReply {
background:transparent url(https://bzrhug.bay.livefilestore.com/y1p0GHissSHS55DjTpa8FV9OeN1SIzVMLUO1YonUxwtqAAuU4l3lXkEt2Ta1-27Leyv5qPmLXaVwH4FFDMjYF5TrSWdZLH4YIBq/lp.png) no-repeat 0 20px !important;
}

.mainMenuMaskLeftWhite, .mainMenuMaskLeftRight, .mainMenuMaskRight {
background-color: #CC6600 !important;
}

.nest {
border-left: 1px solid #000000 !important;
}

#messagesList, .mask, .userLastReplies {
background: #FFCC66 !important;
}

.topicPostItem .post {
background: none repeat scroll 0 0 #999999 !important;
border: 1px solid #454a50 !important;
-moz-border-radius: 0px 0px 7px 7px !important;
padding: 0px !important;
}

.topicFirstPostContent {
background-color: #777777 !important;
}

.mainCol .cast {
background: none !important;
}

.main-menu ul li a, .main-menu ul li span.disabled {
background: url("http://bzrhug.bay.livefilestore.com/y1p5YZ07FHnOKu0e0ODahS9i0Eil7gpVHbVwCbZ6YBNE9RdSbMdInAVVy5NKfbQwkj9PwRAUiHU7ujeIsuVWsbiJ_Key1ncrUi0/tmnbg.png") no-repeat scroll right -54px transparent !important;
}

.main-menu ul li.on, .main-menu ul li:hover {
background: url("http://bzrhug.bay.livefilestore.com/y1p5YZ07FHnOKu0e0ODahS9i0Eil7gpVHbVwCbZ6YBNE9RdSbMdInAVVy5NKfbQwkj9PwRAUiHU7ujeIsuVWsbiJ_Key1ncrUi0/tmnbg.png") no-repeat scroll left 0 #8d8d8d !important;
}

.main-menu ul li.on a, .main-menu ul li:hover a {
background: url("http://bzrhug.bay.livefilestore.com/y1p5YZ07FHnOKu0e0ODahS9i0Eil7gpVHbVwCbZ6YBNE9RdSbMdInAVVy5NKfbQwkj9PwRAUiHU7ujeIsuVWsbiJ_Key1ncrUi0/tmnbg.png") no-repeat scroll right -27px transparent !important;
color: #ffe680 !important;
}

.main-menu ul li {
background: url("http://bzrhug.bay.livefilestore.com/y1p5YZ07FHnOKu0e0ODahS9i0Eil7gpVHbVwCbZ6YBNE9RdSbMdInAVVy5NKfbQwkj9PwRAUiHU7ujeIsuVWsbiJ_Key1ncrUi0/tmnbg.png") no-repeat scroll left -81px #8d0000
!important;
border-left: 0px solid #FFFFFF !important;
border-right: 0px solid #FFFFFF !important;
}

.headerInfoBox, .userTopMenuWrapper, .main-menu, #header .headerCont, #body .bodyWrapper, #footer .footerCont {
margin-bottom: -1px !important;
}

.topicPrev .userInfo {
float: none !important;
margin: 5px !important;
width: 100% !important;
margin-right: -6px !important;
}

.noHeads {
clear: none !important;
}

.darkPlace, .paginator_cont {
-moz-border-radius: 7px 7px 7px 7px;
}

.topicsList .topic {
border: 1px solid #454a50 !important;
margin: 0 6px 8px 6px !important;
}

.siteMap dl dt a {
color: #821628 !important;
}

.siteMap dl dt a:hover, .siteMap dl dd a:hover, .footerNaviRight ul a:hover {
color: #c4c4c4 !important;
}

.footerNaviRight {
background: none !important;
border-left: 1px solid #626466 !important;
}

.addComment {
padding: 6px !important;
}

}
