css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://deseretnews.com/article/"), url-prefix("http://deseretnews.com/dn/view/"),
url-prefix("http://deseretnews.com/blogs/"),
url-prefix("http://www.deseretnews.com/blogs/"), url-prefix("http://www.deseretnews.com/movies/review/"), url-prefix("http://www.deseretnews.com/movies/synopsis/"), url-prefix("http://www.deseretnews.com/dn/view/"), url-prefix("http://www.deseretnews.com/article/"), url-prefix("http://deseretnews.com/user/"), url-prefix("http://www.deseretnews.com/user/"),
url-prefix("http://deseretnews.com/site/staff/"), url-prefix("http://www.deseretnews.com/site/staff/"){

#content, #storyContent { top: 0px !important;}
#main_col {margin-left: 0px !important; }
#storyContent #page_headline, #storyContent #main_col, #storyComments, #commentForm, .comment, .blogEntry { width: 650px!important; }
#tools, #header, #footer-nav, #subnav_sports, #subnav_community, #subnav_arts, #nav_col, .continue, #most_popular, #other_section_heads, .ad, #adLoad, .ad-container, #ad_300, #footer { display: none !important; }
.feature {float:right !important; }
#related_content { display: block !important }
.content-3column-feature {
     float:right !important;
}

.content-3column-main #page_headline, .content-3column-main {
     width:630px !important;
}
}
