css:
/* habrahabr.ru -- Dark Theme [0.1] by catarosso
*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("habrahabr.ru") {

body {
	background: #111111 !important;
	color: #008866 !important;
}

div.similar-posts, div.bg-bott-l {
	background: #222222 !important;
	color: #008866 !important;	
}

div.page-navigation td#write {
	background: #111111 !important;
	border-bottom: #000000 1px solid !important;
}

div.page-navigation td, div.page-submenu ul {
	background: #222222 !important;
	border-bottom: #000000 1px solid !important;
	border-right: #000000 1px solid !important;
}

div.page-navigation td.current {
	background: #224433 !important;
}

div, div.bg-bott-l h3 {
	background-image: none !important;
}

.logo {
	display: none !important;
}

.tags {
	background: #222222 !important;
}

.comments-header {
	background: #224433 !important;
}

.entry-content-only:hover {
	color: #00aa88 !important;
}

.entry-info, input#search {
	background: #111111 !important;
	border: 1px #000000 solid !important;
}

}
