css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {

/**********/
/*Defaults*/
/**********/
body {
background: transparent !important;
}

.body,html{
background-color: #262524 !important;
}

h1,h2,h3,h4{
color:#a4a4a4 !important;
}

a{
color: #a2bbff !important;
}

#footer,#footer li{
background-color: #0d0c0c !important;
color: #a4a4a4 !important;
}

table,td,tr,th{
background: transparent !important;
color: #a4a4a4 !important;
border-color: #a4a4a4 !important;
}

input *,textarea *{
background: #0d0c0c !important;
color: #a4a4a4 !important;
border-color: #000000 !important;
}

li,normal{
background: #262524 !important;
}

div,mw-search-formheader{
background: #262524 !important;
border-color: #333231 !important;
}

/****************************/
/*Top of the page navigation*/
/****************************/
#head{
background: #262524 !important;
}

/*****************************************************/
/*This section changes the front page left side menus*/
/*****************************************************/
[class="portal expanded"],[class="portal collapsed"],.portal li{
background-color: #262524 !important;
}

#panel div.portal
{
background: #262524 !important;
}

/*This changes the left side menu tabs*/
#panel h5
{
color: #ffffff !important;
}

/*********************************************************/
/*Main Page content area, the biggest part of your screen*/
/*********************************************************/
#mp-topbanner table div{
color: #a4a4a4 !important;
}

#mp-topbanner{
background: #0d0c0c !important;
border-color: #262524 !important;
}

#content,#head-base,#footer,#page-base {
color: #a4a4a4 !important;
background-color:#0d0c0c!important;}

/*This is the 1px perimeter box around the main page articles*/
.MainPageBG{
border-color: #262524 !important;
background-color: #0d0c0c !important;
}

/*Banners: Today's Featured Article, and the one underneath it (left side)*/
#mp-left{
background-color: #0d0c0c !important;
}

#mp-left td,#mp-left th{
background: #0d0c0c !important;
border: none !important;
}

#mp-tfa-h2,#mp-dyk-h2{
background: #262524 !important;
color: #ffffff !important;
border-color: #333231 !important;
}

/*The right part: In the news, On this day, blah blah*/
#mp-right{
background-color: #0d0c0c !important;
}

#mp-right td,#mp-right th{
background: #0d0c0c !important;
border:none!important;
}

#mp-itn-h2,#mp-otd-h2{
background: #262524 !important;
color: #a4a4a4 !important;
border-color: #333231 !important;
}

/*Today's featured pornographic*/
#mp-tfp tr td,.MainPageBG table{
background: #0d0c0c !important;
}

#mp-tfp th{
background: #0d0c0c !important;
border: none !important;
}

#mp-tfp-h2,#mp-tfp-h2{
background: #262524 !important;
color: #a4a4a4 !important;
border-color: #333231 !important;
}

/*Catlinks*/
.catlinks{
background-color: #262524 !important;
border-color: #333231 !important;
}

/*Search*/
#simpleSearch,#searchForm{
background: #262524 !important;
border-color: #333231 !important;
}

/***********************/
/*Article Stuff, mostly*/
/***********************/
#mw-js-message{
background-color: #0d0c0c !important;
border-color: #262524 !important;
}

/*standard headings*/
h2{
background: #0d0c0c !important;
border-color: #262524 !important;
}

h1{
border-color: #a4a4a4 !important;}

/*infobox*/
.infobox,.infobox td{
background: #262524 !important;
border-color: #333231 !important;
color: #a4a4a4 !important;
}

/*thumbs*/
.thumbinner,.thumbimage,[class="thumb tleft"],[class="thumb tright"],[class="thumb tnone"]{
background: #262524 !important;
border-color: #333231 !important;
}

.thumbcaption{
background-color: #262524 !important;
text-align: center !important;
}

/*navbox*/
.navbox{
border-color: #333231 !important;
}

/*searchbox*/
#simpleSearch{
border-color: #333231 !important;
}

/*TOCs and plainlinks*/
.plainlinks,#toc{
background: #262524 !important;
border-color: #333231 !important;
}

/********/
/*EDITOR*/
/********/
.wikiEditor-ui-toolbar,.wikiEditor-ui-controls{
background: #0d0c0c !important;
border-color: #333231 !important;
}

.wikiEditor-ui,.wikiEditor-ui-text,.wikiEditor-ui-top{
border-color: #333231 !important;
}

/*dropdowns*/
.index{
background: #0d0c0c !important;
}

.index div{
color: #a4a4a4 !important;
}

.pages{
background: #0d0c0c !important;
}

.cell-description,.booklet{
border-color: #333231 !important;
}

td.cell-syntax,td.cell-result{
color: #a4a4a4 !important;
border-color: #333231 !important;
}

/*****************/
/* inline images */
/*****************/
/*current tab at the top*/
/*this makes the text readable with the following images*/
.vectorTabs span{
color: #a2bbff !important;
}

/*this is the 1px-wide separators between all tabs*/
div.vectorTabs,div.vectorTabs li:not(.icon) a,div.vectorTabs ul,#head div.vectorMenu h5,div.vectorMenu h5 a{
background: #333231 !important;
}

/*these are the non-selected tabs*/
div.vectorTabs ul li:not(.selected){
background: #333231 !important;
}

/*this is the current tab*/
div.vectorTabs li.selected {
background: #333231 !important;
}

/*star icons*/
#ca-unwatch.icon a,#ca-watch.icon a{
background: #333231 !important;
}

#ca-watch.icon a.loading,#ca-unwatch.icon a.loading{
background: #333231 !important;
}

a[title="Visit the main page"]{
background-color: #262524 !important;
}

}
