css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("www.mojehobby.cz") {



/*MH v1.1 by HonzaK 06/2010*/



/*POZADI, REKLAMY, ZAPATI*/

body {background-color:#dfdfdf!important;background-image:none!important;}

div.obrpoz {background-image:none!important;}

div.logo {background-image:none!important; height:80px!important;}

div.ram_top_prihlas, .ram_top_neprihlas  {background-image: none!important;height:80px!important;}



div.obsah_top_prihlas, div.obsah_top_neprihlas {padding-top:0px!important; margin:0px!important;height:50px!important;background-image: none!important;}

div.obsah_top_prihlas span, div.obsah_top_neprihlas span {padding-top:0px!important; margin:0 200px 0 0!important;}

div.prihlasen {padding:0 7px 0 0!important; height:auto!important; background-image:none!important;}

div.ram_sv, td.center .ram_sv {background-image: none!important;}

div.ram_sv div {height:auto!important;}

div.bannery_top {display:none!important;}

td.left {padding:81px 0 0 2px!important;}

td.right {display:none!important;}

.page {background-color:#dfdfdf!important; width:1000px!important; background-image: none!important;}

div.banner_forum {display:none!important;}

div#divBodyAdHorni {display:none!important;}

div.zapati {display:none!important;}



/*FONTY*/

div.bila div div div span.re, div.bila div div div.prisp_seda div {font-family:Arial, Helvetica, sans-serif!important;}



/*SEZNAM TEMAT*/

div.box, div.box_left {width:90%!important; float:left!important;margin:0 0 0 1px!important; padding:0px 0 0 10px!important; background-image:none!important; background-color: #DFDFDF!important; border:0px solid #FF0000!important; height:20px!important;}

div.box_obsah {color:#000000!important; font-size:10px!important; min-height:16px!important; padding:1px 0 0 0px!important; vertical-align:bottom;height:16px!important;}

div.box_obsah a:before {content: "-";font-size: 12px; margin:0px; padding:0 7px!important;}

div.box_obsah br {display:none;}

.box_zahlavi {cursor: pointer!important; float:left;text-transform:lowercase!important; margin:0px!important; padding:0px!important; min-height:22px!important;}

div.box_nadpis {margin: 0px!important; padding: 0px 3px 0px 0px!important;vertical-align:baseline!important;background-color:inherit!important;}



/*SEZNAM TEMAT - NEPRECETENE*/

span.novych {color:#FF0000!important; font-size:12px!important;margin:0 0 0 0px!important; padding:0px!important;}



div.detail {background-color:#DFDFDF; padding: 3px!important; margin: 5px 0 -3px 0!important; border:1px solid #333;}



td.left div.menu_detail {margin: 0 0 1px 0!important; border:1px solid #333333; padding: 0px; width:160px!important;}

div.menu_detail a:hover {background-color:#CFCFCF!important;}



.obsah_top_prihlas {color: black!important;}

div.prihlasen {color:#000!important;}



div.tlzapnute, div.tlvypnute {border: 1px solid black; background-color:#CCC;text-align: left; vertical-align: middle; height: auto!important; width: 160px!important; padding: 0!important; cursor: pointer; margin: 0 0 1px 0!important; background-image:none!important; }



/*VYNULUJ A UZIVATELE*/

.ram_bottom {background-image: none!important; height: 17px; border-top:1px solid #6081B2;} 

.vynuluj {float: left; font-weight: bold; margin-left: 45px!important;} 

.vynuluj a {text-decoration: none; color: #000000;}

.vynuluj a:after {content: " <";}

.uzivatele {font-weight: bold; float: left; margin-left: 390px!important; margin-right:0px!important;}

.uzivatele a {text-decoration: none; color: #000000;}

.uzivatele a:before {content: "> ";}

.vynuluj a:hover, .uzivatele a:hover {text-decoration: underline!important;}

.ram_bottom_cisty {background-image: none!important; } 



/*ZAHLAVI V DISKUZI*/

div.zahlavi_zlute {background-image:none!important; border: 1px solid black; background-color:#CFB29B;}

div.menu_modre {background-image:none!important; background-color:#C0C0C0;}

div.bezove_tl {background-image:none!important; background-color:#CFB29B; padding:1px 0 0 0!important;}

div.bezove_tl:hover {text-decoration:underline;}

div.bezove_tl_red {background-image:none!important; background-color:#FF782F;padding:1px 0 0 0!important;}

div.bezove_tl_aktivni {background-color:#FF782F;padding:1px 0 2px 0!important;}



/*TLACITKO FORMULARE*/

input.frmbutton {background-image:none!important;background-color:#FF782F; border-color:#000000;}

input.frmbutton:hover {background-color:#FF782F;}



/*CLANKY*/

div.obsah_bl_modra {background-image:none!important;background-color:#FFFFFF!important; margin-top:0!important;border-top:1px solid #FF782F;}



/*OBRAZKY OVLADANI VLAKNA*/





/*FORM*/

table.editor tbody tr td.pravy .inputTXT {border:1px solid #333!important;}

table.editor tbody tr td.pravy textarea.inputTXT {height:160px!important;}



/*REGISTRACE*/

div.obsah_top_neprihlas, div.login,div.tudy, div.ram_sv div.registrace {color:#000000!important;}

div.tudy a.zluty_odkaz {color:#FF782F!important;font-weight:bold; text-decoration:underline!important;}

div.login input.inputTXT {border-color:#333!important;}

}
