 * Author : cirer
 * Updated: 2012.08.03
 * Description : 天涯论坛自用风格
================================*/

@namespace url(http://www.w3.org/1999/xhtml);

/****************** 全站 ******************/
@-moz-document domain("tianya.cn") {

/*广告*/
#tianyaBrandSpan1,
#newmainmidright,
#adsp_list_top_banner,
#adsp_list_top_text_root,
#adsp_content_left_div,
#float_right_parent_div,
#ty_msg_mod,/*右下角弹出消息面板*/
#bottom_nav_container,/*底部导航*/
#ty_msg_box,
#adsp_content_replybox_img_1,
#adContentDiv,
#adsp_list_top_banner,
.adb,#adsp_content_top_banner,
#adsp_content_left_banner,
#adsp_content_title_left,
IFRAME[name="google_ads_frame"],
#adsp_list_top_banner_1,
#adsp_content_banner_1,
#adsp_content_banner_2,
#adsp_content_banner_3,
#adsp_content_banner_4,
#adsp_content_title_banner_left,
#adsp_content_adtopic,
#google_ads_div_bbs_index,
.bbs_ad,.zq_ta,BUTTON,
#adsp_list_adsp_xf_floatDiv,
#couplet_left_adsp_banner_couplet,
#couplet_right_adsp_banner_couplet,
#adsp_list_floatDiv,
#adsp_list_top_text_root,.focus_rig,
#couplet_left_NULL,
#couplet_right_NULL,
/*天涯聚焦*/
#CL_640_80_AD_1,/*天涯聚焦LOGO右广告*/
#FOCUS_270_80_AD_1,
#FOCUS_270_80_AD_2,
#FOCUS_270_80_AD_3,
#FOCUS_670_80_AD_1,
#FOCUS_670_80_AD_2,
#FOCUS_670_80_AD_3,
IMG[src="http://advertisement.tianya.cn/ad_js/wangjing_1.gif"],
IMG[src="http://advertisement.tianya.cn/ad_js/wangjing_2.gif"],
IMG[height="250"][border="0"][width="300"]/*回复框旁*/
{display:none !important;}

/*字体表*/
* {font-family: 微软雅黑, Arial, Helvetica, sans-serif !important; }

/*左导航*/
#leftNav{bottom:0px !important;}

#pageright
{width:100%!important;}

/************************* 阅读样式 - BEGIN ********************/
.pagewrap{width:950px !important;}
.pagewrap #pContentDiv{
font-size:16px !important; 
line-height:1.8em !important;
background:#EEEFFF !important;
width:920px !important;
border-radius: 5px ! important;
padding:15px !important;
border:solid 1px #BBB !important;
margin-left:auto !important;
margin-right:auto !important;}

/*标题*/
.pagewrap H1{height:auto!important;}
.pagewrap H1 SPAN{
padding:10px !important;
background:#538EC9 !important;
border-radius:20px ! important;
font-size:18px !important;
font-weight:bold;
text-shadow: black 0px 0px 10px;}
.pagewrap H1 SPAN A:hover{text-decoration: none !important;}

/*登录*/
.topnav,#tianyatopguide_nav{height:0 !important;border:none !important;}
.tianyatopguidenavr{position:fixed !important;bottom:0 !important;left:auto !important; right:0 !important;padding:0 10px !important;}

/*楼主*/
#firstAuthor{width:100% !important;}
 #firstAuthor TD[align="center"]{width:950px !important;}

/*当前位置*/
.guide{position:fixed !important;left:auto !important; top:0 !important;right:3px !important;}
.guide16{font-size:12px !important;}
.guide[style="float:right"]{top:25px !important;}
.guide[style="float:right"] A{color:red !important;}


/*只看某人回复*/
#userlist{position:fixed !important;left:103px !important;top:30px !important;}

/*回复者*/
#pContentDiv TABLE[bgcolor="#f5f9fa"] {width:auto !important;height:30px !important;margin:15px 0 !important;background:#EEEFFF !important;border-radius:5px ! important;}
#pContentDiv TABLE[bgcolor="#f5f9fa"] DD.quickrep{margin-bottom:10px !important;}
#pContentDiv TABLE[bgcolor="#f5f9fa"] TD{line-height: 5px !important;background:#DDD !important;  }
#pContentDiv TABLE[bgcolor="#f5f9fa"] TD[align="RIGHT"]{ line-height: 5px !important; }

/*页脚*/ 
.pagewrap .foot{width:100% !important;}

/*techforum样式*/
.post{font-size:16px !important; 
line-height:1.8em !important;
background:#EEEFFF !important;
padding:15px !important;
}
.vcard{padding:3px!important;background:#DDD !important;border-top-left-radius: 5px ! important;border-top-right-radius: 5px ! important;}
.post-fd{background:#EEEFFF!important;}

/************************* 阅读样式 - END ********************/

/****************** 列表页 ******************/
/*拉伸*/
.wrapper,#forumWrapper,#topNavWrapper,#bottomNavWrapper,#mainDiv,#forumContentDiv,#content,#topNav,#bottomNav{width:100% !important;margin-left:0 !important;}
TABLE[width="640"],.foot,#newmainmidleft{width:90% !important;}/*----->若有超过或不满，请调整这个百分比<-----*/
.listtable{width:100% !important;}

/*链接样式*/
#forumContentDiv a:link,#postlistwrapper a:link{color:#2640A3 !important; text-decoration: none !important;}
#forumContentDiv a:hover,#postlistwrapper a:hover{font-weight:bold !important;}
#forumContentDiv a:visited,#postlistwrapper a:visited{text-decoration: none !important;}

/*版面推荐*/
IFRAME[width="640"],IFRAME[width="638"],IFRAME[width="620"]{height:170px !important;width:100% !important;}
#topNews,#hot{width:100% !important;}
IFRAME[height="37"]{display:none !important;}/*股市论谈*/
IFRAME[src="http://main.tianya.cn/focus/iframe/bbstopf.jsp"]{height:310px !important;}
TABLE.top_all,TABLE.table{width:700px !important; margin-left:0 !important ; margin-right:auto !important;}
TD.top_all{text-align:center !important;}

/*调整各列宽度*/
TD[width="380"],TD[width="370"],TD.posttitle.faceblue,TD.posttitle.facered{width:50% !important;}
TD[width="90"],TD[width="88"],TD[width="87"],TH.row02,TH.w_c[width="75"],TD.author,TD.ttime.w_c{width:15% !important;text-align:left !important;}
TD[width="40"],TD[width="35"],TH.w_c[width="35"],TD[width="37"],TD.tviewre.w_c,TD.treplay.w_c{width:10% !important;text-align:left !important;}

/*刷新栏修正*/
TABLE[cellspacing="3"] td{text-align:left !important;}
TABLE[cellspacing="3"] a:visited{color:#2640A3 !important; }

/*精华帖列表页面*/
CENTER TD.font12{display:block !important;}
#forumContentDiv center{text-align:left !important;}

/*小修正*/
TD.font12,TABLE[style="border-top: 2px solid rgb(102, 153, 204);"]{display:none !important;} /*隐藏*/
TD.guidetd{text-align:left !important;padding:3px 0 3px !important;} /*贴子总计数左移*/
TD[width="20"],TD[width="22"],TD[width="19"]{display:none !important;} /*不显示Wingding字符*/
#forumContentDiv FONT[color="white"],TH.row01,TH.row02,TH.w_c{text-shadow: black 1px 1px 2px;font-size:18px !important; font-weight:bold !important; letter-spacing: 0.2em;} /*白色字*/
TD[style="word-wrap: break-word;"] b{color:#C00000 !important;}/*鬼话列表中加粗字*/
TR[height="20"]:hover,TABLE[height="20"][width="640"]:hover,#postlistwrapper TR:hover{background:#CCC !important;}
TH.row01{text-align:center !important;}

}
