css:
/**************   Freekick Old Green by ghad13  version 2.0   **************/


@namespace url(http://www.w3.org/1999/xhtml);


/*******   Settings for main domain   *******/

@-moz-document url-prefix("http://freekick.org"),
               url-prefix("http://www.freekick.org")
{


  /*****   GLOBAL   *****/
  
  html, body
  {
    min-height: 100%;
    background-color: #226611 !important;
  }

  a, .link
  {
    color: #FFFFFF !important;
  }

  a:active, .link:active
  {
    outline: none !important;
  }

  :-moz-any-link:focus
  {
    outline: none !important;
  }

  input
  {
    margin: 1px 0 !important;
  }

  #page-wrap
  {
    width: 1020px !important;
  }

  #left-content
  {
    width: 17% !important;
  }

  #outer-content
  {
    margin-left: 0px !important;
    float: left;
    width: 83% !important;
  }

  .header-sprite, .red-stripe
  {
    background-image: none !important;
    background-color: #003300 !important;
  }

  .blue-crn-l, .green-crn-l
  {
    -moz-border-radius-topleft: 3px;
    -moz-border-radius-bottomleft: 3px;
  }

  .blue-crn-r, .blue-crn-re, .green-crn-re, .green-crn-r
  {
    -moz-border-radius-topright: 3px;
    -moz-border-radius-bottomright: 3px;
  }

  table th
  {
    background-color: #004400 !important;
    color: #FFFFFF !important;
  }

  table th a
  {
    color: inherit !important;
  }

  .textlink, .textlink a, .player, .club, .user, .nation, .section-head, .grey
  {
    color: #FFFFFF !important;
  }

  .prospect-player
  {
    color: #AAAAAA !important;
  }

  .dots, .dotted, .dotted li, .view-active, .view-passive, .standard-list td,
  .standard-list th, .players td, .players th, .plain-dotted-list li,
  .message-content, .promotion-demotion-none
  {
    background-image: none !important;
    border-bottom: 1px dotted #669966 !important;
  }

  .dotted td, .dotted th, .highlightable th, .odd td, .even td,
  .plain-dotted-list li
  {
    border-bottom: 1px dotted #669966 !important;
  }

  .online
  {
    color: #004400 !important;
    font-weight: bold;
  }

  .offline
  {
    color: #881111 !important;
    font-weight: bold;
  }

  /***  Skill & age colors  ***/

  /* skills */
  .awesome {color: #DD6611 !important;}
  .brilliant {color: #FF8833 !important;}
  .superb {color: #FF9966 !important;}
  .excellent {color: #FF99CC !important;}
  .good {color: #CC33CC !important;}
  .decent {color: #FFFFFF !important;}
  .weak {color: #33CCCC !important;}
  .poor {color: #FFFF00 !important;}
  .awful {color: #FF0000 !important;}

  /* age */
  .age-retired { color: #FF0000 !important; }
  .age-old { color: #CC6622 !important; }
  .age-senior { color: #FFFFFF!important; }
  .age-youth-0 { color: #99DDFF!important; }
  .age-youth-1, .age-youth { color: #4488FF !important; }
  .age-youth-2 { color: #8855FF !important; }
  .age-youth-3 { color: #CC33CC !important; }
  .age-youth-4 { color: #FF8833 !important; }
  .age-youth-5 { color: #C66723 !important; } /* ? */

  /* training */
  .drop { color: #CC7733 !important; }
  .pot { color: #55EE33 !important; }
  .abovepot { color: #EECC00 !important;}



  /*****   HEADER   *****/

  .darkGrey
  {
    background-color: #0099FF !important;
    height: 1px !important;
  }



  /*****   MENU & STATUS   *****/

  .menu-sprite
  {
    background-image: none !important;
    background-color: #030 !important;
  }

  .menu-header
  {
    -moz-border-radius-topright: 5px;
  }

  .menu-bottom
  {
    -moz-border-radius-bottomright: 5px;
  }

  #left-wrap
  {
    margin-top: 12px !important;
  }

  #left-wrap .green-stripe
  {
    background-image: none !important;
    display: none !important;
  }

  #left-wrap .club-name, #left-wrap .settings-header, #left-wrap .title-menu-l, #left-wrap .login-header
  {
    border-bottom: 3px double #FF7700 !important;
    padding-top: 10px !important;
    padding-bottom: 5px !important;
    color: #FFFFFF !important;
    text-align: center !important;
    font-size: 11px !important;
  }

  #left-wrap .club-name a
  {
    color: inherit !important;
  }

  .makeMenu li
  {
    background-image:none !important;
    border-bottom: 1px dotted #008800 !important;
    padding-bottom: 3px !important;
    padding-top: 4px !important;
  }

  .makeMenu li a, .menu-status li a
  {
    color: #DDDDDD !important;
    font-weight: bold !important;
    border-left: 3px solid #FF7700 !important;
  }

  .makeMenu li a:visited, .menu-status  li a:visited
  {
    color: #DDDDDD !important;
    text-decoration: none !important;
  }

  .makeMenu li ul
  {
    border:3px solid #004400 !important;
    background-color:#003300 !important;
    -moz-border-radius: 5px;
  }

  .makeMenu ul li a, .menu-status ul li a
  {
    border-left: 3px solid #FF7700 !important;
  }

  .makeMenu li:hover, .menu-status li:hover
  {
    background-color: #004400 !important;
  }

  .makeMenu li a:hover, .menu-status li a:hover 
  {
    background-color: inherit !important;
  }

  .menu-status ul
  {
    padding-left: 0 !important;
    padding-right: 0 !important;
    padding-bottom: 5px !important;
    border-bottom: 3px double #DD7700 !important;
    margin-bottom: 5px !important;
    width: 100% !important;
  }

  .menu-status-left
  {
    font-weight: bold !important;
    font-size: 9px !important;
  }

  .menu-status-right
  {
    color: #FFFFFF !important;
    font-size: 9px !important;
  }

  .on
  {
    color: #00CC00 !important;
  }

  .off
  {
    color: #991100 !important;
  }

  #left-wrap .menu-sprite.menu-body.menu-status li a
  {
    text-decoration: none !important;
  }

  #left-wrap .login a.reg
  {
    font-size: 10px !important;
    color: #FF7700 !important;
  }

  #form-login
  {
    padding-top: 5px !important;
    margin-bottom: 15px !important;
  }

  #form-login label
  {
    color: #55AA55 !important;
  }

  #form-login ul
  {
    margin-bottom: 8px !important;
  }

  #form-login ul input
  {
    width: 97% !important;
    margin: 1px auto !important;
  }

  #form-login input#username, #form-login input#password
  {
    height: 100% !important;
  }

  #form-login .login-submit
  {
    margin: 0 auto !important;
  }

  #form-login .login-submit input
  {
    margin: 0 !important;
  }

  .menu-body .dots, .menu-body .dotted
  {
    display: none;
  }

  .menu-body .reg
  {
    background-image: none !important;
    padding-left: 0 !important;
  }



  /*****   LOADING   *****/

  .loading
  {
    padding: 3px 50px !important;
    color: #FFFFFF !important;
    font-weight: bold;
    font-size: 14px;
    background-position: 8px center !important;
    background-color: #003300 !important;
    -moz-border-radius: 5px;
  }



  /*****   LANGUAGE SELECTOR   *****/

  #lang-select
  {
    display: block !important;
    background-color: inherit !important;
    font-weight: bold !important;
    visibility: visible !important;
  }

  .lang-select-crn
  {
    -moz-border-radius-bottomright: 5px;
  }

  p.sel-lang
  {
    height: 100%;
  }

  #lang-select .header-sprite
  {
    background-image: none !important;
  }

  #languageSelector
  {
    border: 2px solid #3989D9 !important;
  }

  .language-selector
  {
    width: 100% !important;
    padding: 0 !important;
    border: none !important;
    background-color: #006600 !important;
  }

  .language-selector li
  {
    padding: 0 !important;
  }

  .language-selector a
  {
    border-left: none !important;
    padding: 4px 5px !important;
    font-weight: normal !important;
  }

  .language-selector a:hover
  {
    background-color: #004400 !important;
  }



  /*****   TOP MENU   *****/

  #blue-bar-crn
  {
    -moz-border-radius-bottomleft: 5px;
  }

  #blue-help, #blue-nav
  {
    -moz-border-radius-bottomright: 5px;
  }

  #blue-help
  {
    color: #FF7700;
  }

  #blue-bar .header-sprite
  {        
    background-image: none !important;
    background-color: #030 !important;
  }



  /*****   MAIN CONTENT   *****/

  /***  TABS  ***/

  #main-green-wrap
  {
    margin-top: 12px !important;
    background-image: none !important;
    background-color: #002900 !important;
    -moz-border-radius: 5px;
    color: #FFFFFF !important
  }

  #green-round
  {
    background-image: none !important;
    height: 10px !important;
  }

  #main-tabs
  {
    padding-left: 15px !important;
  }

  #main-tabs li
  {
    padding:0 !important;
    background-image: none !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
  }

  #main-tabs li a
  {
    display: block;
    color: #444444 !important;
    padding: 4px 10px;
  }

  #main-tabs li a:hover
  {
    text-decoration: none !important;
  }

  #main-tabs li.tab-hidden
  {
    background-color: #BBBB99 !important;
    margin-bottom: 3px !important;
  }

  #main-tabs li.tab-hidden:hover
  {
    background-color: #BBC699 !important;
  }

  #main-tabs li.tab-active
  {
    padding-bottom: 2px !important;
  }

  #main-tabs li.tab-active a
  {
    color: #006600 !important;
  }


  /***  CONTEMT HOLDER  ***/

  #white-content
  {
    -moz-border-radius: 5px;
    background-color:#228822 !important;
    background-image: none !important;
    margin: 0 auto !important;
    width: 97% !important;
  }

  #white-content .rigth-crn, #white-content .btm-shadow
  {
    background-image: none !important;
  }

  #main-content
  {
    padding-right:0px !important;
    color: #FFFFFF;
  }

  #content-l
  {
    width: 65.15% !important;
    border-right: 2px solid #225522 !important;
  }

  #content-r
  {
    left: 0 !important;
    width: 29.75% !important;
    border-left: none !important;
  }

  .hidden-result
  {
    color: #AAAAAA !important;
  }

  .form-prediction-denom  /* used in form-tooltip */
  {
    background-color: inherit !important;
  }

  .rating-denom  /* used in rating-tooltip */
  {
    background-color: inherit !important;
  }

  #WzBoDy
  {
    background-color: #226600 !important;
  }

  #WzBoDyI
  {
    color: #000044 !important;
    background-color: inherit !important;
  }


  /***  PLAYERS TABS  ***/

  .players-tabs li
  {
    background-image: none !important;
    padding: 2px 2px 0px 2px !important;
  }

  .players-tabs li a
  {
    background-image: none !important;
    background-color: #223366 !important;
    border-right: 8px solid #223366 !important;
    color: #AAAAFF !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
  }

  .players-tabs li.players-selected
  {
    background-image: none !important;
    background-color: #0099FF !important;
    margin: 0 2px !important;
    padding: 1px 1px 0px 1px !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
  }

  .players-tabs li.players-selected a
  {
    background-image: none !important;
    background-color: #229922 !important;
    border-right: 8px solid #229922 !important;
    color: #FFFFFF !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
  }

  div.players-wrap
  {
    background-color: #228822 !important;
    border-style: solid !important;
    border-color: #000055 !important;
    border-width: 1px 0 !important;
    -moz-border-radius: 5px;
  }

  tr.odd td, .odd, tr.alternate-odd td
  {
    background-color: #006600 !important;
  }

  tr.even td, .even, tr.alternate-even td
  {
    background-color: #005A00 !important;
  }

  tr.odd-marked td, tr.even-marked td, tr.marked td
  {
    background-color: #333333 !important;
  }

  tr.greyLine td
  {
    background-color: #002200 !important;
    border: none !important;
  }

  tr.grayLine td
  {
    border-bottom: 2px solid #002200 !important;
  }


  /***  SITE: Before Login  ***/

  .freekick, .quest, .profile-name
  {
    color: #FFFFFF !important;
  }

  .profile-name
  {
    width: 25% !important;
  }

  .bg, .bg-dwn, .answ-left-up, .answ-left-dwn, .answ-right-up,
  .answ-right-dwn, .answ-content {
    background-image: none !important;
    background-color: #151 !important;
    border: 0px !important;
  }

  .answ-content
  {
    width: 95% !important;
    padding: 0 13px !important;
    text-align: justify;
  }

  .bg, .answ-left-up, .answ-right-up
  {
    -moz-border-radius-topleft: 5px;
    -moz-border-radius-topright: 5px;
  }

  .bg-dwn, .answ-left-dwn, .answ-right-dwn
  {
    -moz-border-radius-bottomleft: 5px;
    -moz-border-radius-bottomright: 5px;
  }


  /***  SITE: Clubhouse  ***/

  li.small-news-item
  {
    border-bottom: none !important;
  }

  .small-news-header, .guestbook-title
  {
    background-color: #004400 !important;
    color: #FFFFFF !important;
  }

  .small-news-header a:hover
  {
    text-decoration: none !important;
  }

  .small-news-date, .small-news-time
  {
    color: #6666CC !important;
  }

  .small-news-content
  {
    background-color: #006600;
    border-top: none !important;
    margin-top: 0 !important;
    padding-top: 5px !important;
  }

  .small-news-content a
  {
    color: #44AAFF !important;
  }

  .guestbook-content
  {
    background-color: #006600;
  }

  .guestbook-content a
  {
    color: #44AAFF !important;
  }

  .guestbook-comment
  {
    background-color: #005A00;
    color: #FFFFFF !important;
  }


  /***  SITE: Status  ***/

  #collapse-table-1 div.floating-row span.second a,
  #collapse-table-2 div.floating-row span.second a
  {
    color: #44AAFF !important;
  }

  .right-stats .second .nation
  {
    white-space: normal;
  }


  /***  SITE: Awards  ***/

  .trophy .box
  {
    width: 33.33% !important;
  }

  .trophy:hover
  {
    background-color: #226622 !important;
  }


  /***  SITE: Finances  ***/

  .income, .income a {color: #44FF44 !important;}
  .expense, .expense a {color: #FF8800 !important;}

  .financesMain td
  {
    border-top: 2px solid #004400 !important;
    background-color: #228822 !important;
  }

  .finances td.so-far, .finances td.coming
  {
   text-align: center !important;
  }

  .finances td.post
  {
    width: 40% !important;
  }


  /***  SITE: Messages  ***/

  .view-active
  {
    background-color: #228822 !important;
    font-weight: bold;
  }

  .message-open
  {
    background-color: #004400 !important;
  }

  .message-header
  {
    margin: 0 !important;
    overflow: auto;
  }

  .message-header input
  {
    margin: 2px 4px !important;
    vertical-align: middle;
    float: left;
  }

  .message-header span
  {
    vertical-align: middle;
  }

  .message-header.message-unread img
  {
    margin: 1px;
    float: left;
  }

  .message-content
  {
    margin: 0 0 5px 0 !important;
    padding-bottom: 7px !important;
    background-color: #006600 !important;
  }

  .message-content a
  {
    color: #44AAFF !important;
  }


  /***  SITE: Message (to send)  ***/

  textarea
  {
    width: 90% !important;
    min-height: 100px !important;
  }


  /***  SITE: Club News  ***/

  #messages li.message-content.loaded i
  {
    display: inline !important;
  }


  /***  SITE: Mentor  ***/

  #pupilsmessages #content-l a
  {
    color: #44AAFF !important;
  }


  /***  SITE: Transfers  ***/

  .brdRGrey
  {
    border-right: 1px solid #339900 !important;
  }

  .transfer-navigator a
  {
    color: #FF7700 !important;
  }

  .transfer-navigator a.current
  {
    font-weight: bold;
    font-size: larger;
  }


  /***  SITE: Training  ***/

  .training-history td
  {
    line-height: 140%;
  }

  .training-history.highlightable a:active
  {
    outline-color: -moz-use-text-color !important;
    outline-style: dotted !important;
    outline-width: 1px !important;
  }


  /***  SITE: Tactics  ***/

  .roundtop, .roundbottom
  {
    display: none !important;
  }

  hr
  {
    height: 2px;
    background-color: #114411 !important;
    border: none;
  }

  .blue-round
  {
    background-image: none !important;
    background-color: #116666 !important;
    -moz-border-radius: 5px;
  }

  #load_settings .tactics-set
  {
    padding-top: 10px !important;
  }

  #load_settings .tactics-set span.right
  {
    float: right !important;
  }

  #load_settings .tactics-set hr
  {
    margin: 10px 0 !important;
  }

  #load_settings .tactics-set span.left.algnL
  {
    margin: 0 !important;
    text-align: center !important;
    width: 25%;
    height: 100% !important;
  }

  #team-order-info
  {
    width: 72% !important;
    text-align: justify !important;
    margin-top: 0 !important;
    float: right !important;
  }

  #lineup-status
  {
    -moz-border-radius: 3px;
  }

  #player_lineup .lineup_player td,
  #player_lineup .lineup_delimiter
  {
    border-bottom: none !important;
  }

  .co-dashed
  {
    border-bottom: 1px dotted #003300 !important;
  }

  .leftpart-bigger, #form_options
  {
    width: 100% !important;
  }

  select#side
  {
    max-width: 350px;
  }

  #predict_long_shot
  {
    vertical-align: middle;
  }

  #orders select, #conditions select
  {
    max-width: 142px;
  }


  /***  SITE: News  ***/

  .news-items
  {
    margin: 0 !important;
    padding-top: 5px !important;
    padding-bottom: 5px !important;
  }

  .news-items p a
  {
    color: #44AAFF !important;
  }

  .news-items-header
  {
    background-color: #005500 !important;
  }

  .news-items h2
  {
    background-image: none !important;
    background-color: #004400;
    padding: 4px !important;
    border-bottom: 2px solid #1875D2 !important;
    -moz-border-radius-topleft: 5px;
    -moz-border-radius-topright: 5px;
  }

  .news-items p.release-date
  {
    padding-top: 2px !important;
    padding-left: 2px !important;
    background-color: #006600;
    color: #FFFFFF !important;
  }

  .news-items-release-date
  {
    display: block;
    padding: 2px 5px !important;
    background-color: #006600;
    color: #AAAAAA !important;
    font-style: italic;
  }

  .news-items span.release-date a
  {
    color: #AAAAAA !important;
  }


  /***  SITE: League  ***/

  .promotion-demotion-automatic td, .promotion-demotion-automatic.marked td
  {
    background-color: #001100 !important;
    border: none !important;
  }

  .promotion-demotion-qualification td,
  .promotion-demotion-qualification.marked td
  {
    background-image: none !important;
    background-color: #003300 !important;
    border: none !important;
  }

  .standings .promotion-demotion-none,
  .standings .dots
  {
    display: none;
  }

  table.upcoming .nowrap.algnL
  {
    white-space: normal !important;
  }


  /***  SITE: IC  ***/

  .int-fixtures th
  {
    border-left: none !important;
    border-bottom: 1px dotted #669966;
  }


  /***  SITE: World Cup  ***/

  .standard-list a.nation
  {
    white-space: normal !important;
  }


  /***  SITE: Transfer List  ***/

  .bid-inactive
  {
    color: #AAAAAA !important;
  }

  .transfer-column-l
  {
    width: 35% !important;
  }


  /***  SITE: Player  ***/

  .training-history td.header
  {
    background-color: #003300 !important;
  }


  /***  SITE: Preferences  ***/

  .preferences-title
  {
    width: 50% !important;
  }

  ul.multiple-select
  {
    background-color: #FFFFFF;
    color: #000000;
  }

  ul.multiple-select input
  {
    margin-top: 5px !important;
  }


  /***  SITE: Schedule  ***/

  .seperator, .seperator th, .seperator td
  {
    border-bottom: 1px solid #1875D2 !important;
  }


  /***  SITE: Matchreport  ***/

  .green-bl, .blue-bl
  {
    width: 100% !important;
  }

  .green-bl, .green-br, .green-tl, .green-tr
  {
    background-image: none !important;
    background-color: #225522 !important;
    -moz-border-radius: 5px;
  }

