css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
http://366.hopto.org
moen
*/

@-moz-document domain("csfd.cz") {
.content input[type="text"], input[type="password"], input[type="checkbox"], input[type="select"], input[type="file"], select, div#pg-login.page-content div.ui-form input[type="text"]{
    -moz-appearance: none !important;
    background-color: #333 !important;
    color: #fff !important;
    border: 1px solid #555 !important;
}



.content input[type="submit"]{
    -moz-appearance: none !important;
    background-color: #ccc !important;
    border: 1px solid #555 !important;
}

body, #page-wrapper {
  background-color: black !important;
  color: white !important;
  border: 0 !important;
}

* {
	-moz-border-radius: 0 !important;
}

div.content {
/*  background-color: black !important;
  border:1px solid #111 !important;*/
  color:white !important;
}

.page-content {
  color:white !important;
  background-color: black !important;
}

#page-header, .header {
  background-color: #111 !important;
}

.footer {
  background-color: #111 !important;
}

a.ui-linkselect-link {
    color: #bbb !important;
}

#pg-home .sidebar .others, #pg-home .sidebar .statistics {
    border-top: 10px solid #111 !important;
}

#user-menu {
    background-color: #111 !important;
}

ul#main-menu {
    border-color: #111 !important;
}

.ui-form fieldset legend {
    background-color: #111 !important;
}


.ui-form fieldset {
    background-color: #333 !important;
}

form input.button {
    color: black !important;
}

.ct-general > .header {
    background-color: #111 !important;
}

.ct-general.th-5, .ct-general.c20161128-th-5, .ct-general.th-5b, body.th-5 .ct-general.th {
  border-color: #333 !important;
}

.ct-general.th, .ct-general.th-0, .ct-general.th-1, .ct-general.th-2, .ct-general.th-3, .ct-general.th-4, .ct-general.th-5, .ct-general.th-5, .ct-general.th-5b, .ct-general.th-filter, .ct-general.th-form {
  border-radius: 0;
}

.ct-general.th-4 {
  border-color: #333 !important;
}

#pg-web-film #rating {
  border-radius: 0;
}

#page-footer {
  border-radius: 0;
}

/*profil, posta*/
ul#context-menu {
    background-color: black !important;
    border-color: #111 !important;
}

.ui-sidebar-menu {
    background-color: black !important;
}

ul.ui-posts-action-list li.received {
    background-color: black !important;
}

ul.ui-posts-action-list li.sent {
    background-color: #111 !important;
}

#pg-messaging .ui-sidebar-menu li.current{
    background-color: #111 !important;
}

/*profil, "kdo jsem"*/
#pg-user .ui-sidebar-menu ul li.active {
    background-color: #111 !important;
}

#pg-user .profile-content-bg, #pg-user .profile-content {
    background-color: #CCC !important;
    color: black !important;
}

#pg-user .ui-sidebar-menu{
    margin:1px !important;
    width: 150px !important;
}

#pg-user .ui-sidebar-menu-bg {
    background-color: #111 !important;
    border-left: 1px solid #111 !important;
}

div.ratings{
    background-color: green !important;
    color: black !important;
}

#user-menu.logged {
    background: 0;
}

/* jmeno filmu */
.ui-profile.ct-general h1{
    color:white !important;
}

/* bile hvezdy */
img[src$="/stars/1.gif"] {
	height: 0 !important;
	width: 0 !important;

	padding-left: 15px !important;
	padding-top: 15px !important;

	background:
		url(data:image/png;base64,)
		no-repeat
		!important;
}

img[src$="/stars/2.gif"] {
	height: 0 !important;
	width: 0 !important;

	padding-left: 30px !important;
	padding-top: 15px !important;

	background:
		url(data:image/png;base64,)
		no-repeat
		!important;
}

img[src$="/stars/3.gif"] {
	height: 0 !important;
	width: 0 !important;

	padding-left: 45px !important;
	padding-top: 15px !important;

	background:
		url(data:image/png;base64,)
		no-repeat
		!important;
}

img[src$="/stars/4.gif"] {
	height: 0 !important;
	width: 0 !important;

	padding-left: 60px !important;
	padding-top: 15px !important;

	background:
		url(data:image/png;base64,)
		no-repeat
		!important;
}

img[src$="/stars/5.gif"] {
	height: 0 !important;
	width: 0 !important;

	padding-left: 75px !important;
	padding-top: 15px !important;

	background:
		url(data:image/png;base64,)
		no-repeat
		!important;
}

/*komentare*/
.ct-general > .content > ul.ui-posts-list{
        background-color: #111 !important;
}

/*sdileni*/
#share {
    background-color: #111 !important;
}

#share ul {
    opacity:0.5 !important;
}

/* hodnoceni vpravo */
#sidebar > #ratings > .content > ul li a{
    max-width: 70px;
}

.ui-profile-user-list .content li.favorite {
    background-color: #111 !important;
}

.th-1 .with-relations > .relations li {
/*    background-color: #111 !important;*/
}

.canon, .oscars, .ceskylev {
    display: none !important;
}

/* obrazky u filmu napravo */
.ui-profile .links li a {
	background-image:
		url(data:image/png;base64,)
		!important;
}

.ct-general.with-navigation > .navigation {
    background-color: #111 !important;
}

/* menu u filmu */
/*disabled*/
.ct-general.with-navigation > .navigation li span {
    height:20px !important;
    background: black !important;
}

.ct-general.with-navigation > .navigation .disabled span {
    color: #333 !important;
}

/* fix pro sede filmy */
.th-0 .ct-general.with-navigation > .navigation .disabled span {
    color: #111 !important;
}

/*aktivni*/
.ct-general.with-navigation > .navigation li a {
    height:20px !important;
    background: #111 !important;
}

/*prave vybrano*/
.ct-general.with-navigation > .navigation li .selected{
    background: #333 !important;
}

.ct-general.with-navigation {
    margin-top: 50px !important;
}

.ct-general.with-navigation > .navigation {
    top: -30px !important;
}

.ct-general.with-navigation > .navigation li{
}

/*kino*/
#pg-cinema .schedule .cp-mediadesk {
    background-color: #111 !important;
    color: white !important;
}

#pg-cinema .schedule .cinema table > caption, div.navigation li.selected span, .ui-list-label {
    background-color: #111 !important;
}

#pg-web-home .sidebar .csfd-hall .content .schedule {
  background-color: #111 !important;
}

/*televize*/
.ct-general > .content, .ct-related > .content, #pg-tv #tips li {
    background-color: #111 !important;
}

#pg-tv .scroller, #pg-tv .station, #pg-tv .station .box {
    background-color: black !important;
}

#pg-tv .station .box.odd {
    background-color: #111 !important;
}

#pg-tv .station-name img.logo {
    border: 2px solid #333 !important;
}

#pg-tv #program .scrollLeft, #pg-tv #program .scrollRight {
  background-color: #111 !important;
}

/* poznamka */
#pg-web-film #private-note .content {
  background-color: #111 !important;
}

/*DVD & Blu-ray sekce*/
ul.ui-posts-list, ul.ui-posts-action-list {
    background-color: #111 !important;
}

div.navigation li.selected > a {
    color: white !important;
    background-color: #111 !important;
}

/*Tvůrci sekce*/
.ui-image-list > li{
    background-color: #111 !important;
}

.ui-image-list > li:nth-child(2n){
    background-color: #333 !important;
}

.ct-general > .content tbody tr td, .ct-related > .content{
    background-color: #111 !important;
}

/* Filmoteky */
.ui-table-list thead td, .ui-table-list th {
    background-color: #111 !important;
}

/* Uzivatele */
#pg-web-users .default #most-active li:nth-child(2n) {
    background-color: #333 !important;
}

/* Diskuze */
.ui-sidebar-menu > ul > li.selected {
    background-color: #333 !important;
}


#pg-forum #posts .ui-posts-action-list li.ad, #pg-forum #posts > .content, #pg-forum #posts .paginator {
    background-color: black !important;
}

/*chyba*/
#pg-error div.main {
    background-color: black !important;
}

/* vyhledavaci pole v bazaru */
ul.token-input-list{
    background-color: #111 !important;
}

/* test */
ul#context-menu li.knowledge-test a{
    background-color: #111 !important;
    color: white !important;
}

/* prihlaseni, registrace */
a.private, a.private:visited  {
    color: #ccc !important;
}

/* veci v profilu (editor) */
div.profile-content a.main-edit {
    color: black !important;
}


html body#tinymce.mceContentBody, html body#advlink form div.panel_wrapper {
    color: black !important;
    background-color: white !important;
}

div.profile-content div.ui-collapsible div.content form#frm-diaryForm {
    color: black !important;
}

span.film-year{
    color: white !important;
}

form#frm-addFanclubFilmForm table tbody tr td label.required{
    color: white !important;
}

table.ui-table-list{
    color: white !important;
}

#pg-user .profile-content.ratings table.ui-table-list tbody td{
    background-color: #888 !important;
}

div#pg-user.page-content div.ct-general div.content-wrapper div.profile-content div.section-menu div.navigation ul li.selected span.selected{
    color: white !important;
}

div.profile-content div.column table.ui-container tbody, div.profile-content div.column table.ui-container caption{
    color: black !important;
}

div.profile-content div.ui-form form#frm-userSettingsForm fieldset, form#frm-userSettingsDisplayForm fieldset{
    background-color: #888 !important;
}

div.profile-content div.ui-form form#frm-userSettingsForm fieldset legend, form#frm-userSettingsDisplayForm fieldset legend{
    color: white !important;
}


div.profile-content > .ct-related{
    color: white !important;
}

div#pg-user.page-content div.ct-general div.content-wrapper div.profile-content div.ct-related div.content ul.sortable li {
    color: black !important;
}

/* odkazy */
#pg-web-home .sidebar .others .links, #pg-web-home .sidebar .others .statistics {
  border-color: transparent;
}

/* csfd ads */

#pg-web-creator .csfd-ad-text {
  background-color: #111 !important;
}

/* end */
}
