css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
body {line-height: 1.25 !important;font-family: Helvetica, "Lucida Sans", Arial, sans-serif !important;color: #222 !important;}
body, ul#sr-bar li a, #sr-bar a, .link .rank, ul.flat-list li.rounded, ul.nsfw-stamp li.rounded, ul.flat-list li {font-family: Helvetica, "Lucida Sans", Arial, sans-serif !important;}

.sponsorshipbox, .sidebox.gold{display: none !important}


.comments, p {font-size:13.5px !important;}
a:visited {color:#76A !important;}
a:hover, a:active {color:#09F !important;text-decoration: underline !important;}
#sr-header-area {font-size:9px !important;text-transform: capitalize!important;background-color: #F3F3F3 !important;border-bottom: 0px solid #CCC !important;margin: 0 0 0 0px !important;}
#sr-bar a {color: #666 !important;padding: 3px 4px 2px 4px !important;background-color: #F3F3F3 !important;border-left: 1px solid #FFF !important;border-right: 1px solid #E3E3E3 !important;}
#sr-bar a:hover, #sr-bar a:active {background-color: #FFF !important;border-color: #DDD !important;color: #09F !important;}
#sr-bar span.separator {display: none !important;}
a#sr-more-link {color: #555 !important;background: #F9F9F9 none !important;-moz-box-shadow: -4px 0 4px #CCC !important;-webkit-box-shadow: -4px 0 4px #CCC !important;top: 0 !important;right: 0 !important;padding: 3px 5px 2px 10px !important;}
a#sr-more-link:hover {background: #FFF none !important;}
#header-img {margin: 0 0 0 0 !important;}
#header-bottom-left {padding-top: 1em !important;}

#header-bottom-left a:visited{color:#06C !important;}
user .hover a:visited{ color:#06C!important;}
.user a,.user a:visited{text-decoration:none!important; color:#FFF !important}

#header-bottom-left a#header-img-a {position: absolute !important;top: 0 !important; left: 0 !important;display:block !important;height: 43px !important;}
.pagename {margin-left: 135px !important;margin-right: -80px !important;font-size:15px !important;line-height: 15px !important;color: #444 !important;}
/***#header-bottom-left a#header-img-a img#header-img {margin: 2px !important;}***/
 
#header-bottom-left a#header-img-a {position: absolute !important;top: 5px; left: 5px;display:block !important;height: 40px !important;}
#header-bottom-left a#header-img-a img#header-img {max-height:45px !important; max-width:140px !important}
 
#header-bottom-right {-moz-border-radius-topleft: 4px !important;-webkit-border-top-left-radius: 4px !important;padding: .3em .5em !important;}
.link {margin: 0 0 0 0 !important;padding-left: 0em !important;padding-top: 6px !important;padding-bottom: 6px !important; border-top: 2px solid #EEE !important;}
.link .rank {margin-top: 7px !important;font-weight: bold !important; color: #CCC !important;}
.arrow {height: 8px !important;margin: 1px auto 0 !important;}
.arrow.down {background-position: -4px -251px !important;}
.arrow.up{background-position: -4px -223px !important;}

.link .score {margin-top: 2px !important;line-height: 1 !important;}
.link .midcol {padding: 1px 0 !important;margin: 0 .75em 0 .5em !important;border: 1px solid #EEE !important; -moz-border-radius-topleft:4px !important;-webkit-border-top-left-radius: 4px !important; -moz-border-radius-topright:4px !important;-webkit-border-top-right-radius: 4px !important; -moz-border-radius-bottomleft:4px !important;-webkit-border-bottom-left-radius: 4px !important; -moz-border-radius-bottomright:4px !important;-webkit-border-bottom-right-radius: 4px !important;-moz-box-shadow: 2px 2px 4px #F0F0F0 !important;-webkit-box-shadow: 2px 2px 4px #F0F0F0 !important;}
a.thumbnail {width:40px !important; height: 35px!important; text-decoration: none !important;background: #DDD none !important;margin:0 !important;padding: 0px !important;-moz-border-radius: 4px !important;-webkit-border-radius: 4px !important;}
.domain a {color: #999 !important;}

a.thumbnail img{width:40px !important ; height: 35px !important; padding:0 !important; margin: 0 !important;}
a.thumbnail:visited {background: #EEE none !important;}
a.thumbnail:active, a.thumbnail:hover {background: #BBB none !important;}
.link .title {font-weight:normal !important; letter-spacing:.3px; font-size:16px !important;;margin-bottom: .0em !important;}

/***#siteTable***/ .tagline{display:none !important}
.linkinfo, .sidebox.submit .spacer, .sidebox.create, .titlebox .bottom, #search, .commentarea h1, .rank {display:none !important;}
.usertext-edit textarea {height: 60px !important;}
.side .usertext-body p{font-size:12px !important}
.spacer{margin: 0 0 0 0 !important;}


.tagline {color: #999 !important; font-weight:normal !important;font-size:10px !important; }
.tagline a {color: #888 !important;font-weight: normal !important;}
.tagline a:hover, .tagline a:focus {color: #666 !important;}
.entry {margin-left:.5em !important;}
.entry hr {background-color: #DDD !important;border-color: #DFDFDF !important;border-style:solid !important;}
.entry .buttons li {margin-bottom: 0px !important;}
.entry .buttons li a {padding: 0px 0px 0 0px !important;color: #AAA !important;font-size:10px !important;line-height:8px !important;vertical-align: middle !important;}
.entry .buttons li a:hover, .entry .buttons li a:active {border-color: #AAA !important;text-decoration: none !important;color: #09F !important;}
.link .flat-list {display: inline!important;padding: 0px 0px 0 0 !important; margin: 0 0 0 0 !important}
.link .flat-list li, .flat-list.share {display:none!important;}
.link .flat-list .first {display:inline!important;}
.sidebox.submit .spacer, .sidebox.create .spacer {margin-left: .5em !important;}
.organic-listing {display: none !important;}
#siteTable {padding-top: .5em !important;}
#siteTable .link {margin-left: 1em !important;margin-right: 2.5em !important;}
#search input[type="text"] {border: 1px solid #999 !important;width: 135px !important;margin: 0 10px !important;padding: 2px 0 !important;font-family: Helvetica, Arial, sans-serif !important;font-size: 13px !important;}
.login-form-side {border: 1px solid #DDD !important;margin: 0 5px !important;padding: 3px 0 !important;background: #F9F9F9 none !important;-moz-border-radius-bottomleft:5px !important;-webkit-border-bottom-left-radius: 5px !important; -moz-border-radius-topleft:5px !important;-webkit-border-top-left-radius: 5px !important; -moz-border-radius-topright:5px !important;-webkit-border-top-right-radius: 5px !important; -moz-border-radius-bottomright:5px !important;-webkit-border-bottom-right-radius: 5px !important;}
.login-form-side input {border: 1px solid #999 !important;width: 124px !important;margin: .5em 0 0 8px !important;padding: 2px 0 !important;font-size: 13px !important;height: auto !important;}
#rem-login-main {width: auto !important;margin: .2em 0 0 2px !important;}
.login-form-side label {margin-left: 2px !important;color: #666 !important;}
#remember-me a {display: block !important;text-align: center !important;padding: 1px 2px !important;border: 1px solid #DDD !important;margin: .5em .25em 0 .25em !important;color: #888 !important; -moz-border-radius-topleft:2px !important;-webkit-border-top-left-radius: 2px !important; -moz-border-radius-topright:2px !important;-webkit-border-top-right-radius: 2px !important; -moz-border-radius-bottomleft: 2px !important;-webkit-border-bottom-left-radius: 2px !important; -moz-border-radius-bottomright:2px !important;-webkit-border-bottom-right-radius: 2px !important;}
#remember-me a:hover, #remember-me a:active {color: #09F !important;border-color: #888 !important;background-color: #FFF !important;}
.redditname {display:none !important;}
ul.tabmenu {margin: 0 0 0 135px !important;}
ul.tabmenu li {padding: 0 0 0 0 !important;}
.tabmenu li a {padding: 2px 6px 2px 6px !important;-moz-border-radius-topleft: 4px !important;-webkit-border-top-left-radius: 4px !important;-moz-border-radius-topright: 4px !important;-webkit-border-top-right-radius: 4px !important;}
@media screen and (-webkit-min-device-pixel-ratio:0) {.tabmenu li a {padding: 2px 6px 1px 6px !important;}}
.tabmenu li.selected a,#header-bottom-left .tabmenu li.selected a:visited {color:#444 !important;background-color: #FFF !important;border-color: #5F99CF #5F99CF #FFF #5F99CF !important;padding: 2px 6px 2px 6px !important;}
.side {background-color: #FFF !important;margin: .5em .4em 0 1em !important; padding: .5em 0 !important; width: 160px !important;overflow: hidden !important;border: 1px solid #DDD !important;-moz-border-radius-bottomleft:5px !important;-webkit-border-bottom-left-radius: 5px !important; -moz-border-radius-topleft:5px !important;-webkit-border-top-left-radius: 5px !important; -moz-border-radius-topright:5px !important;-webkit-border-top-right-radius: 5px !important; -moz-border-radius-bottomright:5px !important;-webkit-border-bottom-right-radius: 5px !important;-moz-box-shadow: -10px 7px 10px #FFF !important;-webkit-box-shadow: -10px 7px 10px #FFF !important;}
.side iframe, .side #ad-frame {width: 160px !important;display: none !important;}

.morelink {margin: 0 5px !important;-moz-border-radius-bottomleft:5px !important;-webkit-border-bottom-left-radius: 5px !important; -moz-border-radius-topleft:5px !important;-webkit-border-top-left-radius: 5px !important; -moz-border-radius-topright:5px !important;-webkit-border-top-right-radius: 5px !important; -moz-border-radius-bottomright:5px !important;-webkit-border-bottom-right-radius: 5px !important;}
.morelink a {font-size: 13px !important;}
.morelink a:hover, .morelink a:active {color: #FFF !important;text-decoration: none !important;}
.morelink .nub {display: none !important;}
.sidebox .spacer {padding: 0 0 0 44px !important;}
.sidebox .subtitle {font-size: 9px !important; color: #666 !important; margin-left: 1px !important;}
#remember-me button.btn {background-color: #FFF !important;background-image: none !important;border: 1px solid #888 !important; color: #444 !important; -moz-border-radius-bottomleft: 2px !important;-webkit-border-bottom-left-radius: 2px !important; -moz-border-radius-topleft: 2px !important;-webkit-border-top-left-radius: 2px !important; -moz-border-radius-topright: 2px !important;-webkit-border-top-right-radius: 2px !important; -moz-border-radius-bottomright: 2px !important;-webkit-border-bottom-right-radius: 2px !important;font-family: Helvetica, "Lucida Sans", Arial, sans-serif !important;font-size: 11px !important;font-weight: bold !important;}
.expando-button {display:none !important;margin: .35em 0 .5em 0 !important;}
.expando {margin: .35em 0 .5em 0 !important;}
.link .usertext .md {-moz-border-radius-bottomleft: 4px !important;-webkit-border-bottom-left-radius: 4px !important;-moz-border-radius-bottomright: 4px !important;-webkit-border-bottom-right-radius: 4px !important;-moz-border-radius-topleft: 0 !important;-webkit-border-top-left-radius: 0 !important;-moz-border-radius-topright: 0 !important;-webkit-border-top-right-radius: 0 !important;padding: 0 0 0 0 !important;border-width: 0 1px 1px 1px !important;border-style: none solid solid solid !important;border-color: #FFF #DDD #CCC #DDD !important;}
.link .usertext .md p {margin: .75em !important;color: #444 !important;}
.entry .buttons li.nsfw-stamp {border: 1px solid #AA6666 !important;background-color: #AA6666 !important;}
.nsfw-stamp acronym {color: #FFFFFF !important;font-weight: bold !important;}
.rounded {-moz-border-radius-bottomleft: 5px !important;-webkit-border-bottom-left-radius: 5px !important;-moz-border-radius-bottomright: 5px !important;-webkit-border-bottom-right-radius: 5px !important;-moz-border-radius-topleft: 5px !important;-webkit-border-top-left-radius: 5px !important;-moz-border-radius-topright: 5px !important;-webkit-border-top-right-radius: 5px !important;}
.link.last-clicked {border: 0px dotted #DDD !important;}
.side .spacer .linkinfo {margin: 0 .5em !important;color: #555555 !important;}
.side .spacer .linkinfo span.number, .side .spacer .linkinfo span.word {color: #666666 !important;}
.linkinfo .upvotes, .linkinfo .downvotes {font-size: 8px !important;}
.linkinfo .upvotes span.number, .linkinfo .downvotes span.number {font-size: 9px !important;font-weight: bold !important;}
.side .spacer .titlebox {margin: 0 .5em !important;font-size: 11px !important;}
.side .spacer .titlebox span.fancy-toggle-button a.remove {border: 0 none !important;color: #FFFFFF !important;text-decoration: none !important;padding: 1px 3px !important;}
.side .spacer .sidecontentbox, .side .sidecontentbox {display:none!important;margin: 0 .5em !important;}
.side .spacer .sidecontentbox .content, .side .sidecontentbox .content {border: 1px solid #AAA !important;}
.arrow.up:hover, .arrow.upmod {background-position: -4px -179px !important;}
.arrow.down:hover, .arrow.downmod {background-position: -4px -207px !important;}
.link .score.likes {color: #7FCC7F !important;}
.link .score.dislikes {color: #CC7F7F !important;}
.link .usertext .md {background-position: 0 -724px !important;background-repeat: repeat-x !important;background-color: #F1F1F1 !important;}

.comment .child {margin: 0 0 4px 0 !important;margin-left: 3em !important;margin-top: 0em !important; }

.comment {border-top: 1px solid #CCC !important;padding-top: .0em !important;margin-top: 1em !important;}
.comment .child .comment {border-top: 0 none !important;margin-top: 0 !important;}
.comment blockquote {margin: .75em 1.25em .75em 1.25em !important;padding: .25em .5em .25em .5em !important;border: 2px solid #DDD !important;border-left: 2px solid #DDD !important;background-color: #F1F1F1 !important;background-position: 0 -724px !important;background-repeat: repeat-x !important;color: #444 !important;-moz-border-radius: 5px !important;-webkit-border-radius: 5px !important;}
.comment blockquote p {color: #666 !important;}
.comment .expand:hover {background-color: #06C !important;color: #FFF !important;}
.commentarea h1 {border-bottom: 0 none !important;padding: 0 0 0 .5em !important;padding-bottom: 0 !important; margin: 1em 160px 0 10px !important;background: #E6E6E6 none !important;color: #FFF !important;font-weight: bold !important;}
.commentarea .menuarea {color: #999 !important;margin: 0 160px 1em 10px !important;padding: .15em 0 0 .25em !important;}
.commentarea .menuarea .spacer .lightdrop {color: #999 !important;}

.child .comment {background-position: 0 -724px !important;background-repeat: repeat-x !important;}
/***.child .comment .comment .comment, .child .comment .comment .comment .comment .comment, .child .comment .comment .comment .comment .comment .comment .comment {background-color: #F3F3F3 !important;} ***/
.child .comment .comment, .child .comment .comment .comment .comment, .child .comment .comment .comment .comment .comment .comment, .child .comment .comment .comment .comment .comment .comment .comment .comment {background-image: none !important;}



.drop-choices {border: 1px solid #BBB !important;-moz-box-shadow: 2px 2px 4px #CCC !important;-webkit-box-shadow: 2px 2px 4px #CCC !important;}
.cover {background-color: #000 !important;opacity: 0.5 !important;}
.popup {-moz-border-radius: 7px !important;-webkit-border-radius: 7px !important;border-color: #CCC #CCC #CCC #CCC !important;border-width: 2px !important;}
.popup h1 {display:block;background-position: 10px -671px !important;background-repeat:no-repeat !important;font-size:16px !important;line-height:22px !important;color:#C00 !important;background-color: #EEE !important;-moz-border-radius: 4px !important;-webkit-border-radius: 4px !important;border: 1px solid #DDD !important;margin: 0 0 15px 0 !important;}
.popup .divide {border-right: 2px solid #EEE !important;}
.loginform h3 {color: #666 !important;}
.popup .loginform {margin-bottom: 5px !important;}
.expando-button.video.collapsed, .expando-button.video.collapsed:hover, .expando-button.video.expanded, .expando-button.video.expanded:hover, .eb-ve, .eb-veh, .eb-vch, .expando-button.selftext.collapsed, .expando-button.selftext.collapsed:hover, .expando-button.selftext.expanded, .expando-button.selftext.expanded:hover, .eb-sch, .eb-se, .eb-seh, .morelink .nub, .morelink:hover .nub, .mlhn, .sidebox.create .spacer, .sidebox.submit .spacer, .arrow.upmod, .arrow.downmod, .arrow.up, .arrow.down, .media-button .option.active, .sr-row.sr-selected, .titlebox form.leavemoderator-button, .titlebox form.leavecontributor-button, .icon-menu .reddit-edit, .icon-menu .reddit-traffic, .icon-menu .reddit-reported, .icon-menu .reddit-spam, .icon-menu .reddit-ban, .icon-menu .reddit-moderators, .icon-menu .reddit-contributors, .link .usertext .md, .comment blockquote, .popup h1 {background-image: url(data:image/png;base64,) !important;cursor: pointer !important;}


}
