css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(userscripts.org){

#header
{border-top-width:0 !important}
#header h1
{background-image:url(http://i50.photobucket.com/albums/f329/Webbpa/userscripts-org-logo.png) !important;
 top:14px !important}

#root
{max-width:1011px !important;
 height:auto !important;
 margin-left:auto !important;
 margin-right:auto !important;
 border:1px solid gray !important;}

/* Buttons effect borrowed from Userscripts.org Main Menu Fix
   http://userstyles.org/styles/17763 by JoeSimmons */
#mainmenu li
{background-color:rgb(255,92,0) !important;
 margin-right: 4px !important;
 border: 1px solid #FFB35E !important;
 -moz-border-radius: 4px !important}
#mainmenu li a:hover
{text-decoration:none !important}
#mainmenu .active
{font-weight:bold !important}
#mainmenu .active a
{color:white !important}

/* Sort by */
.la a
{color:white !important;
 font-weight:normal !important}

#header,th,#footer,H3,.userjs,.details,#homeMenu,.greenbutton
{background-color:rgb(255,136,0) !important}

#navbox
{border-top-width:0 !important}

#script_search
{padding-top:0 !important;
 margin-top:-10px !important;
 margin-right:-10px !important}

.userjs,.details,.details a,#userbox,#userbox *,.greenbutton
{background-image:none !important;
 color:white !important}

#homeMenu
{width:126px !important}

.script-meat,.forums .c2
{font-size:110% !important}

#script_sidebar IMG
{height:20px !important;
 width:20px !important;
 margin-left:15px !important}

ABBR
{border-bottom-width:0 !important}

.favorite,.add
{background-image:none !important;
 margin-top:10px !important;
 color:rgb(0,0,153) !important;
 font-weight:normal !important;
 padding-left:1em !important}

.subtitle,.pagination,.utility
{color:black !important}

.subtitle+.pagination
{margin-top:-20px !important}

th,.userjs,.greenbutton
{border-left-color:white !important;
 border-right-color:white !important;
 border-top-color:white !important;
 border-bottom-color:white !important}

H1
{font-size:1.5em !important;
 margin-bottom:15px !important}

.topics #right H5
{margin-bottom:0 !important}

#group_list
{margin-top:10px !important}

.container,
#scripts-reviews #content,
#forums-index .container #content:first-child,
#forums-show .container #content:first-child,
#groups-index .container #content:first-child,
#cse-search-results>IFRAME
{width:100% !important}

#right
{background:rgb(238,238,238) !important;
 padding-left:0 !important;
 margin-right:5px !important;
 float:right !important}

a[href*="paypal.com"],
.notice,.help,
#uservoice-feedback-tab,
#footer,#root_footer,
.postactions,.spacer a,
div[class="ad bottom"],
iframe[id*="google_ad"],
#script_sidebar>iframe,
#script_sidebar>iframe+BR,
.delicious_post,.fb_share_link,
.subtitle a img,H1+.subtitle,
#uvTab
{display:none !important}}

@-moz-document 
/* url-prefix(http://userscripts.org/topics/), */
url-prefix(http://userscripts.org/forums),
url-prefix(http://userscripts.org/posts),
/* url-prefix(http://userscripts.org/tags), */
url-prefix(http://userscripts.org/groups){
pre{max-width:100% !important; width:100% !important}
/* #content .wide,.stats{width:135% !important} */
#right .search5{padding-left:7px !important}
#right .search{margin-top:-9px !important; margin-left:8px !important}
H5{margin-bottom:0 !important}
H1{margin-bottom:22px !important}
#right P,#right HR,#right H5
{display:none !important}}
