css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gaiaonline.com") {
.userName, .accountQuickLinks, .accountCurrency, .goldMessage {
font-family:Calibri !important;
}

li.status a, li.status a:link, li.status a:visited, li.gaiaCash a, li.gaiaCash a:link, li.gaiaCash a:visited, a.noMail, a.noMail:link, a.noMail:visited {
color:#072A66 !important;
text-decoration:none !important;
}

li.status a:focus, li.status a:hover, li.status a:active, li.gaiaCash a:focus, li.gaiaCash a:hover, li.gaiaCash a:active, a.noMail:focus, a.noMail:hover, a.noMail:active {
color:#333 !important;
}

a.newMail, a.newMail:link, a.newMail:visited {
color:#072A66 !important;
font-weight:900 !important;
font-size:110% !important;
text-decoration:none !important;
background:url(http://skillya.fm.interia.pl/gaia/header_tweak/ic_new_mail.png) !important;
}

a.newMail:focus, a.newMail:hover, a.newMail:active {
color:#F5F4FB !important;
}

a.newMail:after {
content:" !!"
}
}
