css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("http://www\\.asphalto\\.org/.*user_actions\\.php\\?action=printpage.*") {

body {font-family: tahoma, sans-serif; font-size: 8pt !important;}
br {line-height:0.2em !important;}
img {max-width:50px !important; max-height:50px !important;} 

.printpage {width:100% !important; border:0 !important;}
.printpage td {padding:0 !important;}

.post_info {font-size: 8pt !important;}
.post_info a {text-decoration:none !important; font-weight:bold !important;}

.main_title {font-size:10pt; font-weight:bold;  border-bottom: 0!important; border-top:1px dotted black; font-family: tahoma, sans-serif !important;}
.main_title br {display:none;}
.main_post_text {font-size: 8pt !important;}
.main_post_text a {text-decoration:none !important; font-weight:normal !important;}

.main_post_text blockquote {margin:0 !important; border:0 !important; padding: 0 !important; margin-left: 8px !important; padding-left: 3px !important; border-left: 3px dotted grey !important}

.signature {display: none;}
}
