css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("insanejournal.com") {
body.sitescheme,
.sitescheme table {
  background: #ffffff !important;
  color: #000000 !important;
  font: 12px Verdana, Tahoma, Geneva, sans-serif !important;
}

.sitescheme a, 
.sitescheme a:visited,
.sitescheme a:active {
  color: #000099 !important;
  text-decoration: none !important;
}

.sitescheme a:hover {
  text-decoration: underline !important;
}

.sitescheme table.nav {
  border: ridge 1px #000000 !important;
}

.sitescheme a.nav:visited {
  color: #000000 !important;
}
    
.sitescheme a.nav:hover {
  color: #000099 !important;
}  
 
.sitescheme td.head {
        color: #ffffff !important;
        background-color: #99ccff !important;
        font: 10px Verdana, Tahoma, Geneva, Arial, sans-serif !important;
  }  

.sitescheme table.banner {margin-top:-152px !important; visibility:hidden !important;}
}
