css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("boards.4chan.org") {

body {
background: #fff !important;
}

form:not([name="post"]) {
margin-top: 30px !important;
margin-left: 5% !important;
width: 60%  !important;
margin-right: 35%  !important;
background: white !important;
}

SPAN.omittedposts {
float: right !important;
clear: left !important;
}

td.reply  {
background: #fff !important;
border: 0px !important;
}

table:not([cellpadding="1"]) {
clear: left !important;
margin-top: 5px !important;
}

td.doubledash {
display: none !important;
}

hr {
border-top: 1px solid #ccc !important;
width: 100% !important;
}

IMG[src*="thumbs.4chan"] {
background:#fff !important;
padding:4px !important;
overflow:hidden !important;
border:1px solid #d3d3d3 !important;
-moz-border-radius:3px !important;}

/*
IMG {
display:block !important;
overflow:hidden !important;
border:3px double #999  !important;}
*/

INPUT[type="submit"] {
height: 30px !important;
	border:1px solid #ccc !important;
	background:#f6f6f6 !important;
	background-image:-moz-linear-gradient(top,#ffffff,#efefef) !important;
	-moz-border-radius:3px !important;
	vertical-align:middle !important;
	cursor:pointer !important;
-moz-appearance: none !important;
}

INPUT[type="submit"]:hover,
INPUT[type="submit"]:focus {
	border-color:#999 !important;
	background:#f3f3f3 !important;
	background-image:-moz-linear-gradient(top,#ffffff,#ebebeb) !important;
-moz-appearance: none !important;
}

INPUT[type="submit"]:active {
	border-color:#999 !important;
	background:#ddd !important;
	background-image:-moz-linear-gradient(top,#cccccc,#ffffff) !important;
-moz-appearance: none !important;
}

INPUT[class="inputtext"] {
height: 30px !important;
vertical-align:middle !important;
border: 1px solid #666 !important;
-moz-appearance: none !important;
}

INPUT[class="inputtext"]:focus{
border: 1px solid #bbdafd !important;
-moz-appearance: none !important;
}

TD.rules, TD[align="right"], #footer {
display: none !important;
}

/* PAGE BOX */
TABLE.pages {
width: 55% !important;
margin-left: 25% !important;
margin-right: 20% !important;
background: transparent !important;
border: 0px !important;
}

TABLE.pages TD {
font-size: 0px !important;
}

TABLE.pages A {
font: 12px Arial !important;
color: blue !important;
padding: 5px !important;
margin-left: -10px !important;
margin-right: 15px !important;
border: 1px solid #ccc !important;
background: #f6f6f6 !important;
background-image: -moz-linear-gradient(top,#ffffff,#efefef) !important;
-moz-border-radius: 3px !important;
vertical-align: middle !important;
cursor: pointer !important;
}

TABLE.pages A:hover {
border-color: #999 !important;
background: #f3f3f3 !important;
background-image: -moz-linear-gradient(top,#ffffff,#ebebeb) !important;
-moz-box-shadow: 0 0 3px #999 !important;
}

TABLE.pages A:active {
border-color: #999 !important;
background: #ddd !important;
background-image: -moz-linear-gradient(top,#cccccc,#ffffff) !important;
}

TABLE.pages B {
font: 12px Arial !important;
font-weight: bold !important;
color: #000 !important;
background: #ddd !important;
-moz-box-shadow: none !important;
padding: 5px !important;
margin-left: -10px !important;
margin-right: 15px !important;
cursor: default !important;
border: 1px solid #ccc !important;
-moz-border-radius: 3px !important;
vertical-align: middle !important;
}

TABLE.pages B:hover {
-moz-box-shadow: 0 0 3px #999 !important;
}

TABLE.pages INPUT[value="Previous"], 
TABLE.pages INPUT[value="Next"] {
display: none !important;
}

#navtop {
width: 100% !important;
text-align: center !important;
position: fixed !important;
background: white !important;
color: white !important;
top: 0px !important;
padding: 5px !important;
border-bottom: 1px solid grey !important;
}

.logo B {
position: fixed !important;
top: 25px !important;
right: 0px !important;
margin-right: 10px !important;
color: black !important;
}

td.postblock {
background: #ff944d !important;
border:1px solid #cd6701 !important;
}

.logo FONT[size='1'] {
position: fixed !important;
top: 60px !important;
right: 0px !important;
margin-right: 5px !important;
text-align: right !important;
color: black !important;
}

B[style="color: red;"] {
-moz-border-radius: 10px !important;
background: #ffe5e5 !important;
padding: 5px !important;
padding-right: 20px !important;
padding-left: 20px !important;
color: #000 !important;
}

TABLE TR TH[bgcolor="#e04000"] { display: none !important }

SPAN SELECT {
position: fixed !important;
right: 100px !important;
top: 200px !important;
margin: 0px !important;
}

SPAN LABEL {
position: fixed !important;
right: 100px !important;
top: 220px !important;
margin: 0px !important;
}

.postarea > form {
position: fixed !important;
right: 100px !important;
background: silver !important;
top: 200px !important;
margin: 0px !important;
}

HR + A + HR { display: none !important }

DIV + HR { display: none !important }

CENTER + HR { display: none !important }

span[style="left: 5px; position: absolute;"] { display: none !important }

HR[width='90%'] { display: none !important }

.LOGO > BR { display: none !important }

.logo IMG { display: none !important }

#header + BR { display: none !important }

#navtopr { display: none !important }

TABLE[align="right"] {
position: fixed !important;
right: 100px !important;
top: 450px !important;
margin: 0px !important;
}

td.replyhl {
background: #efefef !important;
border: 0px !important;
padding-right: 7px !important;
}

#updater {
border: 1px solid #99adeb !important;
background: #99adeb !important;
-moz-border-radius-topleft: 5px !important;
color: #fff !important;
font-weight: bold !important;
padding: 1px !important;
}

#tw {
border: 1px solid #99adeb !important;
background: #99adeb !important;
-moz-border-radius-topright: 5px !important;
-moz-border-radius-bottomright: 5px !important;
text-align: left !important;
position: fixed !important;
top: 100px !important;
left: 0px !important;
width: 90px !important;
padding: 0px !important;
margin: 0px !important;
}

#tw > DIV:first-child {
text-decoration: none !important;
text-align: center !important;
font-weight: bold !important;
padding: 0px !important;
color: black !important;
}

#tw SPAN > A:first-child {
font-weight: bold !important;
color: black !important;
text-shadow: 0px 0px 5px red !important;
}

#tw SPAN > A {
text-decoration: none !important;
color: black !important;
}

}
