css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("jensweinreich.de") {


body {
	background: url(http://jensweinreich.de/wp-content/uploads/2010/06/sepp-puzzle_bricks.jpg) fixed right top transparent !important;
	/* 
	The BACKGROUND-IMAGE has been created by Taifarious1 <http://commons.wikimedia.org/wiki/User:Taifarious1> 
	and published under a CC-by-sa 3.0 licence <http://creativecommons.org/licenses/by-sa/3.0/deed.en>
	*/
}


/*
 * HEADER
 */

#header {
	background: none repeat scroll 0 0 DarkOliveGreen !important;
	border-bottom: 12px double #333 !important;
	-moz-box-shadow: 0 5px 3px #333 !important;
	-webkit-box-shadow: 0 5px 3px #333 !important;
	box-shadow: 0 5px 3px #333 !important;
	opacity: 0.9 !important;
}

/* H-LEFT */
.headerleft, .headerleft a, .headerleft a:visited, .headerleft a:active {
	text-shadow: 1px 1px 4px #333 !important;
}

/* H-RIGHT */
.headerright a:hover {
	background-color: #333 !important;
	color: #FFF !important;
	text-shadow: 1px 1px 1px #000 !important;
}


/*
 * CONTENT
 */
 
#content {
	background-color: rgba(255, 255, 255, 0.9) !important;
}
.postinfo:hover {
	background-color: rgba(240,240,240,0.6) !important;
}
#l_sidebar {
	border-top: 0 !important;
	background-color: rgba(164,164,164,0.5) !important;
}
#l_sidebar ul li a:hover {
	-moz-box-shadow: none !important;
	-webkit-box-shadow: none !important;
	box-shadow: none !important;
}

/*
 * FOOTER
 */

#footer {
	background:none repeat scroll 0 0 #333 !important;
	border-top:5px solid DarkOliveGreen !important;
}

}
